CREATE TABLE `fands` (
  `id` int(11) NOT NULL COMMENT '美食与特产id',
  `cate_id` int(11) NOT NULL COMMENT '分类id',
  `name` varchar(100) NOT NULL COMMENT '名称',
  `descs` varchar(100) NOT NULL COMMENT '简介',
  `content` text NOT NULL COMMENT '介绍',
  `pic` varchar(255) NOT NULL COMMENT '图片',
  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '类型',
  `area` varchar(100) NOT NULL COMMENT '所属地区',
  `praise` int(100) NOT NULL DEFAULT '0' COMMENT '点赞数'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='美食与特产';

--
-- 转存表中的数据 `fands`
--

INSERT INTO `fands` (`id`, `cate_id`, `name`, `descs`, `content`, `pic`, `type`, `area`, `praise`) VALUES
(1, 65, '木金鱼丸', '木金肉丸选取上等薯粉、白芋丝、肥膘肉为原料，调入红糖拌匀，佐以五香粉，还有的加芝麻、豆腐皮等', '&lt;p&gt;&lt;img src=&quot;/Public/upload/image/201704/1492064445194862.jpg&quot; title=&quot;1492064445194862.jpg&quot; alt=&quot;8b9e31420dc3aaa03117f3a50aef3ca4.jpg&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;木金肉丸选取上等薯粉、白芋丝、肥膘肉为原料，调入红糖拌匀，佐以五香粉，还有的加芝麻、豆腐皮等，制成粘剂，捏成团状，放在铺上荷叶的蒸笼炊熟即可食用。肉丸甜中带咸，油而不腻，很符合老福州人的口味，是老福州人逢年过节时候餐桌上必备的食品之一。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;传说某日姚木金在制作肉丸，恰逢猫追老鼠，把桌上酱油踢向调料的脚桶。他边追打猫边嘀咕：&amp;quot;倒吧！总不能甜味咸味混一起，不出丑才怪？&amp;quot;他叫苦之际，老伴对他说：&amp;quot;猪油炒米不也用些食盐调味。&amp;quot;他茅塞顿开，把脚桶里的芋丝、薯粉等和酱油搅匀，舀在铺好荷叶的蒸笼。就这样响誉海内外的木金肉丸诞生了。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;木金肉丸的第三代传人姚祖兴说，人们的口味是最难伺候的，木金肉丸能长盛不衰，就是因为制作精细，选料严格，加上独特的配方，味道自然很好。不过这种纯手工制作太累，每天早上三四点钟，他和老伴就起来忙活，制面料、制馅、包馅、上锅蒸，每道工序都不能马虎。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef18d00f103.jpg', 1, '鼓楼', 2),
(2, 65, '福州捞化', '老福州人对捞化的印象恐怕最为深刻，小时候，捞化是街边摊头的一碗捞化汤，烫上福州人最爱的细长米线', '&lt;p class=&quot;imgCenter&quot; style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-align: center; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492064517173570.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; vertical-align: top;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;老福州人对捞化的印象恐怕最为深刻，小时候，捞化是街边摊头的一碗捞化汤，烫上福州人最爱的细长米线，沾点福州人独爱的虾油，高汤中网罗猪血、花肠、黄喉、罗汉肉等猪杂内脏，让味道变得浓厚香醇。寒冬的早晨，一碗捞化热气扑腾而来，陪伴了食客整个冬日。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;随着时间的推移，捞化的内容从最初的仅仅只有米粉演变得更加丰富，眼下福州人更喜欢用各种贝类、海鲜或猪腰鸭腱作为捞化的原料，鲜嫩甘美，再淋上古早味的虾油，就是一份地道的福州捞化。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef193053b3b.jpg', 1, '台江', 0),
(3, 65, '佛跳墙', '佛跳墙，又名“满坛香”、“福寿全”，是福州的首席名菜，迄今已有150年历史，为聚春园菜馆老板郑春发研创', '&lt;p class=&quot;imgCenter&quot; style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-align: center; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492064610948527.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; vertical-align: top;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;佛跳墙，又名“满坛香”、“福寿全”，是福州的首席名菜，迄今已有150年历史，为聚春园菜馆老板郑春发研创。佛跳墙用绍兴酒坛装鸡、鸭、羊肉、猪肚、鸽蛋及海产品等20多种原、辅料煨制而成，选料精良，刀工讲究，规程严格，成菜醇香浓郁，味中有味。有人用“坛启荤香飘四邻，佛闻弃禅跳墙来”描绘它的鲜美诱人。1984年，佛跳墙作为接待美国里根总统的国宴大菜，1990年，获中国菜肴最高奖“金鼎奖”，2008年，“聚春园佛跳墙制作技艺”荣登国家级非物质文化遗产名录。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1974c9629.jpg', 1, '鼓楼', 2),
(4, 65, '金鲟糯米饭', '东庠岛生产的锯缘青蟹，俗称“金蟳”，相传达室为古代皇宫御膳的海珍品', '&lt;p class=&quot;imgCenter&quot; style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-align: center; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492064804743894.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; vertical-align: top;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;东庠岛生产的锯缘青蟹，俗称“金蟳”，相传达室为古代皇宫御膳的海珍品，亦民间上等补品，幼儿学步时食之健身，效果尤佳。因其楼息于激流礁石之间，螯大有力，冬至前后膏腴味美。金糯米饭是平潭宴席名菜。它以蒸、煎、炒、爆、炸为主要烹饪方法，制出独一无二的海坛风味，具有清、香、淡、脆等特点，款式繁多，乡味浓郁，老少兼爱，深受欢迎。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1a2815c57.jpg', 1, '平潭', 0),
(5, 65, '福州鱼丸', '福州有句熟语“无鱼丸不成席”，鱼丸承载了福州人的太多记忆。2012年，中国渔业协会授予福州“中国鱼丸之都”称号。', '&lt;p class=&quot;imgCenter&quot; style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-align: center; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492064849685912.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; vertical-align: top;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州有句熟语“无鱼丸不成席”，鱼丸承载了福州人的太多记忆。2012年，中国渔业协会授予福州“中国鱼丸之都”称号。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州鱼丸选用鲜美的鲨鱼、鳗鱼或淡水鱼肉剁蓉，再加入甘薯粉（淀粉）搅拌均匀，包以猪精瘦肉、虾肉等作馅制作成丸状食物进行煮制，就能让你吃到地道的福州鱼丸了。这道最富有福州地方特色的风味小吃，色泽洁白，玲珑晶亮，质嫩滑润，清脆爽口，入口嚼食，富有弹性。当地人喜欢吃鱼丸时，加入几滴香油或虾油，味道更加鲜美。海外华侨多把鱼丸称为七星鱼丸，七星鱼丸汤也因味道醇美而深受老百姓的喜欢。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;福州七星鱼丸的传说：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;古时候，闽江之畔有个渔民。一天，有位商人搭他的船南行经商，船出闽江口，进了大海，正遇台风袭击。船入港湾避风时，不幸触礁损坏。修船拖误了时间，粮断了，天天以鱼当饭。商人叹道：“天天有鱼，食之生厌。能不能换换别的口味?”船妇说：“船上粮已断，唯有薯粉一包。”&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;心灵手巧的船妇便把刚钓到的一条大慢鱼，去皮除刺，把鱼肉剁细，抹上薯粉，制成丸子，煮熟一尝别有风味。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;事后，这位商人回到福州，便在城里开设一家“七星小食店”，特聘这位船妇为厨师，独家经营“鱼丸汤”。开头，生意并不兴隆。一天，一位上京应考的举子路过此店就餐。店主热情款待，捧出鱼丸。举子食后，颇觉味道极美，便题赠一诗：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;点点星斗布空稀，玉露甘香游客迷。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;南疆虽有千秋饮，难得七星沁诗脾。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;店主将诗挂在店堂上，宾客齐来观赏。从此生意兴隆，小店日日春风。“七星鱼丸”也从此得名。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1a56cc862.jpg', 2, '鼓楼', 0),
(6, 65, '鸡汤氽海蚌', '作为闽菜的代表之一，鸡汤氽海蚌以其鲜美的口味和丰富的营养广受欢迎', '&lt;p class=&quot;imgCenter&quot; style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-align: center; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492064891533897.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; vertical-align: top;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;作为闽菜的代表之一，鸡汤氽海蚌以其鲜美的口味和丰富的营养广受欢迎。此菜取材于海中珍品海蚌，洗净后氽（cuān）至八分熟，冲入煮沸的鸡汤即成。成品色泽洁白透明，几乎没有多余的搭配，全凭海蚌脆嫩爽口的肉质，以及鸡汤的鲜美口味而满足食客的口腹之欲。地道的鸡汤汆海蚌，在汆支时对水温和时间的控制十分严格，不足则软，过头则老，所以看起来简单，但难在恰到好处。福州西湖大酒店、安泰楼的鸡汤氽海蚌相当有名。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1a847a0c8.jpg', 2, '福清', 0),
(7, 65, '永泰蛋燕', '福州有肉燕，永泰有蛋燕。美味爽口的蛋燕，是永泰的一碗记忆，以古镇嵩口的最具特色', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492065069339828.jpg&quot; title=&quot;1492065069339828.jpg&quot; alt=&quot;52e8b46ac0a69436f0c46adc00be0078.jpg&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州有肉燕，永泰有蛋燕。美味爽口的蛋燕，是永泰的一碗记忆，以古镇嵩口的最具特色。在永泰，不管是结婚、乔迁还是升官、贺寿，只要有喜庆宴会，蛋燕都是一道必不可少的压轴菜。而在日常人们的餐桌上，它也是一道大众菜。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;蛋燕本来叫蛋面，它之所以改名，跟“铁印直行”的故事有关。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;相传明朝明武宗朱厚照曾来到嵩口。不巧嵩口巡检司周大人下田耕作，司衙大唱空城计。朱厚照命随从在衙门口击鼓，没过多久，就看到周大人与众兵丁，挽着全是泥巴的裤腿，手握长锄头，齐集在衙门口。朱厚照见巡检司与众兵丁官不官、兵不兵、农不农，心中纳闷，细问之下，方知巡检司、众兵丁俸禄少得可怜，要靠自力耕作才能勉强度日。再看厨灶锅中，仅蒸着数条番薯当午饭。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;乡民纷纷向皇帝解释，周大人为官不易，勤政爱民，廉洁奉公，帝心方悦。周大人无物奉敬，只能献上蒸熟的番薯。这时候，紧挨衙门的邻居煮了一碗祖传蛋面送来，朱厚照正皱着眉头啃番薯，一看到黄澄澄、香喷喷的蛋面，立即举筷，品尝之后龙颜大悦，啧啧赞叹。一番询问之后，乃知这面名为蛋面，或许是口音南北差异，他将“面”听成了“燕”，连连称赞：“蛋燕，香也！”皇帝金口一开，从此蛋燕名动四方，扬名天下。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;前些年，中央电视台的一个节目在介绍嵩口古镇的特色小吃时，把蛋燕的燕理解成燕子的意思，说是蛋燕薄、轻，身轻如燕，也不失为一个别有情趣的解释。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;蛋燕有多种吃法，可以炒着吃、煮着吃、拌着吃。炒蛋燕做起来方便，吃起来美味，而且营养丰富、老少皆宜，备受大家的喜爱。将油热好，把做好的蛋燕下锅，加点肉丝、蘑菇、冬笋、木耳翻炒，出锅后口感顺滑、弹性十足，咬一口就是家的味道。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1b30975f7.jpg', 2, '永泰', 0),
(8, 65, '荔枝肉', '荔枝肉属闽菜，成菜仿荔枝色香味形，将猪瘦肉剞上十字花刀，切成斜形块', '&lt;p class=&quot;imgCenter&quot; style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-align: center; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492065108895876.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; vertical-align: top;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;荔枝肉属闽菜，成菜仿荔枝色香味形，将猪瘦肉剞上十字花刀，切成斜形块，炸后卷缩成荔枝形，佐以番茄酱、香醋、白糖、酱油等调料即成。正宗的荔枝菜，外形惟妙惟肖，味道酸甜可口，独具闽菜特点，作为福州传统名菜，已有两三百年历史。因盛产荔枝而得名“荔城”的莆田也有荔枝肉，但福州荔枝肉更为有名一些。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1b71a1f35.jpg', 3, '罗源', 0),
(9, 66, '寿山石印章', '寿山石作为福建省省石，“福州三绝”之一，有深厚的文化底蕴，自明代以来受到文人墨客青睐，成为中国四大章料石之一。', '&lt;p class=&quot;imgCenter&quot; style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-align: center; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492065417804762.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; vertical-align: top;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;寿山石作为福建省省石，“福州三绝”之一，有深厚的文化底蕴，自明代以来受到文人墨客青睐，成为中国四大章料石之一。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;作品以寿山石为原料，雕刻有福州地标性建筑五A级景区的三坊七巷和特色景点镇海楼，不仅传承了寿山石雕刻篆刻文化，同时也张显了福建浓厚的文人气息。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1c96e1db1.JPG', 2, '鼓楼', 0),
(10, 66, '脱胎漆器', '福州脱胎漆器，与北京的景泰蓝、江西景德镇瓷器，并称中国传统工艺的“三宝”', '&lt;p&gt;&lt;span style=&quot;color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492065543549422.jpg&quot; title=&quot;1492065543549422.jpg&quot; alt=&quot;5854a46efd64fdbb1914c72205e1fe87.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;福州脱胎漆器，与北京的景泰蓝、江西景德镇瓷器，并称中国传统工艺的“三宝”，主要分为实用和欣赏两大类，除了轻巧、美观、耐用的优点外，还有耐热、耐酸、耐咸、绝缘的特点。福州脱胎漆器是继承我国古代优秀漆文化发展起来的，其品类之多在全国漆器行业首屈一指。&lt;/span&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1d1e007f1.jpg', 3, '晋安', 0),
(11, 66, '福州橄榄', '福州盛产橄榄，以闽侯、闽清的产量最多。橄榄，又称“青果”，从生到熟，它始终保持着青翠的颜色', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492065622613993.jpg&quot; title=&quot;1492065622613993.jpg&quot; alt=&quot;67b12f5b54c7efd23e342b5b9218f06e.jpg&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州盛产橄榄，以闽侯、闽清的产量最多。橄榄，又称“青果”，从生到熟，它始终保持着青翠的颜色。福州人也称橄榄为“福果”，既是“有福之州”的特产，又有“幸福”的吉祥寓意。初食橄榄，只觉味道微苦涩，片刻后便知回甘清，除盐渍外还可蜜渍来吃。福州橄榄早在唐朝的时候，就被列为贡品，如今更是走亲访友时绝佳的赠品之一。大世界橄榄在福州当地比较有名。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;福州橄榄故事&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;《闽清县志》记载，有个名叫池香的老人家，常年往返南洋和闽清之间做生意。每次回国，闽清籍华侨们就会托他捎信带话给家乡的亲友。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;有一次，池香又要从南洋回来了。华侨们送他上船，临别前央求他：“我们离乡久了，真想尝尝家乡风味，下回能不能给我们带点吃的东西？”池香爽快地答应了。不过上了船后，他却犯起难来。南洋的乡亲少说也有几千人，他才一个人，路途又远，一趟要走好几个月，有什么东西方便携带呢？&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;说来也巧，池香回到闽清的时候，正值橄榄丰收。他心下有了主意，买了好几担小粒的橄榄，又特制了几个大木桶，里面用橄榄叶铺好，倒进橄榄，包装严密，以防海水侵蚀。3个月后，这些装满橄榄的木桶运到了南洋，打开盖子时，芳香四溢。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;华侨们嚼着故乡的橄榄，问这是什么品种。池香说：“叫‘小粒橄榄’，不过这名字实在不怎样，你们来取个新名字吧。”有位华侨就说了：“别看它小，比这里的檀香木还要香呢。”从此，“檀香橄榄”的名字就这么叫开了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1d7e7ea2f.jpg', 1, '闽清', 0),
(12, 66, '软木画', '福州软木画与寿山石雕、脱胎漆器并称为福州民间工艺三宝。', '&lt;p class=&quot;imgCenter&quot; style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-align: center; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492065831927088.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; vertical-align: top;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州软木画与寿山石雕、脱胎漆器并称为福州民间工艺三宝。软木画以质地松软的栓树皮为材料，以圆雕、透雕、浮雕等技法制成山水、园林、花鸟等立体图景，画面上亭台楼阁玲珑精巧，树木花草形象逼真，色彩古朴典雅，深受海内外人士的喜爱。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;其中，人民大会堂的福建厅，内壁挂的“福州西湖”、“鹭岛风光”、“泉州东西塔”、“武夷春色”等大型软木画，都是由福州工艺师制作。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;追溯历史&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;软木画，用栓皮栎进行雕刻。这种木料被誉为“植物软黄金”，产于地中海沿岸，是制作高档葡萄酒的瓶塞的木料。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;100年前，福建巡按史许世英从法国带来一张轻薄、柔韧且带有优雅花纹的贺年片，福州艺人吴启棋、陈春润、郑立溪等看后深受启发，便运用中国各种传统雕刻技法，以刀代笔，精雕巧镂，制成纹理纤细的立体画面。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;此后，软木画艺人代代相传。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;得之不易&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;据大师吴启棋介绍，创作软木画并不是一开始就上手制作，要先设计草图，然后才做“亭台楼阁山水花鸟”等各类零件，做好之后再进行组装。“要观察远近距离，考虑各方面的透视。有些人不会画，只能照相，还有很多老师傅只能凭图案做，凭空造物不太可能。”一位雕刻师曾经为了创作一幅《湄洲妈祖风光》，前前后后去了十几趟湄洲湾，胶卷拍了五六卷，最后又花了大半年的工夫才完成，而这样的事情又太常见了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1e33e46d5.jpg', 1, '鼓楼', 0),
(13, 66, '七境茶', '“七境”位于罗源县西部山区西兰乡，平均气温18度，常年雨量充沛，雾气缭绕。', '&lt;p class=&quot;imgCenter&quot; style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-align: center; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492065879175150.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; vertical-align: top;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;“七境”位于罗源县西部山区西兰乡，平均气温18度，常年雨量充沛，雾气缭绕。得天独厚的自然地理优势，产出了茶中极品“七境堂”，史称“罗源尖子”，又名“元明绿”，简称“七境茶”。最早七境茶见于唐朝陆羽《茶经》一书，明清时代列为宫廷贡品，颇具盛名。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;近年来西兰乡通过引种黄旦、台茶12号等优良品种，改造茶园160亩，并被省农业厅列为无公害栽培生产示范基地，生产系列产品有七境炒绿、龙井、扁针、乌龙茶等。由于采制精细、工艺考究、毫香突出，具有“香高、口爽、色翠、耐泡”四绝特色。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;“七境绿茶”1955年连续八年被福建省农业厅评为省名优茶，1999年首次参加第三届“中茶杯”全国名优茶叶评比荣获二等奖挤身中国名优茶列行。2001年通过国家茶叶检测中心检测，达到无公害绿色食品标准，品牌品质不断提升。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-13/58ef1e7c78215.jpg', 2, '罗源', 0);

