--
-- 表的结构 `hotelorder`
--

CREATE TABLE `hotelorder` (
  `id` int(11) NOT NULL COMMENT '酒店订单id',
  `user_id` int(11) NOT NULL COMMENT '用户id',
  `hotel_id` int(11) NOT NULL COMMENT '酒店id',
  `indate` varchar(100) NOT NULL COMMENT '入住时间',
  `outdate` varchar(100) NOT NULL COMMENT '离开时间',
  `type` varchar(10) NOT NULL COMMENT '房间类型',
  `beizhu` text NOT NULL COMMENT '备注',
