CREATE TABLE `note` (
  `id` int(11) NOT NULL COMMENT '游记id',
  `cate_id` int(11) NOT NULL COMMENT '分类id',
  `title` varchar(100) NOT NULL COMMENT '游记标题',
  `pic` varchar(255) DEFAULT NULL COMMENT '游记图片',
  `content` text NOT NULL COMMENT '内容',
  `spots` varchar(255) DEFAULT NULL COMMENT '相关地区',
  `areas` varchar(255) DEFAULT NULL COMMENT '相关景点',
  `time` date NOT NULL COMMENT '创建时间',
  `user_id` int(11) NOT NULL COMMENT '创建人id',
  `ishot` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0:普通;1:热门',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0:待审核;1:审核通过',
  `praise` int(100) DEFAULT '0' COMMENT '点赞数'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

