-- 转存表中的数据 `note`
--

INSERT INTO `note` (`id`, `cate_id`, `title`, `pic`, `content`, `spots`, `areas`, `time`, `user_id`, `ishot`, `status`, `praise`) VALUES
(3, 70, '【平潭风光】石牌洋', './Public/Uploads/2017-05-09/591157d513f96.png', '&lt;p&gt;石牌洋，也称双帆石、半洋石帆，位于福建省福州市平潭县苏澳镇看澳村附近海面上。在海面一圆盘状的大礁石上，矗立一高一低的两块碑形海蚀柱。两个石柱均由粗粒灰白色的花岗岩组成东侧的一个高达33米，胸宽9米，厚8米；西侧的一个高17米，胸宽15米，厚8米。两个石柱的底部都是近似四方形体，直立在礁石上。据考证，石牌洋是世界上最大的花岗岩球状风化海蚀柱。由于它的奇特壮观，对游人有着强烈的震撼力和吸引力。石牌洋已成为平潭岛标志性的旅游景点。&lt;br&gt;&lt;/p&gt;&lt;p&gt;6月底开始再一次平潭行，平潭岛剩余未去的两处省保单位这次一并给扫了。当天天气还不错，加之时间充足，所以第一站还是再次来到石牌洋。这是事隔近一年后的再次前往。&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846431588850.jpg&quot; title=&quot;1459846431588850.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846431509165.jpg&quot; title=&quot;1459846431509165.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846432151061.jpg&quot; title=&quot;1459846432151061.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846432667288.jpg&quot; title=&quot;1459846432667288.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846433643488.jpg&quot; title=&quot;1459846433643488.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846433216609.jpg&quot; title=&quot;1459846433216609.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846434731964.jpg&quot; title=&quot;1459846434731964.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846434340425.jpg&quot; title=&quot;1459846434340425.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846434854109.jpg&quot; title=&quot;1459846434854109.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846436825081.jpg&quot; title=&quot;1459846436825081.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846434215288.jpg&quot; title=&quot;1459846434215288.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846434530748.jpg&quot; title=&quot;1459846434530748.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846435819014.jpg&quot; title=&quot;1459846435819014.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846435328021.jpg&quot; title=&quot;1459846435328021.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846436997773.jpg&quot; title=&quot;1459846436997773.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160405/1459846436321538.jpg&quot; title=&quot;1459846436321538.jpg&quot; style=&quot;text-align: center;&quot;&gt;&lt;/p&gt;', '1,2,6,12,16', '台江,长乐,平潭', '2017-04-28', 8, 1, 1, 0),
(5, 70, '三月嵩口慢慢走', './Public/Uploads/2017-05-02/5908356b334de.jpg', '&lt;p&gt;如果不是为了参与 @Homeland家园 的“认识嵩口”分享会，我的嵩口之行可能还要 delay ，即使春节的时候已经在微博上看过了 @姚晨 的推荐。&lt;/p&gt;&lt;p&gt;周六，并没有起早，符合今日慢慢走的主题。为了验证之前得到的公共交通信息，特地跑到汽车站坐中巴。不过事后证明，这样的出行方式还是比较容易令人疲乏的：&lt;/p&gt;&lt;p&gt;10:00-11:00，家 - 福州汽车站，买了去永泰嵩口的全程票，成人43元一张；&lt;/p&gt;&lt;p&gt;11:30-13:00，福州汽车站 - 永泰客运站，一路昏睡向西南，过葛岭，上福诏，向嵩口；&lt;/p&gt;&lt;p&gt;13:00-14:30，永泰客运站 - 嵩口古镇，在地标性建筑张元干塑像下了车。&lt;/p&gt;&lt;p&gt;Bingo ！就是这里了，没有过多的停留，凭着脑海里的嵩口地图印象，迅速向镇政府、电影庙的方向寻去。&lt;/p&gt;&lt;p&gt;Homeland 她家的嵩口手绘地图很抢手，这里放一张当天的宣传图。&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160321/1458533035954656.jpg&quot; title=&quot;1458533035954656.jpg&quot; alt=&quot;4756576cjw1f1z1nnqjxtj20m80nggqy.jpg&quot;&gt;&lt;/p&gt;&lt;p&gt;不消十分钟就找到了电影庙，进门的时候，碰巧看到一位当地的阿妈，伸出手示意需要扶她慢行，瞬间感到一种微妙的气氛。放佛被领进门的不是阿妈，而是我，进入的也正是嵩口的原味生活。&lt;br&gt;&lt;/p&gt;&lt;p&gt;没多久，分享会的前奏就响了，是嵩口当地古乐队在开腔开演。台下并未满座，但游客、年轻人、当地居民各占一席，饶有兴致。&lt;/p&gt;', '2,17', '闽侯,连江,罗源', '2017-05-02', 8, 1, 0, 2),
(6, 70, 'zjl', './Public/Uploads/2017-05-03/5909877888c1f.jpg', 'dddddddddd', '7,13', '台江,长乐', '2017-05-03', 10, 0, 1, 0);

