--
-- 表的结构 `page`
--

CREATE TABLE `page` (
  `id` int(11) NOT NULL,
  `title` varchar(100) NOT NULL,
  `name` varchar(100) DEFAULT NULL,
  `pic` varchar(255) DEFAULT NULL COMMENT '图片',
  `descs` varchar(255) NOT NULL COMMENT '简介',
  `content` text
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `page`
--

INSERT INTO `page` (`id`, `title`, `name`, `pic`, `descs`, `content`) VALUES
(1, '福州简介', 'about', './Public/Uploads/2017-04-10/58eaef7e9ca44.jpg', '无', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 26px; font-weight: 500; overflow: hidden; text-align: center; line-height: 60px;&quot;&gt;关于福州&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州，福建省省会，人们更喜欢叫它榕城。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;四周群山环抱，闽江穿城而过，乌塔和白塔如同一对犄角与城中的乌山、于山和屏山一起组成福州的地标。目前，福州拥有平潭海坛、鼓山、三坊七巷、青云山、十八重溪、马尾船政、三山两塔一条江等著名景点。青石铺路的古旧巷陌，氤氲水汽的温泉热汤，2200多年历史的三坊七巷，保存完整的18座名人故居，还有风味绝佳的当地小吃，飘香全国的茉莉花茶，丰富的城市资源让福州老城中的“虎揪人”天生就有一种淡定。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果你愿意逗留更多的时间，可以到老澡堂泡个汤，找一家茶馆看场闽剧或评话，老福州的生活会温柔地给你挠挠痒。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;福州印象：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;“2200历史”的三坊七巷&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;三坊七巷不仅仅是三条“坊”和七条“巷”，还有一条闻名遐迩的南后街。南后街西起杨桥路口，南至吉庇路达澳门桥，全长1000米左右，是福州城内&amp;quot;三坊七巷&amp;quot;的中轴。青砖白瓦格式的老屋，上了年头的石板路，还有南后街从古到今的老商铺，都是2200多年老福州的缩影。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;绿荫满城的榕树&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;榕树是福州的市树。福州植榕，古已成风。特别是北宋时期，太守张伯玉倡导“编户植榕”，“满城绿荫，暑不张盖”，使福州又有了“榕城”的美称。福州城区有古榕树近千株，其中福州国家森林公园内的一株千年古榕被誉为福州第一大榕，相传是北宋冶平年间三位武官在此练武时植下的，树高20米，树冠地面投影面积达1330多平方米。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;澡堂“泡汤”　&amp;nbsp;&lt;/strong&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州人爱泡温泉，每到冬天去泡汤，这似乎已成了一种习惯。由于在福州随处走走就会发现遍布着温泉，且水量大、温度高、质量好，其优越的条件在全国大中城市中绝无仅有，有不少在市区一带的许多人，在家里就能享受温泉。当华清池温泉只是帝王贵族的高贵独有享受时，福州城内到处都有“汤池店”澡堂，福州市区的许多酒店小区都有温泉提供。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;茉莉花&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州曾是海上丝路的“世界茶港”，对外输出的不仅有茶叶、茶具，还有茶文化。这里的茉莉花茶一流。宋时福州已普遍栽培茉莉。福州城郊有许多成片栽培的茉莉园。它象征福州这座历史文化名城芬香四播。以茉莉花薰制的福建茉莉花茶亦久负盛名，远销海内外。1985年2月8日福州市第八届人大常委会第十二次会议决定命名茉莉花为市花。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;江湖调&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州戏用方言形成江湖小调，以平讲班的形式，和儒林班、外来的唠唠班，和流成闽剧，有400多年历史，每一处细节都表达着地道的福州文化。就像去北京听相声，东北看二人转，去闽剧院看福州戏是时下流行的福州夜生活。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;');

-- --------------------------------------------------------

--
-- 表的结构 `play`
--

CREATE TABLE `play` (
  `id` int(11) NOT NULL COMMENT '编号',
  `cate_id` int(11) NOT NULL COMMENT '分类',
  `title` varchar(100) NOT NULL COMMENT '标题',
  `descs` varchar(255) NOT NULL COMMENT '简介',
  `pic` varchar(255) DEFAULT NULL COMMENT '图片',
  `content` text NOT NULL COMMENT '介绍',
  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1:春2:夏;3秋;4:冬',
  `ishot` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0:不是热门;1;热门'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='玩在福州';

--
-- 转存表中的数据 `play`
--

INSERT INTO `play` (`id`, `cate_id`, `title`, `descs`, `pic`, `content`, `type`, `ishot`) VALUES
(2, 57, '建宁万亩梨花赏花活动', '建宁万亩梨花赏花活动', './Public/Uploads/2017-04-11/58ec45c31aa92.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 26px; font-weight: 500; overflow: hidden; text-align: center; line-height: 60px;&quot;&gt;建宁万亩梨花赏花活动&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444385047237021.jpg&quot; title=&quot;1444385047237021.jpg&quot; alt=&quot;1.jpg&quot; width=&quot;578&quot; height=&quot;398&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 578px; height: 398px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;时间：3月 &amp;nbsp;| &amp;nbsp;地点：三明建宁&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;有人赞梨花“占断天下白，压尽人间花”，然而，长期禁锢在车水马龙中的都市人，又有几人记得那千树万树梨花开的美景？&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444385053707534.jpg&quot; title=&quot;1444385053707534.jpg&quot; alt=&quot;建宁万亩梨花赏花活动-1-新浪旅游.jpg&quot; width=&quot;582&quot; height=&quot;402&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 582px; height: 402px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;建宁是“中国黄花梨之乡”，每年3月，10万亩梨花竞相开放，铺在丘陵上，好似春天里的白雪覆盖，非常吸引人。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444385059655093.jpg&quot; title=&quot;1444385059655093.jpg&quot; alt=&quot;建宁万亩梨花赏花活动-2-新浪旅游.jpg&quot; width=&quot;581&quot; height=&quot;399&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 581px; height: 399px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;此外，3、4月也是建宁桃花的花期，赏完梨花再赏桃花，两全其“美”。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444385065184813.jpg&quot; title=&quot;1444385065184813.jpg&quot; alt=&quot;建宁万亩梨花赏花活动-3.jpg&quot; width=&quot;580&quot; height=&quot;424&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 580px; height: 424px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;建宁赏花攻略&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;赏花地点：建宁县溪口镇枫园村桃梨观赏园及其它桃梨观赏点、莲乡大观园、闽江源生态旅游区、中央苏区反“围剿”纪念馆等景区。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;其中建宁县溪口镇枫园村桃梨观赏园，距离建宁县城7公里，总面积1000余亩。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444385071798306.jpg&quot; title=&quot;1444385071798306.jpg&quot; alt=&quot;建宁万亩梨花赏花活动-4.jpg&quot; width=&quot;568&quot; height=&quot;348&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 568px; height: 348px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;桃梨观赏线路&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;D1：中午到达建宁，用午餐；下午桃梨观赏园赏花，参观全国百个红色旅游经典景区、全国爱国主义教育示范基地、全国重点文物保护单位——中央苏区反“围剿”纪念园；晚上在高峰森林人家品尝农家特色菜。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;D2：早餐后，金铙山风景区乘坐福建省最长、海拔最高索道(全长2.1公里)游览世界地质公园金铙山白石顶；中午在樱桃岭凤临阁用午餐；午餐后游览闽江源瀑布景区，返程。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444385076531432.png&quot; title=&quot;1444385076531432.png&quot; alt=&quot;建宁万亩梨花赏花活动-5.png&quot; width=&quot;575&quot; height=&quot;387&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 575px; height: 387px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;怎么去建宁&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;动车：福州乘坐动车到建宁县北站，然后乘坐公交车到建宁县城短途汽车站，再转乘公交车或前往里心、黄埠、客坊乡的农村客运班车，到溪口镇桃梨观赏园下，全程约3个小时。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;自驾：从福州上福银高速，到建宁高速口下，往江西方向走7公里即可到桃梨观赏园，全程约4.5小时&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 1),
(3, 57, '官溪桃花节', '官溪桃花节', './Public/Uploads/2017-04-11/58ec47054ec01.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 26px; font-weight: 500; overflow: hidden; text-align: center; line-height: 60px;&quot;&gt;官溪桃花节&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880159358818.jpg&quot; title=&quot;1444372764341393.jpg&quot; alt=&quot;官溪桃花节-1-台州19楼.jpg&quot; width=&quot;591&quot; height=&quot;353&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 591px; height: 353px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;时间：3月至4月 | 地点：白水洋官溪村&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;正是一年春好处，烂漫山花竞相开。每年的3月中旬至4月，也正是官溪千亩桃花红，美景迎客来的时候。粉红的桃花立于枝头，争奇斗艳，远远望去就是一片花的海洋。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;当然除了美不胜收的桃花林，在历届的官溪桃花节活动中，还有必不可少国家级非物质文化遗产黄沙狮子的表演、戚继光“鸳鸯阵”的阵法表演。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880160662648.jpg&quot; title=&quot;1444372917837449.jpg&quot; alt=&quot;官溪桃花节-3.jpg&quot; width=&quot;588&quot; height=&quot;390&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 588px; height: 390px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;赏花攻略&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;赏花地点：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;临海的括苍镇和白水洋镇都是盛产蜜桃的地方，有许多观赏桃花的地方。看桃花最好的地方就是在白水洋上官、溪头（包括楼下坦）、官溪一带，不仅面积大，而且有坡度，很有层次感。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;最佳观景点是北面的牛背山上与楼下坦村西边、西北边的山上。走常规线路去看，其实只看到一半多一点的桃花，还有一大片的桃花是在楼下坦村的南边一直往东到达白水洋镇的峡长的地带。那里还有古石桥、古樟木等，远眺大雷山也很美。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880161227214.jpg&quot; title=&quot;1444373074203640.jpg&quot; alt=&quot;官溪桃花节-2-台州19楼.jpg&quot; width=&quot;588&quot; height=&quot;350&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 588px; height: 350px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;赏花线路：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;岭溪桃花林——仙居下各的羊棚头桃花源——白水洋官溪桃花——渭溪桃花——大雷山&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;第一站：岭溪桃花林。岭溪是临海括苍山桃花节的举办地，离临海括苍镇较近。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;第二站：仙居下各的羊棚头。看了岭溪的桃花后，再去羊棚头，在羊棚头村除了观赏桃源美景，还可以登上麻姑岩，欣赏了山顶奇石，俯瞰桃花观赏区全景。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;第三站：白水洋官溪的桃花。官溪一带的桃花林面积大，桃花种在山坡上，很有层次感。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;第四站：大雷山。如果有时间再去渭溪探一下通往大雷山的路。而渭溪桃花林就在通往大雷山另一侧的路。在渭溪村还可以观赏古老的石拱桥和石屋，最后驱车去渭溪水库，结束了一天的观赏桃花休闲之旅。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 0),
(4, 57, '白水洋万人狂欢泼水节', '白水洋万人狂欢泼水节', './Public/Uploads/2017-04-11/58ec47481f1b0.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 26px; font-weight: 500; overflow: hidden; text-align: center; line-height: 60px;&quot;&gt;白水洋万人狂欢泼水节&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444372575672236.jpg&quot; title=&quot;1444372575672236.jpg&quot; alt=&quot;白水洋万人狂欢泼水节-2.jpg&quot; width=&quot;588&quot; height=&quot;380&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 588px; height: 380px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;时间：6月份 &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;地点：宁德白水洋景区&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444372582182142.jpg&quot; title=&quot;1444372582182142.jpg&quot; alt=&quot;白水洋万人狂欢泼水节-3.jpg&quot; width=&quot;589&quot; height=&quot;368&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 589px; height: 368px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;白水洋素有“天下绝景，宇宙之谜”之称，是目前世界上唯一的“浅水广场”，此广场总面积达8万平方米，一石而就，河床布水均匀，净无沙砾，人行其上，水仅没踝。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;除了拥有天下独一无二的“十里水街”之外，白水洋每年盛夏都要举办“万人狂欢泼水节”。届时，万米水上广场、万人泼水“湿身”，共享“冰火两重天”的火热激情与清凉夏日。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;白水洋官方网：&lt;a href=&quot;http://www.yyx.com.cn/index.html&quot; _src=&quot;http://www.yyx.com.cn/index.html&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51);&quot;&gt;http://www.yyx.com.cn/index.html&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444372595970279.jpg&quot; title=&quot;1444372595970279.jpg&quot; alt=&quot;白水洋万人狂欢泼水节-4.jpg&quot; width=&quot;592&quot; height=&quot;378&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 592px; height: 378px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444372568619777.jpg&quot; title=&quot;1444372568619777.jpg&quot; alt=&quot;白水洋万人狂欢泼水节-1.jpg&quot; width=&quot;590&quot; height=&quot;400&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 590px; height: 400px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;门票：&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;白水洋120.00元(包含白水洋20.00元/人的观光车费) 鸳鸯溪：78.00元(不含宜洋洞梯40.00元/人) 联票：170.00元(白水洋且含观光车+鸳鸯溪，2天票期)&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;免票政策：6周岁（含6周岁）以下或身高1.2米（含1.2米）以下的儿童免票；景区对70周岁以上实行免门票政策，但是要购景区观光车费20元/人，景区内自费项目不免。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;优惠政策：学生证、教师、现役军人、残疾人、老年人（60周岁-70周岁）、宗教人士凭有效证件实行半票。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160121/1453361239935646.jpg&quot; title=&quot;1453361239935646.jpg&quot; alt=&quot;1.jpg&quot; width=&quot;592&quot; height=&quot;508&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 592px; height: 508px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;装备提示：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;1、如果您想下水“湿身“，记得带上泳衣及干衣服，最好穿拖鞋或者凉鞋。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;2、一张门票配一双防滑袜，如果不是联票，可以进入景区后购买，一双3元。（岩石在河水冲刷下很光滑）若游玩时间较长建议自备一双较厚的袜子，一是防滑效果更佳，二来脚底也不会磨痛。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;3、手机或其他数码设备最好放入有拉链的口袋或包里，防止滑入溪中。佩戴眼镜或者墨镜的朋友，记得在观景台要一根细绳绊住镜架两端，以免眼镜或墨镜掉入水中。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;友情提示：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;在白水洋观景台可以做好下水前的准备。观景台有配套的淋浴设施（淋浴免费），同时也提供寄存服务，餐饮服务。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;物品寄存标准：租用费15元，押金15元。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444372542476747.jpg&quot; title=&quot;1444372542476747.jpg&quot; alt=&quot;1.jpg&quot; width=&quot;590&quot; height=&quot;498&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 590px; height: 498px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;交通攻略：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;1、公车路线：&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;屏南县市中心距景区25km，到屏南汽车站乘公交至白水洋/鸳鸯溪景区;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;宁德市汽车北站距景区150km，到宁德汽车北站乘宁德至屏南班车，至屏南汽车站后转区间公交至景区。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;2、自驾车：&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;福州至屏南&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;（1）福州市区可从福州东（马尾），福州北（五四北）上福宁高速至漳湾出口（宁德出口的下一个出口）下高速。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;（2）104国道到八都，再走宁屏二级路到屏南&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;厦门至屏南&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;（1）沈海高速往温州方向至漳湾出口（宁德出口的下一个出口）下高速。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;（2）104国道到八都，再走宁屏二级路到屏南&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, 0),
(5, 57, '平潭沙雕文化节', '平潭沙雕文化节', './Public/Uploads/2017-04-11/58ec483d4cb95.png', '&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-align: justify&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20160914/1473830096788411.jpg&quot; title=&quot;1473830096788411.jpg&quot; alt=&quot;1_副本.jpg&quot; style=&quot;border: 0px;max-width: 100%&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-align: justify&quot;&gt;时间：5月-10月 &amp;nbsp;| &amp;nbsp;地点：平潭海渔广场沙雕园&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-size: 16px;font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0&quot;&gt;&lt;img title=&quot;1444440767361413.png&quot; alt=&quot;平潭沙雕文化节-2.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20151010/1444440767361413.png&quot; width=&quot;588&quot; height=&quot;363&quot; style=&quot;border: 0px;max-width: 100%;width: 588px;height: 363px&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong&gt;活动介绍：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;平潭沙雕文化节是平潭综合实验区着力打造的一项特色鲜明、影响深远的品牌旅游项目，已连续多年举办沙雕节活动，吸引了大批的游客前往参观。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;今年，2016平潭国际沙雕节 以“启航旅游岛，海丝平潭蓝”为主题，在打造上更加趋向国际化和品牌化，在园区分布上主要分为主题概况区、印象海山区、岚岛春秋区、渔村情思区、海岛新韵区、国际平潭区以及实景舞台区等不同观赏区域，力求全方位展示平潭的景点、传说、未来、海丝和两岸的情节等元素。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong&gt;一是创造首个沙雕迷宫。&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;沙雕迷宫建在主雕塑背后，游客可在其中身临其境地体验寻找和迷失的刺激与乐趣。同时，迷宫还征集游客为迷宫墙体涂抹喷色，体会动手参与的乐趣。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong&gt;二是在园区内上演平潭首个实景歌舞剧《海坛春秋》。&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;歌舞剧由青运会导演和来自福州剧院的专家共同参与编排。剧中融入众多与平潭发展的历史和未来相关的元素，通过歌舞艺术的表现方式，让游客深入了解平潭一夜渔村一夜都城的变化历程。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong&gt;三是打造创吉尼斯世界纪录的巨型沙漏。&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;沙漏将用在沙雕节闭幕倒计时使用，在整个沙雕节庆过程中还将表现纪念逝去的时光的特别意义。希望游客们能够在清扬的夏日，记住沙雕的美好画面。同时在沙雕节闭幕时，工作人员还将把沙漏中的沙子取出分制成许多小型沙漏，留给游客珍藏。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong&gt;四是建造独具特色的水管艺术住宅。&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;夜幕降临时，游客除了可以在篝火旁狂欢，狂欢过后还可以在水管艺术住宅中休息。黎明时分，还能欣赏到海边的日出，静静的聆听海浪与清风宿醉后的交响，可谓一举多得。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;img title=&quot;1444440760750937.jpg&quot; alt=&quot;平潭沙雕文化节-1.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20151010/1444440760750937.jpg&quot; width=&quot;583&quot; height=&quot;389&quot; style=&quot;border: 0px;max-width: 100%;text-align: justify;font-family: 宋体;width: 583px;height: 389px&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;img title=&quot;1444440783191122.png&quot; alt=&quot;平潭沙雕文化节-4-昵图网.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20151010/1444440783191122.png&quot; width=&quot;592&quot; height=&quot;365&quot; style=&quot;border: 0px;max-width: 100%;font-family: 宋体;font-weight: bold;width: 592px;height: 365px&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong&gt;交通信息：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;1、飞机&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;距离平潭最近的机场是长乐机场。若选择乘飞机到达平潭，可以先乘飞机到达福州市长乐机场，然后再转乘汽车前往平潭。长乐机场位于福州长乐漳港镇美丽的海边距离福州市区约39公里，机场到市区约40分钟。长乐机场现开通有至厦门、北京、香港、广州、上海、重庆、合肥、天津、海口、深圳、杭州、武汉、西安、桂林、昆明、珠海、澳门、济南等城市的航线。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;长乐机场有民航班车和多种出租车往返市区。长乐机场距平潭约50公里，乘车地点在机场到达厅南侧，肯德基楼下，发车时间为:12:30、14:30、16:30、19:00、21:30、23:30，票价50元，车程近2小时。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;此外，也可以在机场乘坐去福州南站大巴，车程约50-70分钟，票价25元，换乘开往平潭的大巴，每25分钟一趟，票价7元。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;2、火车&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;平潭没有火车站，但若选择铁路到达平潭，可以先乘火车到达福州市，然后再转乘汽车前往平潭。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;福州有2个火车站，分别是福州站和福州南站。福州站位于福州市晋安区华林路502号，福州南站位于福州市仓山区闽江和乌龙江围合的南台岛东端。多个车次直达北京、上海、青岛、南京、合肥、泉州、南昌、厦门、鹰潭、光泽等地。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;从福州站到平潭：&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;（1）先从火车站乘坐公交51路在汽车站下，公交2路在打铁档站下，K2路在雁塔站下，步行至福州汽车站南站，每25分钟一趟，票价7元。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;（2）从火车站乘公交K2线路到闽运汽车北站，每30分钟一趟开往平潭，票价约7元。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;从福州南站到平潭：&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;（1）先从火车站坐公交309路到打铁档站下，公交K2路到雁塔站下，公交124路到茶亭公园站下，步行至福州汽车站南站，每25分钟一趟，票价7元。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;（2）先从火车站坐公交K2路到闽运汽车站北站，每30分钟一趟开往平潭，票价约7元。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;3、大巴&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;从福州客运北站转乘大巴到达平潭，从6:00-18:00的每隔半小时都有一班车。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;4、自驾&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;渔溪/平潭/江阴高速路口--渔平高速--平潭海峡大桥--龙凤头海滨浴场&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-size: 16px;font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0&quot;&gt;&lt;img title=&quot;1444440791348312.jpg&quot; alt=&quot;平潭沙雕文化节-5.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20151010/1444440791348312.jpg&quot; width=&quot;591&quot; height=&quot;372&quot; style=&quot;border: 0px;max-width: 100%;font-weight: bold;width: 591px;height: 372px&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong&gt;小贴士：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;1、福州南站一层的集散中心还在建设中，暂时没有前往平潭等县的班车。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;2、平潭当地的公交线路不少，出行代步比较方便。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;font-family: &amp;#39;Microsoft Yahei&amp;#39;, Arial, sans-serif;text-indent: 2em;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;3、夏季日光足、海风大，不论是在平潭游玩还是参赛，记得做好防晒。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, 1),
(6, 57, '塔下村萤火虫节', '塔下村萤火虫节', './Public/Uploads/2017-04-11/58ec487711fcc.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 26px; font-weight: 500; overflow: hidden; text-align: center; line-height: 60px;&quot;&gt;塔下村萤火虫节&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880048667962.jpg&quot; title=&quot;1453362154320131.jpg&quot; width=&quot;586&quot; height=&quot;389&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 586px; height: 389px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;时间：6月-8月 &amp;nbsp; | &amp;nbsp;地点：漳州南靖土楼景区&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880048725511.jpg&quot; title=&quot;1453362154977611.jpg&quot; width=&quot;583&quot; height=&quot;396&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 583px; height: 396px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880049447266.jpg&quot; title=&quot;1453362154175238.jpg&quot; width=&quot;588&quot; height=&quot;392&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 588px; height: 392px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;“黑黑的天空低垂，亮亮的繁星相随……虫儿飞，花儿睡，一双又一对才美……”在世界文化遗产地南靖土楼景区，素有“太极水乡”美誉的塔下村成千上万只萤火虫自然群居于此。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;每当夜幕降临，从塔下村通往田螺坑的道路两旁，绿光闪烁的小生灵们就会不约而同地飞出来，整个情景宛若人间仙境。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;据介绍，萤火虫观赏之旅是今年新推出的旅游项目。由于塔下村地处偏远，植被茂盛，生态环境好，每年夏季总会有众多的野生萤火虫聚集，5月底到8月底是最佳的观赏期。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880049462267.jpg&quot; title=&quot;1453362154780538.jpg&quot; width=&quot;587&quot; height=&quot;392&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 587px; height: 392px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880049211336.jpg&quot; title=&quot;1444444791225969.jpg&quot; alt=&quot;塔下村萤火虫节-2.jpg&quot; width=&quot;589&quot; height=&quot;358&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 589px; height: 358px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;萤火虫观赏线路：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;从南靖书洋塔下村大坝停车场走起，沿路一公里长的小溪边，都是萤火虫活动的范围。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;最佳观赏时间：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;5月底到8月底，晚上7点半到10点半&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;雨天无法赏萤，下雨的时候萤火虫都躲在树叶背面，并且雨天空气湿度较大，露水沾在萤火虫翅膀上面，导致其无法飞翔。月圆之夜会影响萤火虫活动，可能导致萤火虫数量变少&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880050137284.png&quot; title=&quot;1444444797796320.png&quot; alt=&quot;塔下村萤火虫节.png&quot; width=&quot;589&quot; height=&quot;382&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 589px; height: 382px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;门票：&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;看“萤火虫王国”，不需要另外加钱，直接买南靖土楼A线的门票即可，只是塔下村离南靖土楼旅游区的门口，还有12公里的路程，需坐旅游车前往。目前计划在接待中心和云水谣景区设置旅游车接送点，开放后方便游客乘坐。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;交通信息：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;1.住云水瑶的游客18：40到云水谣桥头停车场(德风楼)，住在游客服务中心附近的19：00在中心广场乘车。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;2.自己车一律停在塔下水尾停车场或大巴停车场，赏萤区不准停车。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;3.住在塔下村的游客建议走路或乘坐电瓶车至赏萤区。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880050352555.jpg&quot; title=&quot;1453362154308007.jpg&quot; width=&quot;588&quot; height=&quot;397&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 588px; height: 397px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;赏萤须知：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;1、萤火虫观赏全部采用预约。游客预约后领取赏萤时间手环，观赏后交还。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;2、赏萤区蚊虫蛇蚁较多，为防止蚊虫叮咬，避免毒蛇咬伤，建议穿上长袖衣服、长腿裤子、长筒雨靴。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;3、萤火虫对光和噪音很敏感，观赏萤火虫时，不宜使用手电筒、闪光灯等发光的工具，也不宜太靠近萤火虫，或是大声喧哗。而摄影爱好者想去拍萤火虫，建议带上三脚架和快门线&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, 0),
(7, 57, '福州温泉国际旅游节', '福州温泉国际旅游节', './Public/Uploads/2017-04-11/58ec495a373db.jpg', '&lt;h3 style=&quot;;padding: 0px;font-size: 26px;font-weight: 500;overflow: hidden;text-align: center;line-height: 60px&quot;&gt;福州温泉国际旅游节&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;img title=&quot;1444441985413036.jpg&quot; alt=&quot;福州温泉国际旅游节-1.jpg&quot; src=&quot;/Public/upload/image/201704/1491880277611946.jpg&quot; width=&quot;607&quot; height=&quot;384&quot; style=&quot;border: 0px;max-width: 100%;font-family: 宋体;font-weight: bold;width: 607px;height: 384px&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;时间：11-1月 &amp;nbsp;| &amp;nbsp;地点：福州温泉景区&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880278779802.jpg&quot; title=&quot;1453366849963688.jpg&quot; alt=&quot;溪山温泉疍家人.jpg&quot; style=&quot;border: 0px;max-width: 100%&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880278314479.jpg&quot; title=&quot;1453366794895155.jpg&quot; width=&quot;596&quot; height=&quot;370&quot; style=&quot;border: 0px;max-width: 100%;width: 596px;height: 370px&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;strong&gt;活动介绍：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;福州人爱泡温泉，似乎已成了一种习惯。由于在福州随处走走就会发现遍布着温泉，其优越的条件在全国大中城市中绝无仅有，有不少在市区一带的许多人，在家里就能享受温泉。当华清池温泉只是帝王贵族的高贵独有享受时，福州城内到处都有“汤池店”澡堂，福州市区的许多酒店小区都有温泉提供。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-size: 16px;font-family: 宋体&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880278280379.jpg&quot; title=&quot;1453366791358967.jpg&quot; style=&quot;border: 0px;max-width: 100%&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-size: 16px;font-family: 宋体&quot;&gt;&lt;img title=&quot;1444442013134803.jpg&quot; alt=&quot;福州温泉国际旅游节-4.jpg&quot; src=&quot;/Public/upload/image/201704/1491880279365962.jpg&quot; width=&quot;606&quot; height=&quot;407&quot; style=&quot;border: 0px;max-width: 100%;font-weight: bold;width: 606px;height: 407px&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;福州温泉开发利用至今已有1700多年历史，泉量丰富，泉质优异。“五凤朝阳生丽水，九龙经脉出金汤”，正是对福州温泉的生动写照。近年来，福州市大力发展温泉旅游产业，推进温泉旅游精品项目建设，逐步形成功能完备的温泉旅游产业集群，福州温泉旅游的知名度与影响力与日俱增。自2010年起，福州围绕“中国温泉之都”的金字牌匾，已连续四届成功举办了福州温泉国际旅游节，打造了特色鲜明、内涵丰富的温泉旅游节庆品牌，使“温泉古都·有福之州”的城市形象深入人心。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;img title=&quot;1444442005163051.jpg&quot; alt=&quot;福州温泉国际旅游节-3.jpg&quot; src=&quot;/Public/upload/image/201704/1491880279230692.jpg&quot; style=&quot;border: 0px;max-width: 100%;font-family: 宋体&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;温泉节期间将开设温泉直通车专线，发车点设在福州市三坊七巷及工人文化宫门口。市民在付出低廉的票款后可乘坐温泉直通车前往各个温泉景点，往返一票制10元，车票将作为返程上车的凭证。同时，市民乘坐温泉直通车前往温泉景点后，还可凭借车票获得车票款的等值返还，返还金额直接抵相关温泉景点的门票款。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 宋体&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880280595442.jpg&quot; title=&quot;1453367134181210.jpg&quot; alt=&quot;1.jpg&quot; style=&quot;border: 0px;max-width: 100%&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 宋体&quot;&gt;&lt;img title=&quot;1444441992828056.jpg&quot; alt=&quot;福州温泉国际旅游节-2.jpg&quot; src=&quot;/Public/upload/image/201704/1491880280459790.jpg&quot; width=&quot;604&quot; height=&quot;422&quot; style=&quot;border: 0px;max-width: 100%;width: 604px;height: 422px&quot;/&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;strong&gt;交通信息：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;2014年温泉节便民直通专线参考&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;线路1、台江文化宫--天生海水温泉景区：11月15日（星期六）至11月21日（星期五），每日一班，9:00发车，车程130分钟，16:00返程；11月25日（星期二）至12月28日（星期天），每周二、四、六、日每日一班，9:30发车，16:00返程。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;线路2、台江文化宫（9:15发车）--黄楮林温泉景区（16:00返程），车程150分钟。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;线路3、台江文化宫（9:45发车）--永泰香米拉（16:45返程）--青云山御温泉景区（16:00返程），车程90分钟。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;线路4、台江文化宫（10:00发车）--川捷温泉高尔夫俱乐部酒店（16:55返程）--溪山温泉旅游度假村（16:00返程），车程70分钟。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;线路5、三坊七巷（9:15发车）--桂湖美人谷温泉景区（16:40返程）--贵安温泉景区（16:00返程），车程75分钟。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;线路6、三坊七巷（9:30发车）--源脉温泉景区（18:00返程）--七叠温泉景区（16:00返程），车程135分钟。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;线路7、三坊七巷（9:45发车）--旗山森林温泉度假村（17:00返程）--赤壁温泉景区（16:00返程），车程110分钟。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 0),
(8, 57, '清水祖师迎春活动', '清水祖师迎春活动', './Public/Uploads/2017-04-11/58ec49b5ab761.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 26px; font-weight: 500; overflow: hidden; text-align: center; line-height: 60px;&quot;&gt;清水祖师迎春活动&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880365116928.jpg&quot; title=&quot;1453368627141044.jpg&quot; width=&quot;594&quot; height=&quot;398&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 594px; height: 398px;&quot;/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;时间：正月初八至十一 &amp;nbsp;| &amp;nbsp;地点：安溪清水岩&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880365168807.jpg&quot; title=&quot;1444443927842519.jpg&quot; alt=&quot;清水祖师迎春活动-1.jpg&quot; width=&quot;587&quot; height=&quot;391&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 587px; height: 391px;&quot;/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;活动介绍：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;清水祖师迎春民俗，始自宋代，盛在明时。迎春订在开春，寄托着当地百姓叩请恩主清水大师下山游境，驱邪镇魔，扶持庇佑风调雨顺、国泰民安、五谷丰登、六畜兴旺、里社和平。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;清水祖师迎春绕境巡香仪仗阵容庞大、规矩严格、程序庄重。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;一为春官阵。春官阵依旧时县令的仪容队伍。前面由一对敲着“咚咚”单声对应的“头人鼓”开先。一位身着羊羔皮毛夹袄、手执告示牌和一根竹梢，口中大声呵喝“开、开”的“跑遍”清道。四位礼生、四人戏班，四人香盘、一支署标“清水巡境司”的红凉伞引领着“春官”大蓝轿、“案公”大兰轿，两辆大兰轿的四面角应写掛“清水巡境司”轿灯，“春官”“案公”应戴礼帽穿呢大衣掛红绫坐在轿中，后面凉伞及茶担跟上。一路风光荣耀。谁都不能逾越，这就是“春官”阵的风采。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;二为迎春绕境队。迎春绕境队以民间礼仪排场。“正大旗”标在前面。车鼓亭有鼓、钟、钹仔、嗳仔、双蝶等。身穿“兵”、“勇”字的衙服，举着敕赐的三十六“驾”行列于后。再接着是各地民间“随香”的鼓乐队、古装戏乐队，有大鼓吹、花鼓队、弄龙、舞狮、彩阁、彩球、南音、车鼓等技艺工夫和艺苑故事。轿后，紧跟着三支彩绣的“清水祖师”大凉伞。还跟随着一面大锣和手执法器敲绕钹鼓的岩僧。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;随香的海内外游客数以万计、蔚为壮观。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880366554933.jpg&quot; title=&quot;1453368619200882.jpg&quot; width=&quot;589&quot; height=&quot;397&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 589px; height: 397px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880366967636.jpg&quot; title=&quot;1453368623890702.jpg&quot; width=&quot;589&quot; height=&quot;397&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 589px; height: 397px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880367839994.jpg&quot; title=&quot;1453368623457726.jpg&quot; width=&quot;592&quot; height=&quot;399&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 592px; height: 399px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880367715433.jpg&quot; title=&quot;1453368619587609.jpg&quot; width=&quot;586&quot; height=&quot;398&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 586px; height: 398px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880367126276.jpg&quot; title=&quot;1453368622409197.jpg&quot; width=&quot;592&quot; height=&quot;400&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 592px; height: 400px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;门票信息：&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;15.00元&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;交通信息：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;到达泉州市区后，可以乘坐公交车2路到恒兴车站，里面有清水岩专线。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;或者到达安溪县城后，坐车到安溪龙湖或者北石车站，再坐到蓬莱清水岩的车。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;小贴士：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;春节期间到安溪清水岩只能带三支香进祖殿礼佛朝拜。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491880368982035.jpg&quot; title=&quot;1453368622690501.jpg&quot; width=&quot;593&quot; height=&quot;899&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 593px; height: 899px;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 4, 1);
INSERT INTO `play` (`id`, `cate_id`, `title`, `descs`, `pic`, `content`, `type`, `ishot`) VALUES
(9, 57, '风筝冲浪国际巡回赛平潭站', '风筝冲浪国际巡回赛平潭站', './Public/Uploads/2017-04-11/58ec60d49ac8e.jpg', '&lt;h3 style=&quot;;padding: 0px;font-size: 26px;font-weight: 500;overflow: hidden;text-align: center;line-height: 60px&quot;&gt;风筝冲浪国际巡回赛平潭站&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-size: 16px;color: rgb(0, 0, 0);letter-spacing: 0;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;&lt;img title=&quot;1444384836317244.jpg&quot; alt=&quot;风筝冲浪国际巡回赛平潭站-1-平潭旅游局.jpg&quot; src=&quot;/Public/upload/image/201704/1491886276266701.jpg&quot; width=&quot;587&quot; height=&quot;383&quot; style=&quot;border: 0px;max-width: 100%;width: 587px;height: 383px&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-size: 16px;color: rgb(0, 0, 0);letter-spacing: 0;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;时间：5月-11月 | 地点：平潭海渔广场&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;img title=&quot;1444384847476378.jpg&quot; alt=&quot;风筝冲浪国际巡回赛平潭站-2.jpg&quot; src=&quot;/Public/upload/image/201704/1491886276765434.jpg&quot; width=&quot;591&quot; height=&quot;364&quot; style=&quot;border: 0px;max-width: 100%;font-family: 宋体;font-size: 16px;font-weight: bold;letter-spacing: 0;width: 591px;height: 364px&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;&lt;strong&gt;活动介绍：&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;每年夏季，福建平潭将会举办国际顶级风筝冲浪赛事，该项比赛混合了特技风筝、帆板、冲浪等多种运动元素，是世界上最刺激和最充满活力的极限运动，被誉为“最能吸引众多比基尼女郎眼球的运动”。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;在被五彩斑斓的比基尼女郎包围的海滩上，突然腾空飞起一只色彩鲜艳的巨大风筝，风筝细线连着一个帅小伙子，小伙子踩着滑板在波涛翻滚的海面上急速滑行，岸边传来比基尼女郎的尖叫，这是何等的拉风。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;实际上作为一项时尚的海上体育运动，风筝冲浪近年来已经从欧美国家进入中国得到推广，并且在与平潭一水之隔的台湾，这项运动也很受欢迎。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-size: 16px;font-weight: bold;color: rgb(0, 0, 0);letter-spacing: 0;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;&lt;img title=&quot;1444384853591002.jpg&quot; alt=&quot;风筝冲浪国际巡回赛平潭站-3-平潭旅游局.jpg&quot; src=&quot;/Public/upload/image/201704/1491886277252399.jpg&quot; width=&quot;605&quot; height=&quot;362&quot; style=&quot;border: 0px;max-width: 100%;width: 605px;height: 362px&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-size: 16px;font-weight: bold;color: rgb(0, 0, 0);letter-spacing: 0;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;&lt;img title=&quot;1444384869668142.jpg&quot; alt=&quot;风筝冲浪国际巡回赛平潭站-4-平潭旅游局.jpg&quot; src=&quot;/Public/upload/image/201704/1491886279897273.jpg&quot; width=&quot;604&quot; height=&quot;339&quot; style=&quot;border: 0px;max-width: 100%;font-family: 宋体;width: 604px;height: 339px&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;&lt;strong&gt;交通信息：&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;1、飞机&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;距离平潭最近的机场是长乐机场。若选择乘飞机到达平潭，可以先乘飞机到达福州市长乐机场，然后再转乘汽车前往平潭。长乐机场位于福州长乐漳港镇美丽的海边距离福州市区约39公里，机场到市区约40分钟。长乐机场现开通有至厦门、北京、香港、广州、上海、重庆、合肥、天津、海口、深圳、杭州、武汉、西安、桂林、昆明、珠海、澳门、济南等城市的航线。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;长乐机场有民航班车和多种出租车往返市区。长乐机场距平潭约50公里，乘车地点在机场到达厅南侧，肯德基楼下，发车时间为:12:30、14:30、16:30、19:00、21:30、23:30，票价50元，车程近2小时。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;此外，也可以在机场乘坐去福州南站大巴，车程约50—70分钟，票价25元，换乘开往平潭的大巴，每25分钟一趟，票价7元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;2、火车&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;平潭没有火车站，但若选择铁路到达平潭，可以先乘火车到达福州市，然后再转乘汽车前往平潭。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;福州有2个火车站，分别是福州站和福州南站。福州站位于福州市晋安区华林路502号，福州南站位于福州市仓山区闽江和乌龙江围合的南台岛东端。多个车次直达北京、上海、青岛、南京、合肥、泉州、南昌、厦门、鹰潭、光泽等地。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;从福州站到平潭：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;（1）先从火车站乘坐公交51路在汽车站下，公交2路在打铁档站下，K2路在雁塔站下，步行至福州汽车站南站，每25分钟一趟，票价7元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;（2）从火车站乘公交K2线路到闽运汽车北站，每30分钟一趟开往平潭，票价约7元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;从福州南站到平潭：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;（1）先从火车站坐公交309路到打铁档站下，公交K2路到雁塔站下，公交124路到茶亭公园站下，步行至福州汽车站南站，每25分钟一趟，票价7元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;（2）先从火车站坐公交K2路到闽运汽车站北站，每30分钟一趟开往平潭，票价约7元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;3、大巴&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;从福州客运北站转乘大巴到达平潭，从6:00-18:00的每隔半小时都有一班车。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;4、自驾&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;渔溪/平潭/江阴高速路口--渔平高速--平潭海峡大桥--龙凤头海滨浴场&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-size: 16px;color: rgb(0, 0, 0);letter-spacing: 0;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491886279467957.jpg&quot; title=&quot;1453358805839340.jpg&quot; alt=&quot;1.jpg&quot; width=&quot;601&quot; height=&quot;395&quot; style=&quot;border: 0px;max-width: 100%;width: 601px;height: 395px&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;&lt;strong&gt;小贴士：&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;1、福州南站一层的集散中心还在建设中，暂时没有前往平潭等县的班车。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;2、平潭当地的公交线路不少，出行代步比较方便。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;&quot;&gt;3、夏季日光足、海风大，不论是在平潭游玩还是参赛，记得做好防晒。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 0),
(10, 57, '南安香草世界樱花节', '南安香草世界樱花节', './Public/Uploads/2017-04-11/58ec611f219a9.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 26px; font-weight: 500; overflow: hidden; text-align: center; line-height: 60px;&quot;&gt;南安香草世界樱花节&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444385254685523.jpg&quot; title=&quot;1444385254685523.jpg&quot; alt=&quot;南安香草世界樱花节-1-新华网闽南频道.jpg&quot; width=&quot;585&quot; height=&quot;379&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 585px; height: 379px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;时间：2-4月 &amp;nbsp;| 地点：南安香草世界度假村&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;南安的春天一到，在樱花园里，缤纷如火的绝美樱花开满了小路两旁，淡粉的、朱红的、艳红的……红透了半边天，它们正向人们散发着春的气息！当你置身在一片花海之中，眼中是樱花温柔的旋舞，再加之阵阵暗香传来，怎么能不让你爱上这里。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;在樱花节感受漫天飞舞的花美景，也是一年里最心仪的事情。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444385264492282.jpg&quot; title=&quot;1444385264492282.jpg&quot; alt=&quot;南安香草世界樱花节-2.jpg&quot; width=&quot;581&quot; height=&quot;373&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 581px; height: 373px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;赏花攻略&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;由于南安香草世界位于天柱山风景区内，对于住在厦漳泉的朋友们而言，这算是一个周末自驾游的好去处。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;香草世界门票：30—40元，学生证半价，65岁以上老人和1米2以下儿童免费。　　&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;樱花花期：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;南安香草世界预计2—4月份开，其中樱花进入旺盛后，一般可以开一个多月。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;自驾线路：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;南安成功雕像——过柳城大桥——美林圆盘左转——直走1公里多右转（诗山/永春方向）——过保福岭隧道——过收费站——左转（诗山/永春）——金陶——诗山镇区前——左转（蓬华方向）——山路10公里——蓬华（接下来沿着街道的指示牌子行驶）——香草世界度假村&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20151009/1444385271270874.jpg&quot; title=&quot;1444385271270874.jpg&quot; alt=&quot;南安香草世界樱花节-3.jpg&quot; width=&quot;586&quot; height=&quot;419&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; width: 586px; height: 419px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;赏花行程安排：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;抵达南安香草世界后，开始游览景游客服务周边的景点。如樱花林-开心农场-金熊乐园-宋代古井-枫树林-爱丁堡等；&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;接近中午，可以先返回到景区的餐厅，来享用午餐。在这里可以品尝到各种以香草为主题的农家菜肴，如天柱山封肉、食补土鸡汤、蓬华炒白果、天柱山土猪脚、蓬华咸菜饭、食补土鸡汤等；&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em;&quot;&gt;午餐后，在餐厅做短暂休息后，下午继续游览香草世界余下景点。法国薰衣草乐园-温沙城堡-野营基地-天池-天柱岩寺-天柱峰-好汉坡，而后返回游客中心，短暂歇息后，结束一天行程。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 1),
(11, 57, '海峡两岸花卉博览会', '海峡两岸花卉博览会', './Public/Uploads/2017-04-12/58edec02ee5d9.jpg', '&lt;h3 style=&quot;;padding: 0px;font-size: 26px;font-weight: 500;overflow: hidden;text-align: center;line-height: 60px&quot;&gt;海峡两岸花卉博览会&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;img title=&quot;1444441907499702.jpg&quot; alt=&quot;漳州花博会-1.jpg&quot; src=&quot;/Public/upload/image/201704/1491987453369490.jpg&quot; width=&quot;590&quot; height=&quot;406&quot; style=&quot;border: 0px;max-width: 100%;font-family: 宋体;font-size: 16px;letter-spacing: 0;width: 590px;height: 406px&quot;/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;时间：11月 | &amp;nbsp;漳州东南花都&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-size: 16px;font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0&quot;&gt;&lt;strong&gt;&lt;img src=&quot;/Public/upload/image/201704/1491987453856446.jpg&quot; title=&quot;1453365509887935.jpg&quot; width=&quot;595&quot; height=&quot;351&quot; style=&quot;border: 0px;max-width: 100%;width: 595px;height: 351px&quot;/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;strong style=&quot;font-family: 宋体&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491987454569015.jpg&quot; title=&quot;1453365509868298.jpg&quot; width=&quot;591&quot; height=&quot;442&quot; style=&quot;border: 0px;max-width: 100%;width: 591px;height: 442px&quot;/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;strong&gt;活动介绍：&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;海峡两岸花卉博览会（简称“花博会”）是国家4A级旅游区，省级农业旅游示范点。花博会位于福建漳州百里花卉走廊黄金地段的漳浦马口，国道324线旁，是历届“海峡两岸(福建漳州)花卉博览会”所在地，总面积7800亩，其中2700亩是花卉博览园。现有各种花卉苗木2000多种，年培育各种花卉苗木1000多万株，是集花卉生产、销售、展示、出口、旅游观光、农家休闲度假、会议培训、健身娱乐为一体的现代农业大观园。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;花都依山傍水，绿草如菌，棕榈园、锦锈漳州园、闽南瓜果园、沙生植物园、榕景园、荫生植物馆、百花廊、花卉超市、兰花培育基地、农家生态园、大地艺术展区、南溪茶文化一条街、纪念林植树区等景区交相辉映。到此旅游不仅可以观赏到闽南、台湾及国外的珍贵花卉植物，体验到闽南及台湾风俗，了解到现代农业生产技术;还可进行射箭、游泳、水疗、垂钓、打网球、户外拓展训练（滑索、攀岩、空中单杠、过独木桥）等一系列健身运动。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;img title=&quot;1444441921860893.jpg&quot; alt=&quot;漳州花博会-3.jpg&quot; src=&quot;/Public/upload/image/201704/1491987455245407.jpg&quot; width=&quot;593&quot; height=&quot;406&quot; style=&quot;border: 0px;max-width: 100%;font-family: 宋体;width: 593px;height: 406px&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;strong&gt;交通信息：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;1、漳州市区，从客运西站乘坐18路公交车至终点站即可，票价4元。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;2、从广东、诏安、云霄、漳浦往漳州方向的小型车辆，请从国道右转进入东泗路沿溪坂方向，至园区景观桥，绕行上国道往漳州行驶。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;3、其他车辆，请从国道右转进入东泗路沿龙海方向通行。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;4、花博园内还有设电瓶车运营点，分别在主大门、1号馆、景观桥、闽南文化广场、现代农业馆，游客可在各个运营点（不同年份稍有不同）买票，坐上电瓶车，在芳香四溢的花海当中游览。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1491987455928287.jpg&quot; title=&quot;1453365556174425.jpg&quot; alt=&quot;姐妹亭风景.jpg&quot; width=&quot;590&quot; height=&quot;417&quot; style=&quot;border: 0px;max-width: 100%;font-family: 宋体;width: 590px;height: 417px&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;strong&gt;小贴士：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;参观漳州花博会无需门票，只需提前网络预约。你可以在农博会官网、花都官网、手机微信、漳龙集团公司各营业网点和邮政网点采用身份证号码进行预约，成功预约后即可在预订时间内刷身份证免费入园。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;官网地址：&lt;a href=&quot;http://www.hxnbh.com/&quot; target=&quot;_self&quot; style=&quot;;color: rgb(51, 51, 51)&quot;&gt;http://www.hxnbh.com/&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 4, 1),
(12, 57, '仙公山除夕守岁', '仙公山除夕守岁', './Public/Uploads/2017-04-12/58edec52144bd.jpeg', '&lt;h3 style=&quot;;padding: 0px;font-size: 26px;font-weight: 500;overflow: hidden;text-align: center;line-height: 60px&quot;&gt;仙公山除夕守岁&lt;/h3&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;strong&gt;&lt;img title=&quot;1444442157918454.jpg&quot; alt=&quot;仙公山除夕守岁-1.jpg&quot; src=&quot;/Public/upload/image/201704/1491987533835859.jpg&quot; width=&quot;605&quot; height=&quot;375&quot; style=&quot;border: 0px;max-width: 100%;font-family: 宋体;width: 605px;height: 375px&quot;/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;时间：除夕 &amp;nbsp;| &amp;nbsp;地点：泉州仙公山&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px;line-height: 21px;text-indent: 0&quot;&gt;&lt;img title=&quot;1444442165117649.jpeg&quot; alt=&quot;仙公山除夕守岁-2.jpeg&quot; src=&quot;/Public/upload/image/201704/1491987534508937.jpeg&quot; width=&quot;600&quot; height=&quot;406&quot; style=&quot;border: 0px;max-width: 100%;line-height: 20.7333px;width: 600px;height: 406px&quot;/&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;strong&gt;活动介绍：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;明清以来，仙公山成为闽南一带信徒和海外泉籍华侨华人景仰的圣地，香火鼎盛。随着泉州人的足迹，仙公信仰也流传到港澳台地区和东南亚海外。而仙公“十分灵圣”的传说，也吸引许多善男信女不远万里前来抽签卜卦、求梦问前程，因此每年来自各地的香客络绎不绝，仙公山也成为海内外泉籍乡亲口口相传的“祈梦圣地”。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;每年除夕，远近信徒扶老携幼，登山诣庙，陪伴仙公守岁。传说此夜祈祷，有求并应，可祛一年之灾。当夜聚集山寺有时多至万人。同时丰山仙洞也将举行“开锦室”仪式。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;仙公山奇岩怪石星罗棋布，山势险峻，雄伟壮观。主峰狮头峰海拔高758.5米，为泉州市区北郊第一高峰。这里风景秀丽，文物荟萃，自然和人文景观十分丰富。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;仙公山上，每一处景点也都有一段迷人的传说。如在一块磊岩石上有一左一右两个大脚印，名曰“仙脚迹”，传说为神仙降临踩踏所留；在“仙脚迹”旁镌刻有象棋盘一幅，相传是何氏九位仙师闲时弈棋娱乐之处；在白水岩后面，有一处深不见底、五公分圆径的岩穴裂孔，名曰“出米岩”；还有“仙井”、“藏杉井”、“仙茶树”等景迹，不胜枚举。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;strong&gt;交通信息：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;1、汽车&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;在泉州市区的新车站乘坐前往马甲的车，到马甲镇区下车，在仙公山山下停车场有车上山。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;2、公交&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;在市区的中心客运站可以乘坐11路车到马甲，以同样的方式上山。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;&lt;strong&gt;小贴士：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px;margin-bottom: 10px;padding: 0px;text-indent: 2em&quot;&gt;仙公山缆车往返45元，上行35元，下行20元，每日运营时间大约是8:00-16:00。若坐缆车上山，则无需购买景区大门票。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 1);

