--
-- 表的结构 `post`
--

CREATE TABLE `post` (
  `id` int(11) NOT NULL,
  `title` varchar(255) DEFAULT NULL,
  `descs` varchar(255) NOT NULL COMMENT '简介',
  `content` text,
  `time` date DEFAULT NULL,
  `cate_id` int(11) DEFAULT NULL,
  `user_id` int(11) DEFAULT NULL,
  `type` tinyint(1) DEFAULT '1' COMMENT '1:普通,2:置顶,3:热门,4:推荐',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '文章状态1发布，0',
  `pic` varchar(255) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `post`
--

INSERT INTO `post` (`id`, `title`, `descs`, `content`, `time`, `cate_id`, `user_id`, `type`, `status`, `pic`) VALUES
(2, '福州市2017年第一季度旅游投诉情况简述', '福州第一季度共受理旅游投诉21件，其中12315旅游投诉平台接到旅游投诉10件，12301旅游投诉平台受理旅游投诉7件，12345政府公共服务系统平台投诉4件。其中，涉及旅行社的有20件，景区的有1件。目前投诉件已办结16件，在办4件，转办1件。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　福州第一季度共受理旅游投诉21件，其中12315旅游投诉平台接到旅游投诉10件，12301旅游投诉平台受理旅游投诉7件，12345政府公共服务系统平台投诉4件。其中，涉及旅行社的有20件，景区的有1件。目前投诉件已办结16件，在办4件，转办1件。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　投诉主要反映：因购物及领队服务态度要求道歉，因旅游产品价格变动要求退还差价，因个人原因取消行程发生退团费用纠纷，因擅自缩短游览时间要求退还未游玩费用，因住宿不达标要求赔偿及因景区多收门票要求退还差价等问题。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　市旅游质监所严格落实投诉受理平台监测工作，保持联络畅通，第一时间采取应对措施，一口受理，及时解决旅游中的矛盾和问题。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 1, 1, './Public/Uploads/2017-05-09/59114e3d8eaf8.jpg'),
(4, '马尾区船政主景区招募志愿者', '即日起，马尾区船政主景区中国船政文化博物馆、马江海战纪念馆等志愿者招募活动开始报名，报名时间截止为4月15日；此次招募活动希望热心社会主义公益事业，普通话标准，语言表达能力强，身心健康，具有亲和力，年龄在18-65岁之间的民众都积极报名参加，特别优先考虑对本地历史文化了解的人。本次招募是福州多个博物馆联合举办，招募后，博物馆将对志愿者进行免费培训后上岗，志愿者将从事展厅讲解工作及公共教育服务。\r\n\r\n', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;即日起，马尾区船政主景区中国船政文化博物馆、马江海战纪念馆等志愿者招募活动开始报名，报名时间截止为4月15日；此次招募活动希望热心社会主义公益事业，普通话标准，语言表达能力强，身心健康，具有亲和力，年龄在18-65岁之间的民众都积极报名参加，特别优先考虑对本地历史文化了解的人。本次招募是福州多个博物馆联合举办，招募后，博物馆将对志愿者进行免费培训后上岗，志愿者将从事展厅讲解工作及公共教育服务。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;有意者可通过以下两种方式报名：&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;一、电话报名：马江海战纪念馆：0591-83682339；船政文化博物馆：0591-83983253。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;二、现场报名地址：马江海战纪念馆——马尾昭忠路1号；中国船政文化博物馆——马尾昭忠路7号。现场报名需携带身份证、学生证、退休证等相关有效证件。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;中国船政文化博物馆、马江海战纪念馆是船政文化景区的重要组成部分。中国船政文化博物馆是中国首个以船政为主题的博物馆，该馆依山而建，为五层建筑，分前言、船政概览、科教夙兴、产业先驱、海军根基五个部分通过大量珍贵文物、图片和模型展示船政的内涵。马江海战纪念馆又名昭忠祠，是为了纪念1884年爆发的中法马江海战中阵亡的736位福建水师官兵，清光绪皇帝所赐旨建祠致祭，赞扬福建水师官兵临危不惧、视死如归的精神。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;船政文化博物馆、马江海战纪念馆通过向社会招募志愿者，能够充分利用馆内的文物资源，让更多的民众特别是青少年朋友通过博物馆了解船政历史，挖掘船政文化内涵，更好的弘扬船政文化精神。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-04-11', 63, 1, 3, 1, './Public/Uploads/2017-04-10/58eb415158cc5.jpg'),
(5, '去马祖的船票降价了', '根据福州市人民政府工作常务会议精神，从2017年4月1日起，马尾--马祖福澳、黄岐--马祖白沙往返船票将调整。马尾--马祖福澳，船票价格由原来单程', '&lt;p&gt;&lt;span style=&quot;color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 32px; background-color: rgb(255, 255, 255);&quot;&gt;根据福州市人民政府工作常务会议精神，从2017年4月1日起，马尾--马祖福澳、黄岐--马祖白沙往返船票将调整。马尾--马祖福澳，船票价格由原来单程人民币300元，往返人民币560元调整为单程人民币270元，往返人民币500元。黄岐--马祖白沙，船票价格由原来单程人民币150元，往返人民币300元调整为单程人民币130元，往返人民币260元。&lt;/span&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 1, 1, './Public/Uploads/2017-05-09/59114e0dd27ef.jpg'),
(6, '清明时节陈文龙尚书庙古礼祭民族英雄', '清明小长假期间，一场古色古香的的祭祀仪式在台江区陈文龙纪念馆循古礼进行。在庄严的古乐声中，现场参与活动的福州百六峰诗社诗人及福建汉服天下协会的同袍们按传统三献祭祀礼仪，齐声朗诵陈文龙爱国诗篇，以诗明志，并依次献花献果献爵，表达了对民族英雄陈文龙的崇高敬意和深深缅怀。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;陈文龙清明_副本&quot; src=&quot;/Public/upload/image/201704/1491812814118848.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　清明小长假期间，一场古色古香的的祭祀仪式在台江区陈文龙纪念馆循古礼进行。在庄严的古乐声中，现场参与活动的福州百六峰诗社诗人及福建汉服天下协会的同袍们按传统三献祭祀礼仪，齐声朗诵陈文龙爱国诗篇，以诗明志，并依次献花献果献爵，表达了对民族英雄陈文龙的崇高敬意和深深缅怀。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　万寿尚书庙管委会主任卢鑫称，民俗信仰中被称为水部尚书的陈文龙，在历史上，是一位忠勇爱国的民族英雄，是中华民族抵抗外侮精神的重要象征。清明节祭祀他，就是为了传承爱国主义精神和优秀的传统文化。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　祭祀活动结束后，福建汉服青年在陈文龙纪念馆前的河道旁雅集，以传递酒杯的方式再现古老的曲水流觞之风雅，身着汉服的他们共同徜徉在美丽的上下杭中，感受这片历史文化街区的独特魅力。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-04-11', 63, 1, 1, 1, './Public/Uploads/2017-04-10/58eb41cfe05db.jpg'),
(7, '第七届（中国·福安）畲乡桃花节在福安隆重举办', '3月31日，第七届（中国·福安）畲乡桃花节在美丽的福安市穆云畲族乡虎头村隆重举办。开幕式上，舞狮助庆、畲歌对唱、汉服展示、畲婚演绎等节目，精彩不断、好戏连连，以花为媒，以节会友，浓郁的畲俗特色风情表演，为现场观众呈现出一场极具民族特色的旅游节庆盛会。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;00000&quot; src=&quot;http://www.fjta.gov.cn/fileCenter/res_base/rjcms/application/2017/04/01/58dfb5ce380267b1708fe2d3.png&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;11111&quot; src=&quot;http://www.fjta.gov.cn/fileCenter/res_base/rjcms/application/2017/04/01/58dfb5ce380267b1708fe2d7.png&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;22222&quot; src=&quot;http://www.fjta.gov.cn/fileCenter/res_base/rjcms/application/2017/04/01/58dfb5cf380267b1708fe2db.png&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;3月31日，第七届（中国·福安）畲乡桃花节在美丽的福安市穆云畲族乡虎头村隆重举办。开幕式上，舞狮助庆、畲歌对唱、汉服展示、畲婚演绎等节目，精彩不断、好戏连连，以花为媒，以节会友，浓郁的畲俗特色风情表演，为现场观众呈现出一场极具民族特色的旅游节庆盛会。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;本届畲乡桃花节以“山水福安，多彩畲乡”为主题，将原有万亩桃花核心区的“乡村旅游”升级为“全域旅游”。即以虎头村为核心，联片带动周边溪塔葡萄沟、世界地质公园白云山、开闽第一进士村廉村等景点，共同打造“白云山十里画廊乡村旅游景观带”。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;虎头村是福安市穆云乡的一个纯畲族村，临近白云山世界地质公园，以盛产穆阳水蜜桃而闻名。近年来，福安市依托虎头畲乡桃源这一得天独厚的旅游景观优势，举办桃花节，旨在“以节造势，办节兴旅”，借助畲乡桃花节的示范效果，推动畲乡乡村旅游产业发展，辐射带动特色乡村休闲旅游产业整体发展，进一步打响福安旅游品牌。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;3月份以来，虎头村已接待游客7万人次。随着4月份桃花盛开，虎头、溪塔、白云山将迎来旅游旺季，预计4月份游客量可达40万人次。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-04-11', 63, 1, 3, 1, './Public/Uploads/2017-04-10/58eb421bb972b.png'),
(8, '永泰云顶天空风筝节将于4月8日拉开序幕', '　　草长莺飞四月天，春暖花开，又是放风筝的好时节。永泰云顶天空风筝节即将于4月8日在永泰云顶景区拉开序幕。\r\n\r\n', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;永泰云顶天空风筝节将于4月8日拉开序幕&quot; src=&quot;/Public/upload/image/201704/1491813876853171.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　草长莺飞四月天，春暖花开，又是放风筝的好时节。永泰云顶天空风筝节即将于4月8日在永泰云顶景区拉开序幕。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　本次风筝节，永泰云顶景区经过精心筹备和策划，设置了DIY画风筝、“筝”奇斗艳急速风筝PK赛、特色风筝秀等丰富多彩的活动内容。活动现场还设置了各种奖项，游客在制作与放飞风筝的过程中，可以追寻儿时意义非凡的乐趣，同时还可以参与现场比赛，获得各种奖品。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　想参加的游客，可以详询各大旅行社及各大旅游团购网站，或淘宝搜索“云顶官方”进入易达（福建）旅游集团旗舰店。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-04-11', 63, 1, 4, 1, './Public/Uploads/2017-04-11/58ec89d355b56.jpg'),
(9, '华安即将开工建设土楼旅游公路', '近日，华安县上坪土楼群至大地土楼群公路工程项目初步设计方案完成报批，预计将于今年8月份全线动工建设。本项目路线总里程29.728公里，项目建设标准为三级路标准，设计速度30km/h，路基宽7.5米，路面宽6.5米，双向双车道，水泥混凝土路面。估算总投资金额46669万元，计划2018年完工。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 0em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;《升平楼》组照 王哲勇_副本&quot; src=&quot;/Public/upload/image/201704/1491813929343139.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;br/&gt;&lt;span style=&quot;text-indent: 2em;&quot;&gt;图为华安县上坪土楼群景区&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;近日，华安县上坪土楼群至大地土楼群公路工程项目初步设计方案完成报批，预计将于今年8月份全线动工建设。本项目路线总里程29.728公里，项目建设标准为三级路标准，设计速度30km/h，路基宽7.5米，路面宽6.5米，双向双车道，水泥混凝土路面。估算总投资金额46669万元，计划2018年完工。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;据悉，上坪土楼群至大地土楼群公路工程的建设不仅提高了华安两土楼景区之间的公路等级，使之由原来的等外路提升为三级公路，而且将进一步优化华安旅游道路布局，对发挥华安旅游资源优势，实现华安两土楼景区的客源互送，带动华安县旅游项目建设和整体开发具有十分重要的意义。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-04-11', 63, 1, 4, 1, './Public/Uploads/2017-04-11/58ec89ac89fee.jpg'),
(10, '长乐漳港温泉入选福州温泉资源开发重点项目', '近日，福州市出台温泉资源综合开发利用工作方案，根据该方案，下一步福州市将对9个在建温泉项目和7个温泉旅游招商项目进行重点开发利用，其中长乐市漳港温泉旅游度假区作为温泉旅游招商项目入围其中。漳港温泉旅游度假区，项目拟投资2亿元，规划建设集温泉体验、文化感受、康体养生、商务会议、休闲度假为一体的高尚度假区。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　近日，福州市出台温泉资源综合开发利用工作方案，根据该方案，下一步福州市将对9个在建温泉项目和7个温泉旅游招商项目进行重点开发利用，其中长乐市漳港温泉旅游度假区作为温泉旅游招商项目入围其中。漳港温泉旅游度假区，项目拟投资2亿元，规划建设集温泉体验、文化感受、康体养生、商务会议、休闲度假为一体的高尚度假区。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　近期，长乐漳港龙峰已打出第二口温泉井，深度为800米，目前仍处于水质监测阶段。与此前发现的第一口温泉井相比，水温更高，达42.5度，并且日出水量达到2000吨。早在2009年，有村民在打井时，就发现了地下温泉。2012年，长乐打出了第一口温泉井。第一口温泉井深度501.3米，井口出水水温为37.9度，日出水量1700吨，经检测，水质优良，达到“医疗矿泉命名”标准。第二口温泉井距离第一口仅200多米，出水量和水温均高于第一口井，虽水质仍在检测阶段，但初步判断，水质将比第一口更好。这里的温泉有医疗价值，水中富含二十几种对人体有益的化学元素及化合物，对人体健康有很大的帮助，尤其是有益于人的皮肤，属优质温泉，加上交通优势突出，具有很好的市场前景和竞争力。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　经专家建议，长乐拟在两口温泉井周边做进一步资源勘探，扩大温泉范围，收集资源，编制长乐温泉资源勘查和保护规划，以保证当地温泉资源的可持续开发利用。目前，长乐与福建海峡源脉温泉股份有限公司正洽谈海水温泉的合作事宜。下一步，长乐将加快温泉资源勘探，计划再打一口温泉井，并对温泉资源进行规划保护再利用。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 2, 1, './Public/Uploads/2017-05-09/59114e1c5ece5.jpg'),
(11, '马尾区完善交通体系 助力旅游全面升级', '近日，马尾琅岐再次传来好消息，琅岐岛首个公交枢纽站正式启动建设。该项目的建设无疑对马尾区初步形成四通八达的旅游交通体系，快速发展本区旅游业提供了坚实的保障。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;近日，马尾琅岐再次传来好消息，琅岐岛首个公交枢纽站正式启动建设。该项目的建设无疑对马尾区初步形成四通八达的旅游交通体系，快速发展本区旅游业提供了坚实的保障。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;琅岐公交枢纽站总建筑面积17501.92平方米，总投资10392.10万元。公交枢纽站建成后将服务区域公交，拟停6-8条公交线路，3辆长途大巴，并专门建设有出租车接驳站。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;该项目将于2019年初投入使用，建成后将增加出岛公交线路，和长途大巴、出租车的接驳站，为赴岛游客、岛内群众的出行提供更加便捷的交通条件。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 1, 1, './Public/Uploads/2017-05-09/59114e32272ed.jpg'),
(12, '“醉美泰宁”摄影赛落下帷幕 大量美图新鲜出炉', '3月29日，“醉美泰宁”风光风情新闻摄影竞赛正式落下帷幕，86幅（组）美图脱颖而出。这86幅（组）获奖、入选作品，注重内容创新、视角创新、拍摄手法创新，全新展现了“醉美泰宁”，摄影家们用镜头展示宣传了泰宁的秀美风光、生态环境、人文风情、大事盛事。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;一等奖《灵秀汇聚》李江川13305016693_副本&quot; src=&quot;/Public/upload/image/201704/1491875385436137.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;br/&gt;一等奖《灵秀汇聚》 李江川&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;二等奖《丹霞洞穴萦梵音》刘贤健13358508901_副本&quot; src=&quot;/Public/upload/image/201704/1491875385389854.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;br/&gt;二等奖《丹霞洞穴萦梵音》 刘贤健&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;蒋永强，长龙大战，13950996676_副本&quot; src=&quot;/Public/upload/image/201704/1491875387119445.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;br/&gt;入选奖《长龙大战》 蒋永强&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　3月29日，“醉美泰宁”风光风情新闻摄影竞赛正式落下帷幕，86幅（组）美图脱颖而出。这86幅（组）获奖、入选作品，注重内容创新、视角创新、拍摄手法创新，全新展现了“醉美泰宁”，摄影家们用镜头展示宣传了泰宁的秀美风光、生态环境、人文风情、大事盛事。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　“醉美泰宁”风光风情新闻摄影竞赛自2016年初开赛以来，受到了各地摄影家的关注，截止评选前共收到2000多幅（组）参赛作品。本次评选会在泰宁举行，主办方邀请资深专家组成评委组，对参赛作品进行无记名投票评选。经过10多轮的认真反复筛选，选出了86幅（组）入选作品，在入选作品中，评委经过投票、讨论、推敲、甄别，最终评选出6幅（组）等级奖作品。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　据悉，本次“醉美泰宁”摄影赛最终评选结果即日起将在聚焦三明、泰宁旅游微信公众号进行公布和公示，接受社会监督。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-04-11', 63, 1, 3, 1, './Public/Uploads/2017-04-11/58ec363cafa3e.jpg'),
(13, '国际船舶在福州可“上户口”“海上福州”建设再提速', '近日，交通运输部海事局印发通知，正式将“福建福州”设立为中国（福建）自由贸易试验区国际船舶登记船籍港，这标志着福建自贸试验区福州片区将正式开展国际船舶登记业务。同时，还确定了“中国福州”作为中资“方便旗”船回国登记船籍港。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;近日，交通运输部海事局印发通知，正式将“福建福州”设立为中国（福建）自由贸易试验区国际船舶登记船籍港，这标志着福建自贸试验区福州片区将正式开展国际船舶登记业务。同时，还确定了“中国福州”作为中资“方便旗”船回国登记船籍港。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　船籍就相当于船舶的“户口”,船籍港则相当于船舶的“户口所在地”。船舶登记是指船舶登记机关按照国家法律、法规的规定，对船舶所有权、船舶国籍、船舶抵押权、光船租赁、船舶烟囱标志和公司旗进行登记的行为，一艘船舶只有在进行注册登记后，才能取得船舶国籍和悬挂国旗，拥有航行权。国际船舶登记船籍港的设立，将有效带动福州船舶融资、航运保险、船舶检修等航运服务产业链的发展，吸引更多高端航运要素聚集福州，提升国际海上运输保障能力，促进航运经济的不断发展。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　福州是一座伴海而生，因海而兴，拓海而荣的港口城市，是古代海上丝绸之路的重要发祥地和重要门户。上世纪90年代初，时任福州市委书记习近平基于对世界经济发展格局和趋势的深刻洞察，提出了建设“海上福州”发展战略，他指出，福州的优势在于江海，福州的出路在于江海，福州的希望在于江海，福州的发展也在于江海。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　2016年11月，福州市委、市政府印发《对接国家战略建设海上福州工作方案》，提出传承上世纪90年代福州在全国沿海城市率先提出的“海上福州”发展战略，积极响应“一带一路”倡议和对接建设海洋强国的国家战略，加快建设海洋强市。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 2, 1, './Public/Uploads/2017-05-09/59114dfcd63a7.jpg'),
(14, '福州市鼓楼区与甘肃岷县签署《协作框架协议》 共促旅游发展', '3月23日，福州市鼓楼区与甘肃省定西市岷县签署《鼓楼区与岷县协作框架协议》，双方就产业开发、劳务输转、人才支援等方面达成共识。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;3月23日，福州市鼓楼区与甘肃省定西市岷县签署《鼓楼区与岷县协作框架协议》，双方就产业开发、劳务输转、人才支援等方面达成共识。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　《协议》中约定，双方搭建旅游合作平台。依托鼓楼区闽都文化、温泉养生、休闲度假等旅游品牌，由鼓楼区提供平台和载体，协助岷县宣传推介湿地草原、沙金等独特的自然风光，大力发展旅游产业，促进岷县旅游产业开发。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 1, 1, './Public/Uploads/2017-05-09/59114de77e85b.jpg'),
(15, '福建日报记者来罗源县考察采访旅游产业发展情况', '3月16-17日，福建日报要闻部副主任、福州记者站站长等一行来罗采访我县在“退海还山”的大背景下，如何发挥资源优势，大力发展旅游产业，引导群众转产转业走上致富道路。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;3月16-17日，福建日报要闻部副主任、福州记者站站长等一行来罗采访我县在“退海还山”的大背景下，如何发挥资源优势，大力发展旅游产业，引导群众转产转业走上致富道路。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;当天，福建日报一行记者先后实地采访了福州罗源湾海洋世界旅游区（国家4A级旅游景区）、吕洞畲族风情休闲度假园项目、硒浦山生态休闲农场、福湖畲族民俗文化村、畲山水景区等，感受不同的风景带来视觉盛宴享受的同时，并就旅游景区（点）近年来基础设施建设情况、旅游产业发展情况及未来发展规划对景区进行采访。媒体记者对我县的旅游发展情况给予了高度评价，表示将通过广泛的宣传报道对我县旅游景区（点）进行全面推介，希望为罗源旅游发展作出努力。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;近年来，罗源县着力以项目引领，转变旅游产业发展模式，加快向文化旅游、生态旅游延伸。罗源湾的“退海还山”为罗源县重点工作之一，发展生态旅游已成为大势所趋。同时，罗源县在发展旅游的过程中要充分利用好自身的资源优势，做好景区包装策划宣传，增强景区（点）品质，扩大对外宣传影响力，为促进全县旅游经济发展作出贡献。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 2, 1, './Public/Uploads/2017-05-09/59114dd5e4959.jpg'),
(16, '福州市2015年国庆黄金周假日旅游情况综述', '“十一”黄金周期间全市天气以阴到多云为主，气候宜人、气温适中，较为适宜外出旅游。在市委、市政府的正确领导和各部门、各旅游接待单位的密切配合下，我局早安排、早部署，黄金周期间全市旅游市场保持繁荣有序、拥而不堵的总体态势，旅游人数、旅游收入整体平稳增长，全市未发生重特大旅游安全事故和投诉，实现了“安全、质量、秩序、效益”四统一的假期旅游工作目标。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;“十一”黄金周期间全市天气以阴到多云为主，气候宜人、气温适中，较为适宜外出旅游。在市委、市政府的正确领导和各部门、各旅游接待单位的密切配合下，我局早安排、早部署，黄金周期间全市旅游市场保持繁荣有序、拥而不堵的总体态势，旅游人数、旅游收入整体平稳增长，全市未发生重特大旅游安全事故和投诉，实现了“安全、质量、秩序、效益”四统一的假期旅游工作目标。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　一、总体情况&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　国庆黄金周期间，福州市旅游市场状况良好，七天共接待游客219.63万人次，比增13.20%；实现旅游收入12.36亿元，同比增长13.32%。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　（一）人数情况&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　福州市七天共接待游客219.63万人次，其中过夜游客和一日游游客分别为35.97万人次和183.66万人次，较去年同期分别增长0.50%和16.07%。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　重点监测的四个景区点共接待游客177.50万人次,实现门票收入1.09亿元，同比增长20.91%。其中三坊七巷接待游客84.53万人次，鼓山接待游客37.20万人次，森林公园接待游客23.00万人次，青云山接待游客32.77万人次。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　（二）收入情况&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　黄金周我市累计实现旅游收入12.36亿元，同比增长13.32%。其中，接待过夜游客收入7.33亿元，同比增长3.65%；接待国内一日游游客收入5.04亿元，同比增长31.13%。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　游客人均天花费为562.91元，其中过夜游客人均住宿天数为1.86天，人均天花费1094元；一日游游客人均花费274元。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　二、基本特征&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　（一）青运之旅受青睐，紧抓机遇引游客&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　首届青运会将于本月18日开幕，省旅游局借此契机推出以“清新福建、青运之旅、乐透国庆”为主题的全省联动落地营销，而我市作为主赛区，更是紧抓机遇开展青运会系列宣传推广活动，全力拓展旅游宣传营销渠道，大力宣传我市景区景点、优势旅游产品。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　三坊七巷、福州森林公园、长乐显应宫、三叠井森林公园、白沙朝阳休闲农场等众多景区景点可凭本人身份证和青运会各类证件享受门票折扣；海峡奥体中心气势恢宏的“一场三馆”已然成为福州新的地标性建筑物，给游客留下深刻的印象；青运村、五一广场青运会倒计时牌等青运主题旅游点受到广大市民和游客的青睐和追捧，前往拍照留念的游客络绎不绝；三坊七巷青运店内售卖的含有青运元素的工艺品、文创品，吸引了不少消费者驻足。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　（二）高铁游掀热潮，日输客量显增长&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　福州火车站迎来了合福高铁开通后的第一个国庆黄金周，为吸引游客，我市三坊七巷历史文化街区、永泰天门山景区、青云山景区、贵安欢乐世界休闲旅游度假区等景区可凭动车票购买到优惠门票。在国庆旅游热潮与优惠活动的共同助力下，在“清新福建”的品牌影响下，高铁游再掀热潮。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　1-7日，福州火车站累计发送旅客60.7万人次，比增8.8%，其中动车发送旅客53.2万人次。尤其是10月1日发送旅客10.6万人次，为单日最高；10月5日发送旅客比增达18.2%，增幅创本次黄金周新高。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　（三）温泉古都显风采，温泉景区近饱和&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　在以“温泉古都有福之州”为主题的系列推介会的推动之下，国庆黄金周期间，温泉游初显活力。据了解,各温泉景区3日起客流量大增，多数温泉景区4日客流量仍在攀升，多数温泉景区入住率接近饱和，自驾游均超七成。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　十一期间，旗山森林温泉接待游客2.29万人次，七叠温泉接待游客0.75万人次，黄楮林温泉接待游客0.55万人次，源脉温泉园接待游客0.32万人次，龙泉山庄接待游客0.33万人次，美人谷温泉接待游客0.16万人次；乐峰赤壁温泉5-7日接待游客0.36万人次。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　此外，为展示和宣传温泉文化而建的温泉博物馆在黄金周期间也迎来大批游客。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　（四）乡村旅游显活力，都市休闲游成热点&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　乡村游能够感受农村的风土人情、品味地道农家美食、饱览自然美景，而都市休闲游交通方便、休闲舒适，是家庭游的好去处，在黄金周期间充分彰显活力。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　罗源的畲山水景区、溪坂农家乐、畲家湾民俗度假村等乡村旅游点共接待游客0.44万人次；闽侯县乡村旅游接待4.47万人次；永泰县乡村旅游共接待游客约4.6万人次，其中嵩口古镇接待游客3.36万人次。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　国庆前夕，西湖左海连通段新建的“翠浪桥”对外开放，意味着左海与西湖正式实现通航，形成“大西湖”格局，吸引大批游客前来游览。市区四大重点监测公园共接待游客124.39万人次，其中西湖公园、左海公园、温泉公园和闽江公园分别接待游客49.00万人次、46.50万人次、12.31万人次和16.58万人次。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　随着金鸡山公园木栈道的建成，金鸡山公园成为我市居民又一个休闲好去处，本次黄金周金鸡山公园接待游客人数出现井喷式增长；而连接着魏杰故居与观晋台的空中廊道飞虹桥基本贯通，也吸引了不少游客登高揽胜。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　（四）文化旅游受推崇，文明旅游受关注&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　福州拥有很多充满文化底蕴的旅游景点，在今年“十一”黄金周期间备受游客推崇，也为外地游客入榕旅游注入了丰富内涵。三坊七巷最大的单体古民居建筑刘家大院于国庆黄金周对外试开放，吸引众多游客前来感受它的历史风韵，黄金周期间三坊七巷累计接待游客达到81.53万人次。由于对船政文化旅游的持续宣传推广，旅游配套设施的不断完善，以及“三坊七巷-马尾船政”旅游观光巴士的助力，使船政景区人气火爆，其中船政文化博物馆接待游客21830人次，同比增长10.09%。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　此外，文明旅游观念也逐渐受到关注，国庆期间旅游志愿服务活动全面开展，所提供服务包括信息咨询、讲解服务、游览引导、环境卫生、文明倡导、安全疏导、困难帮扶等，这也成为今年国庆假期的一道新风景。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　（五）自贸区购物游成新风尚&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　随着福建自贸区（福州）片区的推进，马尾两马旅游服务中心（跨境电商O2O体验中心）、跨境电子商务园区、进口商品直销展示中心、保税商品展示区等多个海外商品购物点建成投入使用，十一期间吸引了大量福州市区及周边游客。加之即时推出的“马尾船政文化—自贸区购物一日游”，进一步带旺旅游人气。国庆七天，马尾自贸区购物体验中心人气爆棚，每天接待游客近万人次。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　三、保障措施&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　为确保假日旅游秩序良好，我局联合各相关职能部门对各景区开展拉网式安全隐患排查、检查，通过事先部署、实地走访，重点对景区的游乐设施、宾馆消防设施及食品卫生安全等进行检查，积极做好安全预防工作；通过检查，对存在的问题提出整改意见，并督促进行整改，及时排除安全隐患。同时，也对全市旅游接待工作进行安排部署，节假日期间安排专人24小时值守，确保讯息畅通，认真做好突发事件的应急处置和信息上报工作。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　四、旅游投诉及安全事故情况&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　国庆黄金周期间，我市共接到各类投诉13件，其中投诉旅行社6件，投诉景区6件，投诉星级饭店1件。已协调处理7件，转物价部门处理1件，5件正在协调处理中。无重大旅游投诉事故及安全事故，市场秩序良好。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 2, 1, './Public/Uploads/2017-05-09/59114dcbe28d5.jpg'),
(17, '连城星光生态旅游度假区红枫如织醉游人', '近日，在连城县文亨镇炉坪村星光生态旅游度假区内，近百亩、万余棵日本红枫逐渐转红，树姿优美，叶色鲜艳，吸引众多游客和摄影师前来欣赏美丽“枫”景。星光景区所栽植...', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 26px; font-weight: 500; overflow: hidden; text-align: center; line-height: 60px;&quot;&gt;连城星光生态旅游度假区红枫如织醉游人&lt;/h3&gt;&lt;h4 style=&quot;margin: 0px; padding: 0px; font-size: 14px; font-weight: 500; overflow: hidden; text-align: center; line-height: 34px; color: rgb(153, 153, 153);&quot;&gt;&lt;span style=&quot;display: inline-block; padding: 0px 10px;&quot;&gt;&lt;span class=&quot;icon-position&quot; style=&quot;display: inline-block; background-position: -483px -169px; width: 11px; height: 17px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); margin: -2px 10px 0px 0px; vertical-align: middle;&quot;&gt;&lt;/span&gt;来源：福建省旅游局政府门户网站&lt;/span&gt;&amp;nbsp;&lt;span style=&quot;display: inline-block; padding: 0px 10px;&quot;&gt;&lt;span class=&quot;icon-time&quot; style=&quot;display: inline-block; background-position: -229px -65px; width: 17px; height: 17px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); margin: -2px 10px 0px 0px; vertical-align: middle;&quot;&gt;&lt;/span&gt;发布时间：2017-04-11&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;连城星光生态旅游度假区红枫如织醉游人1&quot; src=&quot;/Public/upload/image/201704/1491894236562448.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;连城星光生态旅游度假区红枫如织醉游人2&quot; src=&quot;/Public/upload/image/201704/1491894236507879.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;连城星光生态旅游度假区红枫如织醉游人3&quot; src=&quot;/Public/upload/image/201704/1491894237410905.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　近日，在连城县文亨镇炉坪村星光生态旅游度假区内，近百亩、万余棵日本红枫逐渐转红，树姿优美，叶色鲜艳，吸引众多游客和摄影师前来欣赏美丽“枫”景。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;星光景区所栽植的红枫原产地为日本，主要品种是血红鸡爪械。日本红枫树遍植景区，树枝亭亭玉立，枫叶或红或黄，与青草、碧树、蓝天互相辉映，呈现出一派层林尽染、色彩斑斓的景象。前来踏青赏春的游人，无一不沉醉在这一片美丽动人的景色中。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;据了解，星光生态旅游度假区是福建省三星级乡村旅游经营单位，占地面积3500亩，已种好可供游客观光的百果园有2500亩，杨梅、枇杷、水蜜桃、葡萄、梨、柑橘等100多种果树可供游客们四季采摘，各种旅游配套设施一应俱全，特色观赏区更是与众不同。漫步其中，让人觉得每个观赏区都场地宽大且茂密繁盛，一派欣欣向荣的景象。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;目前，星光景区还在进一步建设完善，每年通过举办客家民俗、田园活动体验，铁皮石斛认养、水果采摘节等形式多样的活动，提升乡村旅游特色品牌知名度。这也是近年来，连城充分发挥良好的生态资源和丰富的户外休闲运动资源，推动户外休闲、自驾游等深度乡村旅游产品开发，促进乡村旅游产业转型升级的一个缩影。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-04-11', 63, 1, 1, 1, './Public/Uploads/2017-04-11/58ec7fdecc958.jpg'),
(18, '“有福之州 文明同行”福州市2016年度“十佳导游', '“有福之州 文明同行”福州市2016年度“十佳导游（讲解）员”评选活动网络投票开通', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;为深入开展旅游行业精神文明建设活动，提高旅游从业人员思想业务素质，引导导游和讲解员为游客提供文明、优质服务，充分发挥文明旅游先进典型的示范引领作用，有效激发和传播社会正能量，福州市旅游局在全市旅游系统开展“有福之州 文明同行”福州市2016年度“十佳导游（讲解）员”评选活动。目前，评选活动已进入网络投票阶段，广大网友可登陆活动投票专题（&lt;a href=&quot;http://news.fznews.com.cn/zt/2017/ggwmjssjpx/%EF%BC%89%EF%BC%8C&quot; _src=&quot;http://news.fznews.com.cn/zt/2017/ggwmjssjpx/），&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51);&quot;&gt;http://news.fznews.com.cn/zt/2017/ggwmjssjpx/），&lt;/a&gt;为导游（讲解）员投票，评选出心目中的“十佳导游（讲解）员”。投票将持续至5月17日，公众投票结束后，根据投票情况及专家考评结果，确定“十佳导游（讲解）员”名单，相关事迹在网上公示，接受社会监督。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　“有福之州 文明同行”福州市2016年度“十佳导游（讲解）员”评选活动是福州市委文明办举办的第五届“有福之州.文明同行”公共文明建设系列“十佳”评选活动的重要组成部分。3月份，“有福之州 文明同行”福州市2016年度“十佳导游（讲解）员”评选活动正式开展，经过单位推荐和展示，市旅游局专家评审组对推荐人选的初审、初评，最后共有15人进入大众评选阶段，参与网络投票。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 2, 1, './Public/Uploads/2017-05-09/59114b03645c9.jpg'),
(19, '泰宁：大金湖经营权回收三年 景区发展取得新变化', '　2014年4月1日，泰宁县正式接管大金湖景区经营管理，今年泰宁大金湖旅游项目许可经营权合作回收满三周年，景区发展取得喜人变化。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　2014年4月1日，泰宁县正式接管大金湖景区经营管理，今年泰宁大金湖旅游项目许可经营权合作回收满三周年，景区发展取得喜人变化。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　&lt;strong&gt;一是客源总量大幅提升。&lt;/strong&gt;2016年全县旅游接待总量390万人次，实现旅游总收入32.76亿元，比大金湖旅游项目许可经营权回收前（2013年）分别比增63%和50%。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;　　二是旅游品牌再创佳绩。&lt;/strong&gt;先后被列入国家全域化旅游示范区、国家旅游标准化示范县、全国森林旅游示范县、“深呼吸小城”、“中国森林氧吧”，峨嵋峰升格为国家级自然保护区等。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　&lt;strong&gt;三是业态发展持续推进。&lt;/strong&gt;发展了全域写生基地、耕读李家禅修中心、大金湖怡养中心、崇际豆腐坊展示中心、大金湖喷射艇、五朵金花乡村旅游、铁皮石斛体验馆等一批特色旅游休闲业态。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　&lt;strong&gt;四是客商投资兴趣更浓。&lt;/strong&gt;先后有福建夕阳红、厦门大云房车、北京通厦集团、中金集团控股、华侨城等来访客商30余批次，已落地开发旅游项目12个，吸引投资30余亿元，目前有投资意向或在洽谈项目15个。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　&lt;strong&gt;五是上级扶持力度更大。&lt;/strong&gt;三年来，累计争取旅游扶持资金约2亿元，仅2016年就争取上级部门资金（含基金）支持近1.5亿元，为历年最多，有力促进了泰宁旅游基础配套设施建设。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 2, 1, './Public/Uploads/2017-05-09/59114b841e87e.jpg'),
(20, '“遇见福州”App升级上线 让游客出行更显智慧', '近日，由福州市旅游局推出的“遇见福州”手机APP升级上线。APP充分整合了政府、企业、社会各类信息资源，是一款展现福州旅游形象的应用软件。全新的界面，丰富的内容，更加实用的功能，让游客和市民随时都能掌握全方位的旅游资讯，为游客订制专属行程和旅游攻略，使旅游更显智慧。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;11&quot; src=&quot;/Public/upload/image/201705/1494305730542240.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　近日，由福州市旅游局推出的“遇见福州”手机APP升级上线。APP充分整合了政府、企业、社会各类信息资源，是一款展现福州旅游形象的应用软件。全新的界面，丰富的内容，更加实用的功能，让游客和市民随时都能掌握全方位的旅游资讯，为游客订制专属行程和旅游攻略，使旅游更显智慧。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　这一次的升级，采用了简明的界面，融入福州旅游元素，给用户带来更加直观、轻松的感受。功能方面，在原有的基础上，增加了GPS定位地图、景区720度全景导览图、景区趣味地图、景区导览、语音讲解、旅行社信息、自助导游等实用模块，让手机成为游客忠实的向导，使初次入榕的游客也能畅游福州。此外，电子围栏，一键投诉等功能让游客在游玩途中更安全、更省心，把意外事故概率降至最低。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　该APP还可以利用卫星定位技术与后台数据衔接，精确估算出游客当前所在位置，推荐周边景区、住宿等旅游资讯，提供合适的旅游线路及旅游攻略，引导游客玩转福州。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　“遇见福州”旅游品牌经过几年的发展，已经涵盖了旅游资讯网、手机APP、旅游触摸屏、政务微信等多个新媒体平台，成功搭建了福州旅游新媒体宣传营销体系，助推旅游营销升级发展。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 2, 1, './Public/Uploads/2017-05-09/59114bc40a7fe.jpg'),
(21, '连城高地村亲子游红火 游人畅享“森林浴”', '五一以来，连城县莒溪镇高地村生态亲子游红红火火，山地骑行、森林浴、登山、采摘、红豆杉观赏、野菜品尝等各色活动吸引了省内外众多游客前来度假休闲，成为连城乡村旅游的新亮点。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;五一以来，连城县莒溪镇高地村生态亲子游红红火火，山地骑行、森林浴、登山、采摘、红豆杉观赏、野菜品尝等各色活动吸引了省内外众多游客前来度假休闲，成为连城乡村旅游的新亮点。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;高地村海拔1000多米，森林覆盖率89.2%，是个天然氧吧。村头水尾有遮天蔽日、枝繁叶茂的柳杉林，每棵树都有一百多年以上树龄，树干要好几人才能围抱。村庄四周随处可见被称为“植物活化石”的南方红豆杉与枝干遒劲直指苍穹的古杉。这些古树不仅是村里的美丽景观，也是村里的宝贵财富。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;这两年，高地村依托良好的自然环境和丰富的旅游资源，以生态和亲子为主题，大力发展乡村生态旅游。为了打造清新怡人的美丽乡村，高地村积极开展河道清理、路灯安装、古建筑修复、红豆杉古树群落开发、观景路铺设等项目建设。目前，高地村已集中清理河道兴建双边河堤，铺没村道水泥路面，统一安装太阳能路灯，新建仿古凉亭，铺设石砌旅游休闲观景小道，修旧如旧的古建筑已对游人开放。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;为了打造生态亲子游产品，高地村成立了“趣撒野”亲子生态互动体验平台，以夏令营、户外拓展培训、亲子农家乐等形式，开展古树纳凉、清溪赏鱼、山地骑行、森林畅游、红豆杉观赏、野菜品尝、打糍粑、采灵芝、挖竹笋、摘佛手瓜等各种丰富多彩的活动，吸引省内外各地不少家长带着孩子前来体验。特别是村里投资100多万元开发红豆杉古树群落，进一步增强了高地村的旅游吸引力，每年夏天，前来这里避暑休闲的游客不计其数。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 2, 1, './Public/Uploads/2017-05-09/59114c16c2904.jpg'),
(22, '曙光救援队为漳平九鹏溪景区安全保驾护航广受好评', '五一小长假期间，漳平九鹏溪4A级景区迎来游客高峰，为保障节假日景区高峰期游客安全，九鹏溪景区联合曙光救援队开展游客安全保障工作，保障了游客安全，受到了游客的一致认可和好评。', '&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　五一小长假期间，漳平九鹏溪4A级景区迎来游客高峰，为保障节假日景区高峰期游客安全，九鹏溪景区联合曙光救援队开展游客安全保障工作，保障了游客安全，受到了游客的一致认可和好评。&lt;/p&gt;&lt;p style=&quot;margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;　　曙光救援队假期三天时间共派出15名救援队员协助景区安全保障工作，救援队员统一着装，精神饱满，在景区停车场，有序指挥车辆进出停车场；在游客旅游码头，协助游客安全上下游船，检查游客是否穿上救生衣，同时登船随行，为游客安全保驾护航，及时消除安全隐患。救援队员尽职尽责，以高度的责任心、严谨的作风、热情的服务，全身心投入到安保工作中，让游客放心游。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2017-05-09', 63, 1, 2, 1, './Public/Uploads/2017-05-09/59114c4f9b84f.jpg');
INSERT INTO `post` (`id`, `title`, `descs`, `content`, `time`, `cate_id`, `user_id`, `type`, `status`, `pic`) VALUES
(23, '福建人注意啦！5月可免费游宁夏！一起去撩神奇的塞上江南~', '2017年5月，福建籍游客凭身份证游宁夏，21家4A级以上景区免费，全年其他时间半价。', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;福建人注意啦~&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;金色五月可免费游宁夏！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; color: rgb(63, 63, 63); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;宁夏4A级以上景区今年对闽再推优惠政策&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; color: rgb(63, 63, 63); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 20px; color: rgb(46, 139, 87); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;5月免费&amp;nbsp; 全年半价&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; line-height: 22.4px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;而且有着“塞上江南”之称的宁夏&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; line-height: 22.4px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;好玩好吃的可不少哦&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; line-height: 22.4px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;快跟着小旅一起去领略大漠风光~&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; text-align: justify; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; color: rgb(63, 63, 63); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;2017年5月，福建籍游客凭身份证游宁夏，&lt;strong style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; color: rgb(46, 139, 87); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;21家4A级以上景区免费&lt;/span&gt;&lt;/strong&gt;，全年其他时间半价。4月24日，在福建与宁夏对口帮扶协作21周年之际，宁夏旅游发展委员会在福州举行神奇宁夏旅游推介会，会上发布了针对福建籍游客推出的“&lt;strong style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; color: rgb(46, 139, 87); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;八闽亲人宁夏游&lt;/span&gt;&lt;/strong&gt;”和“&lt;strong style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; color: rgb(46, 139, 87); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;金色五月免费游宁夏&lt;/span&gt;&lt;/strong&gt;”优惠政策，诚邀福建省游客到宁夏旅游观光，进一步加深两省区人民的情感。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; text-align: justify; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; color: rgb(63, 63, 63); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20170424/1493024969745747.png&quot; title=&quot;1493024969745747.png&quot; alt=&quot;1.png&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; color: rgb(136, 136, 136); font-size: 14px; line-height: 24.5px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;△&lt;/span&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; color: rgb(136, 136, 136); font-size: 14px; line-height: 24.5px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;沙坡头&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; text-align: justify; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; color: rgb(63, 63, 63); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;宁夏历史悠久、山川秀丽、文化积淀深厚，自然生态和历史人文两种旅游资源得天独厚。雄浑的大漠风光、古老的黄河文化、神秘的西夏古韵、浓郁的回乡风情、悠远的丝绸之路、美丽的塞上江南，以及伊斯兰文化与中原文化的有机结合、多民族和谐共处的人文环境等共同构成了宁夏丰富多彩而又富有鲜明特色的旅游资源。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20170424/1493024977280416.png&quot; title=&quot;1493024977280416.png&quot; alt=&quot;2.png&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; color: rgb(63, 63, 63); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20170424/1493024983689885.png&quot; title=&quot;1493024983689885.png&quot; alt=&quot;3.png&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; color: rgb(136, 136, 136); font-size: 14px; line-height: 24.5px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; line-height: 25.6px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; line-height: 24.5px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;△&lt;/span&gt;镇北堡西部影城（《大话西游》取景地）&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; text-align: justify; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; color: rgb(63, 63, 63); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;近年来，在宁夏回族自治区党委、政府的大力支持下，宁夏旅游不断加大国内外宣传营销力度，完善基础设施建设，提升旅游服务质量，“塞上江南、神奇宁夏”品牌的影响力和知名度快速提升，旅游业已经成为了宁夏的支柱产业。2016年9月10日，第二届全国全域旅游推进会在宁夏中卫市举行，会议确定宁夏成为我国继海南之后第二个省级全域旅游示范区创建单位，创建范围包括宁夏5个地级市，奠定了“南有海南，北有宁夏”的中国全域旅游发展新格局。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20170424/1493024990290213.png&quot; title=&quot;1493024990290213.png&quot; alt=&quot;4.png&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; color: rgb(63, 63, 63); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; text-align: center; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; line-height: 1.75em; color: rgb(136, 136, 136); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;△沙湖&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; text-align: justify; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-size: 14px; color: rgb(63, 63, 63); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;旅游作为闽宁两省区协作的重要内容和纽带，出现了前所未有的可喜局面，清新福建、神奇宁夏的旅游资源差异化优势互补，使两省区旅游协作发展的前景更加广阔。2016年，宁夏首次推出“八闽亲人游宁夏全年半价暨金色五月免费游宁夏”的优惠政策，吸引了众多福建游客到宁夏。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 1.75em; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, Arial, sans-serif; text-indent: 2em; white-space: normal; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); line-height: 25.6px; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;img src=&quot;http://www.fjta.com/static/uploads/content_img/20170424/1493024997372214.png&quot; title=&quot;1493024997372214.png&quot; alt=&quot;5.png&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;marg