--
-- 表的结构 `route`
--

CREATE TABLE `route` (
  `id` int(11) NOT NULL COMMENT '线路id',
  `name` varchar(100) NOT NULL COMMENT '线路名称',
  `cate_id` int(11) NOT NULL COMMENT '类型',
  `descs` varchar(255) NOT NULL COMMENT '线路介绍',
  `content` text NOT NULL COMMENT '线路详情',
  `pic` varchar(255) DEFAULT NULL COMMENT '线路图片',
  `price` int(11) NOT NULL COMMENT '线路价格',
  `days` varchar(100) NOT NULL COMMENT '线路天数',
  `type` varchar(100) NOT NULL COMMENT '线路类型',
  `ishot` tinyint(1) DEFAULT NULL COMMENT '0:不是热门;1;热门',
  `spots` varchar(255) DEFAULT NULL COMMENT '途经景点',
  `code` varchar(255) DEFAULT NULL COMMENT '产品编码'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='线路表';

--
-- 转存表中的数据 `route`
--

INSERT INTO `route` (`id`, `name`, `cate_id`, `descs`, `content`, `pic`, `price`, `days`, `type`, `ishot`, `spots`, `code`) VALUES
(1, '福州到永定土楼，上杭古田会址，连城冠', 55, '福建土楼是东方文明的一颗明珠，它以历史悠久、种类繁多、规模宏大、结构奇巧、功能齐全、内涵丰富著称，具有极高的历史、艺术和科学价值，被誉为“东方古城堡”、', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size:16px;color:#77cc33&quot;&gt;福建土楼是东方文明的一颗明珠，它以历史悠久、种类繁多、规模宏大、结构奇巧、功能齐全、内涵丰富著称，具有极高的历史、艺术和科学价值，被誉为“东方古城堡”、“世界建筑奇葩”，“世界上独一无二的、神话般的山区建筑模式”。福建土楼2008年7月6日在加拿大魁北克城举行的第32届世界遗产大会上，被正式列入《世界遗产名录》。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;早6：00在福州火车北站一楼王威服务台集中，导游举“365假期“旗子接团，乘动车D6401 福州至龙岩（06:58/09:53）到龙岩，后乘车赴永定土楼（约2小时）。午餐后，游&amp;quot;世界文化遗产地&amp;quot;--永定客家土楼民俗文化村（2小时）：参观&amp;quot;最富丽堂皇的圆形土楼王子&amp;quot;--振成楼；&amp;quot;宫殿府第式的方形土楼&amp;quot;--福裕楼；&amp;quot;最小的袖珍圆土楼&amp;quot;--如升楼；&amp;quot;布达拉宫式的方形土楼&amp;quot;--奎聚楼。最后参观客家糯米酒造酒坊--玉成楼 （0.5小时）：您可以亲眼目睹客家糯米酒酿酒工艺的制作全过程，亲口品偿甜美的客家糯米酒。后乘车赴龙岩市区，晚餐后入住酒店。当天行程结束。（含中餐） 住：龙岩&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;早餐后，龙岩乘车赴央视剧《红色摇篮》主景拍摄地--上杭古田镇（高速约40分钟）。参观瞻仰古田会议会址（0.5小时）：1929年12月，中国工农红军第四军第九次代表大会在古田召开，会议通过了毛泽东亲自起草的《古田会议决议》确立了党对军队的绝对领导，是我党我军建党建军史上的重要里程碑，为中国革命成功奠定可靠的基础，被誉为中国红色&amp;quot;麦加&amp;quot;朝圣地。后继续前往连城县城（高速约1小时）。游览&amp;quot;国家重点风景名胜区&amp;quot;、&amp;quot;国家4A级旅游区&amp;quot;、有&amp;quot;北夷南豸、丹霞双绝&amp;quot;之称的冠豸山（3小时）：先乘船游览碧水丹山石门湖景区：远眺马鞍寨、映山亭、老君岩、翠岛微澜、疯僧戴帽、莲花峰、猴狲撞钟、生命之门、大象戏水。后步行游览冠豸山景区：香兰亭、三叠潭、桄榔谷、必达亭、揽月峰、鲤鱼背、寿星峦、长寿亭、生命之根、一线天、东山草堂：纪晓岚题匾《追步东山》、林则徐题匾《江左风流》、凝碧山房、上游第一观、迎客松。后乘车赴龙岩火车站，乘动车D6445龙岩至福州（17:46/20:42）或D6447(17:55/21:11)返回福州，结束愉快旅程！(含早中餐)&lt;/span&gt;&lt;/p&gt;', './Public/Uploads/2017-04-19/58f74897b1858.jpg', 3472, '两日', '自由行', 0, '2,11,13,14', 'route333333'),
(2, '福州到莆田湄洲岛', 55, '福州到天门山漂流门票多少钱_天门山漂流门票团购_福州到天门山漂流', '&lt;p&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;客人自行抵达福州早上7：50在福州火车站（北站南广场黄金大酒店对面）大钟底下集中.福州早上8：30在福州五一广场福建大剧院大门口处集中；导游接客人乘旅游专用车前往游览：有福建园林明珠之誉、福建省历史最悠久的古典园林——&lt;/span&gt;&lt;span style=&quot;color:#ee4488;font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;西湖&lt;/span&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;（游览约60分钟），游览林则徐纪念馆（游览约60分钟）福州市林则徐纪念馆(又称“林则徐祠堂”），参观独具福州地域特色、堪称闽都文化的聚合地、中国十大历史文化街区之首——&lt;/span&gt;&lt;span style=&quot;color:#ee4488;font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;三坊七巷&lt;/span&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;(只走南后街街景+林则徐故居和林觉民故居.不走其他景点.游览时间约90分钟)，中餐团队餐.中餐后乘车赴马尾（车程约50分钟），参观“船政文化主题公园”——&lt;/span&gt;&lt;span style=&quot;color:#ee4488;font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;罗星塔&lt;/span&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;公园（游览约50分钟）参观誉为中国塔--罗星塔；然后参观中法马江海战纪念馆（昭忠祠）（游览约90分钟），了解惨烈悲壮的马江海战史实——凭吊烈士墓，感受马江英烈抗御侵略为国慷慨捐躯的爱国精神——参观&lt;/span&gt;&lt;span style=&quot;color:#ee4488;font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;马限山&lt;/span&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;（视时间而定，自由参观）马限山炮台，观览马尾形胜，考证当年海防工事，梅园监狱，英国领事馆，后参观中国近代海军博物馆，适时乘车返回福州；途经鼓山风景区下院（山脚下）预留30分钟自由活动；乘车返回福州火车站（北站），乘D6235（17：44/20：01）或D6237（18：51/21：03）赴厦门，抵达厦门站我社工作人员接团，不含晚餐入住酒店；&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;早餐后导游酒店接团乘车前往始建于唐代的闽南古刹、厦门经济特区佛教首刹---【&lt;/span&gt;&lt;span style=&quot;color:#ee4488;font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;南普陀寺&lt;/span&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;】（游览时间约50分钟以上），最早的佛教高等教育基地—闽南佛学院、全国唯一的佛教学院，之后【外观厦门大学】;午餐后前往轮渡码头，过渡，登上“海上花园”——鼓浪屿，步行平等路，途经“万国建筑博物馆”之称的鼓浪屿民居，参观遍布全世界20多个国家的建筑风格——万国建筑群、哥特式天主教堂、林巧稚大夫纪念馆，毓园.港仔后海滨浴场自由活动(30分钟)，之后游览台湾富绅林尔嘉的私家海滨花园，堪称江南古典园林精品的由藏海园、补山园组成的【&lt;/span&gt;&lt;span style=&quot;color:#ee4488;font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;菽庄花园&lt;/span&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;】（游览时间约30分钟），参观花园内中国唯一的【&lt;/span&gt;&lt;span style=&quot;color:#ee4488;font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;钢琴博物馆&lt;/span&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;】，鼓浪屿商业街（岛上商店不属于旅行社合作购物点，请酌情购买, 所买商品的质量好坏与否、价格合理与否均与旅行社无关）。欣赏美丽海上风光。过渡返回厦门，(晚餐自理)后乘车往厦门站乘厦门/福州D6234（20：19/22：26）或D6228(19:24/21：26)或者厦门北/福州D6230（20：19/22：01）或D6448（21：12/23：00）返回福州，结束愉快行程;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;', './Public/Uploads/2017-05-09/59116e1471b31.jpg', 4560, '四日', '跟团游', 1, '9,13', 'route4666'),
(3, '福州大金湖6日游', 55, '世界地质公园样板景区寨下大峡谷！体验中国最美的、有上清仙境之称的“华东第一漂”——【上清溪】！', '&lt;p class=&quot;imgCenter&quot; style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-align: center; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img alt=&quot;&quot; title=&quot;&quot; align=&quot;top&quot; src=&quot;/Public/upload/image/201705/1493875685803788.jpg&quot; style=&quot;border: 0px; margin: 10px auto; width: 680px; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;途经城市：福州、莆田&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Day 1 星期四 晴 福州&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;又要开始我们的行程了，几天的乘车帮助我们翻过了福建大山，也助长了我的依赖和懒惰。前面总算是没有山了，心中多少有些安慰。早晨吃过饭，整顿好行装，李波从别人自行车上偷了一根麻绳捆住了我们的书包，看来这种损人利己的小小恶作剧也不可缺少，不然我们怎么办呢？&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;随着福州上班的自行车流，我们上路了。我不时地回头看着向我们频频招手的小李波，心中有一种不舍的依恋。旅途的艰辛给朋友之间一种真挚的友谊，在患难中人心是那样容易沟通。而在平常的环境中，交一个朋友却是很难，平淡的生活掩盖了人的天性，相关的利益使人产生矛盾。我之所以这样酷爱旅行，也是希望在艰苦的环境中得到更多的友爱，人类本性中最真挚的友爱。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-05-09/59116da9a63d0.jpg', 1236, '六日', '独立成团', 1, '7,10,18', 'routeeee22'),
(4, '福州出发永定洪坑土楼、民俗文化村自驾游', 55, '洪坑土楼群凝聚着古代汉族劳动人民超绝智慧的古建群，建于不同时代、形态各异、规模不一的客家土楼以及林氏宗祠、寺庙、学堂等沿溪而建，错落有致，布局合理，与青山、绿水、村道、小桥、', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;福州火车站集合乘D6401次06：58动车出发，09:17抵达南靖动车站，导游在南靖火车站接团，客人自由活动半小时左右，乘旅游车前往【&lt;span style=&quot;color:#dd0000&quot;&gt;永定土楼&lt;/span&gt;】11：00【用午餐】安排客人品尝土楼风味餐。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;12：00 参观【&lt;span style=&quot;color:#dd0000&quot;&gt;&lt;a href=&quot;http://longyan.cncn.com/jingdian/kejiatulouminsuwenhuacun/&quot; target=&quot;_blank&quot; style=&quot;text-decoration-line: none; cursor: pointer;&quot;&gt;客家土楼民俗文化村&lt;/a&gt;景区&lt;/span&gt;】（约2小时）：【&lt;span style=&quot;color:#dd0000&quot;&gt;天后宫&lt;/span&gt;】又称妈祖庙--&amp;quot;最富丽堂皇的土楼王子&amp;quot;-【&lt;span style=&quot;color:#dd0000&quot;&gt;振成楼&lt;/span&gt;】、&amp;quot;府第式的方型土楼&amp;quot;-【&lt;span style=&quot;color:#dd0000&quot;&gt;福裕楼&lt;/span&gt;】、&amp;quot;最小的袖珍圆楼&amp;quot;-【&lt;span style=&quot;color:#dd0000&quot;&gt;&lt;a href=&quot;http://longyan.cncn.com/jingdian/rushenglou/&quot; target=&quot;_blank&quot; style=&quot;text-decoration-line: none; cursor: pointer;&quot;&gt;如升楼&lt;/a&gt;&lt;/span&gt;】&amp;quot;布达拉宫式的土楼&amp;quot;-【&lt;span style=&quot;color:#dd0000&quot;&gt;奎聚楼&lt;/span&gt;】等它们沿溪而上，气势恢宏，错落有致，与青山、绿水、翠竹、水车、农田和谐相处，组成一幅幅多姿多彩的绚丽画卷。 14:00-14:30 结束行程停车场集合乘旅游车返回动车站。&lt;/span&gt;D6443（18：01）或D6445（18:19）动车，返程福州温馨家园。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size:18px;color:#333399&quot;&gt;&lt;strong&gt;洪坑土楼简介：&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size:18px;color:#dd0000&quot;&gt;洪坑土楼群凝聚着古代汉族劳动人民超绝智慧的古建群，建于不同时代、形态各异、规模不一的客家土楼以及林氏宗祠、寺庙、学堂等沿溪而建，错落有致，布局合理，与青山、绿水、村道、小桥、田园完美结合、融为一体。 洪坑村土楼为林氏民居，现有建于公元16世纪中叶（明代）至现代的圆形土楼、方形土楼、宫殿式土楼、五凤式土楼、府第式土楼等各种类型的土楼数十座，为汉族传统的生土民居建筑艺术和传统文化提供了特殊的见证。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-05-09/59116d4703b6e.jpg', 2320, '七日以上', '自驾游', 0, '4,5,9', 'route333'),
(15, '安水世界自由行', 55, '贵安水世界简介古远的玛雅文明今现海西，在福州贵安这片风水宝地揭开神秘面纱，辉煌依旧的玛雅文化在南墨西哥雨林大幕下缓缓展开，贵安水世界横空出世！该项目是由世纪金源集团斥巨资打造的高品', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;早上10:30&lt;a href=&quot;http://fuzhou.cncn.com/&quot; target=&quot;_blank&quot; style=&quot;text-decoration-line: none; cursor: pointer;&quot;&gt;福州&lt;/a&gt;北站集合接车，赴贵安新天地旅游度假区，抵达后游玩【&lt;span style=&quot;color:#ee4488&quot;&gt;贵安花海拓展公园&lt;/span&gt;】，贵安花海拓展公园位于国家AAAA级景区贵安新天地休闲旅游度假区内，是世纪金源集团打造休闲旅游的又一标志性经典户外体育休闲主题公园。项目总占地47万平方米，涵盖花海园林、户外探险、水上竞技运动、萌宠乐园等四大奇乐主题区域。这里不仅有迤逦壮观的花海可以欣赏品鉴，更有近年来新兴的热点旅游项目,其中包括被誉为无敌水上飞车的摩托艇、皮划艇、亚洲精品“人鱼共舞潜水世界”、被称为“岩壁上的芭蕾”极限攀岩、欢乐骑行、极速竞技、网球场等风靡世界、趣味无比系列健康时尚的绿色户外运动，让你领略感官刺激的精髓和极速的真谛！&amp;nbsp;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;中餐后游玩【&lt;span style=&quot;color:#ee4488&quot;&gt;福州贵安水世界&lt;/span&gt;】，水世界充分利用独特的自然山形地貌，运用梦幻唯美的装饰手法以及高新科技娱乐手段，有机地把最先进、最刺激、最好玩的游乐设备，最时尚、最动感、最精彩的主题演艺融入娱乐场景之中，为您带来世界级的缤纷娱乐体验！汇聚亚洲最大的漩涡式滑道项目“飓风喇叭”;国内最长、坡度最陡的水上过山车“蟒蛇滑道”;海西首套六并列竞赛滑道“彩虹竞技”;最长的人工漂流河“亚马逊漂流”;海西最大的人工造浪池“玛雅海啸”;东南最大的主题嬉水区“梦幻水城”;亚洲精品首创、&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;全国独一无二的水上“金字塔”、“魔力指环王”；拉文塔族居住的密林仙境；玛雅舞台互动活动及专业乐队演奏……让您在古典与现代中穿越时空，感受水魔力的劲爽与惊险！行程结束后17:00集合乘车返回福州，返回温馨的家！&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;备注：花海摩托艇、皮筏艇、人鱼共舞、欢乐骑行、极速竞技、网球场等需另外收费&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;background: url(&amp;quot;images/ico_show.png&amp;quot;) 0px -82px no-repeat; position: absolute; margin: 2px 0px 0px -24px; width: 17px; height: 17px;text-decoration:line-through;&quot;&gt;&lt;/span&gt;用餐：&lt;span style=&quot;padding-right: 20px;&quot;&gt;早餐（敬请自理）&lt;/span&gt;&lt;span style=&quot;padding-right: 20px;&quot;&gt;中餐 √&lt;/span&gt;&lt;span style=&quot;padding-right: 20px;&quot;&gt;晚餐（敬请自理）&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;background: url(&amp;quot;images/ico_show.png&amp;quot;) -19px -83px no-repeat; position: absolute; width: 17px; height: 17px; margin: 2px 0px 0px -24px;text-decoration:line-through;&quot;&gt;&lt;/span&gt;住宿：温馨的家&lt;/p&gt;&lt;p&gt;注意：&lt;/p&gt;&lt;p&gt;1、60至69周岁的长者（含60周岁）可购买优惠票；70周岁以上（含70周岁）长者免费入园。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;2、1.2米至1.5米（含1.2米，不含1.5米）儿童可购买优惠票；1.2米以下，不含1.2米儿童可&amp;nbsp;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;享受免费入园游玩。（必要时需现场确认身高）；儿童必需由成人陪同方可入园游玩。&amp;nbsp;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;3、持军官证、警官证（武警）、士兵证等现役军人可购买优惠票。&amp;nbsp;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;4、持军残或民残4-9级伤残证残疾人购买优惠票。&amp;nbsp;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;备注：在享受优惠时请出示相关证明文件和有效证件。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;贵安水世界地址：福州市连江县近郊连江贵安新天地&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;导航：百度地图输入 贵安水世界&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;电话：0591-88001987&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-05-09/59116ce58275d.jpg', 230, '一日', '自由行', 1, '1,2,9,11,12', 'route123'),
(16, '闽江一日游', 55, '在豪华游轮上，品尝淡淡的茶香，欣赏闽江两岸迷人的夜景，渐渐在成福州市民新的休闲娱乐项目。这就是八方海上船运公司推出的“夜游闽江”。', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;上次登船地址:&lt;a href=&quot;http://fuzhou.cncn.com/&quot; target=&quot;_blank&quot; style=&quot;text-decoration-line: none; cursor: pointer;&quot;&gt;福州&lt;/a&gt;市台江码头 （五一路和江滨路交叉口 ）&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;公交路线：可乘：1路 10路 13路 109路 25路 3路 30路 尚干31路 37路 41路 71路 83路K1路到台江步行街下车；&amp;nbsp;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;或者打车到安平小区或者台江元洪城，走路就可以到达，建议打车到安平小区公交车站，只有走过去距离&lt;a href=&quot;http://fuzhou.cncn.com/jingdian/minjiangyeyou/&quot; target=&quot;_blank&quot; style=&quot;text-decoration-line: none; cursor: pointer;&quot;&gt;&lt;/a&gt;&lt;a href=&quot;http://fuzhou.cncn.com/jingdian/minjiang/&quot; target=&quot;_blank&quot; style=&quot;text-decoration-line: none; cursor: pointer;&quot;&gt;闽江&lt;/a&gt;夜游上船地点仅仅需要几分钟路程。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;不懂得直接拨打我们的咨询电话0591-88883072&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;1、闽江夜游线路&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;注意：需提前半个小时到夜游闽江登船处办理登船手续（每晚两个航班19:00,20:30，每班航行时间1个小时）&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;19:00（或20:30）准时从台江旅游码头出发，游览时间1个小时。沿途远观景点：解放大桥、中亭街、苍霞洲、三县游大桥、江滨大道、闽江公园北园之一（闽风园）、尤溪洲大桥、福建广播电视中心大楼、闽江公园北园之一（望龙园）、洪山桥、闽江公园（南园）、金山大桥、金山新区、以船为家捕鱼为生--疍民、上渡、龙潭文化公园（陈靖姑祈雨处大殿、望北台真武庙、万寿头陀寺）、中洲岛、南江滨大道、仓山以“泛船浦天主堂”为代表的近现代西洋建筑群、福州东部新城、鼓山大桥，20：00（或21：30）返回台江旅游码头下艇，结束愉快行程。如遇潮汐，改走马尾路线。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;2、壶江岛一日游线路&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;周末发船（特殊情况除外）8:30—16:00&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;从台江旅游码头出发，乘坐游船，船上欣赏罗星塔等马尾船政文化系列旅游景点，长乐郑和公园雕像、金刚腿，双龟岛，停靠连江馆头壶江岛。岛上用餐、参观妈祖文化中心、陈氏祠堂、壶江民俗馆等。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;坐&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在豪华游轮上，品尝淡淡的茶香，欣赏闽江两岸迷人的夜景，渐渐在成福州市民新的休闲娱乐项目。这就是八方海上船运公司推出的“夜游闽江”。闽江作为福建的&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;母亲河而闻名。很多外地游客到福州都会坐上游轮去欣赏母亲河晚上迷人的夜景。近些年在福州市政府的大力支持下，“夜游闽江”逐渐成为福州的特色文化旅游项&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;目，和三坊七巷，鼓山一起成为福州旅游城市的名片。为了让更多人了解闽江文化，八方海上客运公司也根据客户不同的需求相继推出了&amp;quot;闽江一日游&amp;quot;，&amp;quot;闽水文&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 24px; font-size: 14px; color: rgb(85, 85, 85); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;化之旅&amp;quot;等新的旅游线路。&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-04-21/58f9a5f135a4b.jpg', 78, '一日', '自驾游', 1, '9,10,11,14', 'route20170421142553349831'),
(17, '罗源海洋世界二日游', 55, '畅游最亲子海洋世界：亚洲地区超大规模的海洋世界；与海洋精灵近距离接触，观看唯美的人鱼show，可爱的海豚表演；体验最丰富的主题乐园：魔幻主题尖叫乐园--梦幻魔都、主题乐园—深海奇幻、国内首个大型水上高空竞技表演场—海上搏斗以及上游艇俱乐部体验豪华游艇。', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal;&quot;&gt;如有疑问，欢迎来电咨询；&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal;&quot;&gt;泉州宝中国际旅行社：0595-28283567/28288567 13395958567&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal;&quot;&gt;更多旅游线路尽在泉州宝中国际旅行社&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal;&quot;&gt;官方网站：http://lvyou0595.com/&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal;&quot;&gt;欣欣网站：http://lxs.cncn.com/61577&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal;&quot;&gt;泉州宝中国际旅行社微信公众号：lvyou0595&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal;&quot;&gt;一、行程亮点：&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &amp;quot;microsoft yahei&amp;quot;, arial; font-size: 14px; white-space: normal;&quot;&gt;畅游最亲子海洋世界：亚洲地区超大规模的海洋世界；与海洋精灵近距离接触，观看唯美的人鱼show，可爱的海豚表演；体验最丰富的主题乐园：魔幻主题尖叫乐园--梦幻魔都、主题乐园—深海奇幻、国内首个大型水上高空竞技表演场—海上搏斗以及上游艇俱乐部体验豪华游艇。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-05-04/590af3681ee09.jpg', 455, '两日', '跟团游', 1, '3', 'route20170504172455663375');

