--
-- 表的结构 `spot`
--

CREATE TABLE `spot` (
  `id` int(11) NOT NULL COMMENT '景点id',
  `cate_id` int(11) NOT NULL COMMENT '所属分类',
  `name` varchar(100) NOT NULL COMMENT '景点名称',
  `descs` varchar(255) NOT NULL COMMENT '景点简介',
  `pic` varchar(255) DEFAULT NULL COMMENT '景点图片',
  `content` text NOT NULL COMMENT '景点介绍',
  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '''1'' COMMENT ''1:普通,2:置顶,3:热门,4:推荐''',
  `area` varchar(100) NOT NULL COMMENT '所属地区',
  `star` varchar(100) NOT NULL COMMENT '星级',
  `ticket` varchar(50) NOT NULL COMMENT '门票信息',
  `address` varchar(50) NOT NULL COMMENT '地址信息',
  `telephone` varchar(50) NOT NULL COMMENT '电话',
  `opentime` varchar(50) NOT NULL COMMENT '开放时间',
  `playtime` varchar(50) NOT NULL COMMENT '游玩时间',
  `goodtime` varchar(50) NOT NULL COMMENT '最佳季节',
  `praise` int(100) NOT NULL DEFAULT '0' COMMENT '点赞数'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `spot`
--

INSERT INTO `spot` (`id`, `cate_id`, `name`, `descs`, `pic`, `content`, `type`, `area`, `star`, `ticket`, `address`, `telephone`, `opentime`, `playtime`, `goodtime`, `praise`) VALUES
(1, 40, '三坊七巷', '对老福州人来说，三坊七巷就是他们的家园，也是福州的代表。', './Public/Uploads/2017-04-14/58f069f9e3907.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;三坊七巷景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492150763232939.jpg&quot; title=&quot;1443176410380719.jpg&quot; alt=&quot;1-三坊七巷-1.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;对老福州人来说，三坊七巷就是他们的家园，也是福州的代表。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;三坊七巷起于晋，完善于唐五代，至明清鼎盛，古老的坊巷格局至今基本保留完整，是中国都市仅存的一块“里坊制度活化石”。坊巷内保存有200余座古建筑，其中全国重点文物保护单位有9处，省、市级文保单位和历史保护建筑数量众多，是一座不可多得的“明清建筑博物馆”。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492150765181261.png&quot; title=&quot;1443176425946846.png&quot; alt=&quot;1-三坊七巷-4.png&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;三坊七巷向西三片称“坊”，向东七条称“巷”，自北而南依次为：“三坊”衣锦坊、文儒坊、光禄坊，“七巷”杨桥巷、郎官巷、安民巷、黄巷、塔巷、宫巷、吉庇巷。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;与主干道南后街不同，坊和巷里显得安静缓慢，这里不乏名人故居，严复、冰心、林徽因，还有更往前的达官显贵……一个个名字在眼前滑过，一时间眼前时光交错：一会儿是明清大学士大官僚论道说仕、鸡鸣鼎食，一会儿是追求自由和民主的先行者风云际会、前赴后继。三坊七巷的每座坊、每条巷均因名人或因历史事件而得名，历史上先后有70多位名人生活在这里，历代众多著名的政治家、军事家、文学家、诗人、学者从这里走向辉煌。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492150766219724.jpg&quot; title=&quot;1443176437276107.jpg&quot; alt=&quot;1-三坊七巷-2-福州旅游资讯网.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;走在这个街区内，坊巷纵横，石板铺地；白墙瓦屋，曲线山墙、布局严谨，匠艺奇巧；不少还缀以亭、台、楼、阁、花草、假山，融人文、自然景观于一体。许多民居的门窗漏花采用镂空精雕，榫接而成，以及丰富的图案雕饰，精巧的石刻桂础、台阶、门框、花座、柱杆随处可见，纵横交错的坊巷，浓缩了福建民居的特色，也浓缩了福州的文化内涵。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 4, '鼓楼', '5A', '免费，部分景点需购买门票', '福州市鼓楼区南后街东侧沿线', '0591-87675791', '全天开放，收费景点8:30-17:00开放', '3小时', '四季皆宜 ', 2),
(2, 40, ' 马尾船政博物馆', '马尾船政博物馆，也即中国船政文化博物馆，位于福州市马尾区昭忠路马限山东麓，', './Public/Uploads/2017-04-14/58f06a5c5ecd2.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;马尾船政博物馆景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492150815971393.jpg&quot; title=&quot;1448260164965894.jpg&quot; alt=&quot;3.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;马尾船政博物馆，也即中国船政文化博物馆，位于福州市马尾区昭忠路马限山东麓，为中国第一个以船政为主题的博物馆。博物馆依山而建，共有五层，馆内陈列分为序厅、船政概览厅、船政教育厅、船政工业厅、海军根基厅、船政名人堂。博物馆建筑面积4100平方米，正面造型为两艘乘风破浪的战舰，气势磅礴。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;2006年4月，当时来福建省参访祭祖的中国国民党荣誉主席连战一行来到本馆参观，临走前，连战还为博物馆题词：“东学为体西学用，马江巨舰驭狂涛”。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492150815382519.jpg&quot; title=&quot;1448260171345129.jpg&quot; alt=&quot;1.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;说起福州马尾船政博物馆，就不得不说到清朝洋务运动中发展起来的船政学堂。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;1866年，在鸦片战争中吃了大亏的清朝，吸取了血与火的惨痛教训，终于认识到闭关锁国的害处，掀起了一场轰轰烈烈的“洋务运动”，当时，由担任闽浙总督的左宗棠奏请朝廷批准，在福州马尾，创办了这所在中国近代史上有着深远影响的马尾船政学堂。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;马尾船政，在当年是一所起点很高，视野非常开阔的学堂，在创办过程中，为近代中国培养和造就了一大批优秀人才，尤其是海军人才。以甲午战争为例，北洋水师邓世昌、“定远”舰管带刘步蟾、“镇远”舰管带林泰曾等这几位英勇的北洋水师将领都来自于同一个地方——马尾，都出自于同一所学校——福建船政学堂。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;与此同时，毕业生中还有一大批人成为了中国近代杰出的科技人才，如严复、詹天佑、魏瀚等，马尾为中国的近代科技队伍萌生和形成打下了坚实的基础。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;福州马尾船政博物馆，了解中国船舶发展历史的一扇窗户。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, '马尾', '4A', '免费', '福州市马尾区昭忠路马限山东麓', '0591-83983253', '9:00-12:00，13:30-17:00', '2小时', '四季皆宜 ', 0),
(3, 40, '罗源湾海洋世界', '福州罗源湾海洋世界以“海洋之旅”为主题，位于罗源湾滨海新城东南面，占地172亩，建筑面积20470平方米', './Public/Uploads/2017-04-14/58f06acea9530.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;罗源湾海洋世界景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492150981504451.jpg&quot; title=&quot;1465365302711629.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;福州罗源湾海洋世界以“海洋之旅”为主题，位于罗源湾滨海新城东南面，占地172亩，建筑面积20470平方米，周边配备多达4000余个停车位的大型停车场及海洋世界商业广场等设施，能满足吃、住、行、玩、购、娱等六大旅游需求。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;不同于其他海洋馆相对单一的展示功能，福州罗源湾海洋世界在展示功能设施上是最齐全的。项目的设计方负责人XX介绍，馆内共分为珊瑚礁原生态区、儿童互动区、海洋生物科普展区、梦幻水母宫区、大洋区、热带雨林区、两栖动物区、海豚表演剧场等八大功能区域，这些区域共同构成一个丰富多彩的海洋世界。其中像热带雨林区、两栖动物区是国内乃至亚洲地区许多海洋馆所没有的。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492150982108473.png&quot; title=&quot;1465365650111622.png&quot; alt=&quot;1.png&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;珊瑚礁原生态区——通过“城市围墙”红树林珊瑚礁与城市两者生态链的保护理念，倡导人与海洋和谐共处。在这里，游客可以亲手触摸形态各异的海洋生物，主要展示品种有鲎、海苹果、海星、海龟、神仙鱼等。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;儿童互动区——主要展示的是书型展缸和儿童探险隧道，“寓教于乐”，主要展示有柠檬鲨、护士鲨、海豹等，孩子们可以通过探险隧道与鲨共舞。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492150982687922.jpg&quot; title=&quot;1465365323641271.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;科普展示区——展示多种海洋生物标本，海洋科普放映室可放映海洋生物知识的相关影片，“蛟龙号”模型展示了我国在海洋科技领域上的突破发展。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;水母宫区——游客置身于五彩缤纷的水母宫，可以体验到水母用自己生命的轮回诠释着对海的眷恋，短暂却精彩至及……这里向游客展示了太平洋海刺水母、澳洲斑点水母、海月水母等种类。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492150983232115.jpg&quot; title=&quot;1465365323168097.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;大洋区——以二战沉船为时代背景，配以色彩斑斓极具观赏性的大型海底隧道、充满惊险刺激的人鲨共舞表演及优雅多姿的美人鱼表演，给游客营造了梦幻唯美、惊心动魄的视觉盛宴。展示的鲨鱼种类包括护士鲨、柠檬鲨、白鳍鲨等。而美人鱼展窗长15.5米，高达4.5米，是我省最大的美人鱼展窗，在全国也排在前列。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;热带雨林区——神秘的亚马逊丛林风光带给您无限遐想、大型淡水鱼隧道也将给您带来视觉上的饕餮大餐。该区展示近70种热带淡水观赏鱼包括匙吻鲟、俄罗斯鲟、金龙、巨骨舌鱼等等。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492150983673027.jpg&quot; title=&quot;1465365323592140.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;两栖动物区——展示有各种各样的两栖动物，主要有金刚鹦鹉、绿蜥、树蛙、黄金蟒、长臂猴等近20多种。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;海豚表演剧场——在容纳1500位观众的剧场里，海豚、海狮等海洋精灵们将献上搞趣、精彩的演出。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, '罗源', '4A', '150元，儿童80元', '罗源湾开发区江滨南路1号', '0591-24879800', '8:30-18:00', '半天', '四季皆宜 ', 0),
(4, 40, '永泰天门山', '永泰天门山因山有一巨石屹立形状如门而得名，几条大溪贯穿山谷，水流清澈，两岸怪石嶙峋，风景如画', './Public/Uploads/2017-04-14/58f06b22429bc.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;永泰天门山景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151061246041.jpg&quot; title=&quot;1453536233583722.jpg&quot; alt=&quot;1453536233583722.jpg&quot; width=&quot;860&quot; height=&quot;440&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; width: 860px; height: 440px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;永泰天门山因山有一巨石屹立形状如门而得名，几条大溪贯穿山谷，水流清澈，两岸怪石嶙峋，风景如画，有大大小小许多瀑布。走在天门峡谷山道上，随处可见万年古松，千年古藤，百年香樟，红豆杉等各种珍稀植物；成群的天然猕猴、短尾猴、羚羊、穿山甲随时从你身旁穿越而过，让您突然心跳一惊；欣赏万石瀑布，地下河，天门洞，石走廊等众多名景让您如临大奇境，感受大自然鬼斧神工的力量。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151063945232.jpg&quot; title=&quot;1452225040709258.jpg&quot; alt=&quot;1452225040709258.jpg&quot; width=&quot;860&quot; height=&quot;487&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; width: 860px; height: 487px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;从“天门山”门入口至九天观景台，全是新架设的水泥栈道，曲径通幽，令人心旷神怡。古朴幽奇的鸳鸯林、迎客松、千年藤、百年樟、酸枣王、刺桫椤、红豆杉，成群的天然弥猴、羚羊、穿山甲等珍稀动植物和千年园坪、古代染窑、红军洞、天门寺遗址都有很高的欣赏价值。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151063504179.jpg&quot; title=&quot;1457517419171363.jpg&quot; alt=&quot;勇者无惧.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;天门山巍峨秀丽，景点众多，类形各异，主要景观：天门、天门洞、天门窗、天门桥。地下河，乃自然界天公造物之奇，地下河岩壁上万年石乳形成的天然“观音”、“弥勒”佛显像，栩栩如生，神秘莫测；天门山瀑布深潭众多，葫芦瀑布、人参瀑布、燕潭瀑布、天涯瀑布、万石瀑布、三叠飞泉等姿态万千，最壮观的当数葫芦瀑布，瀑布落差仰角高169米，多级旋转，层层跌落，瀑布的出水形状奇特像葫芦，泉水喷涌似珍珠，又被誉为“葫芦倒珍珠”，实属国内罕见；景区奇峰怪石林立，诸如飞来石、试剑石、帆船石、风动石、佛光岩、骆驼峰等，似人似兽似物，形态逼真，给人以无限遐想。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151064214737.jpg&quot; title=&quot;1452225070174696.jpg&quot; alt=&quot;1452225070174696.jpg&quot; width=&quot;860&quot; height=&quot;559&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block; width: 860px; height: 559px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;古朴幽奇的鸳鸯林、万年古松、千年藤、百年香樟、酸枣王、刺桫椤、红豆杉，成群的天然短尾猴、羚羊、穿山甲等国家一、二级重点保护动物在林间自由嬉戏，显示了天人合一的高度和谐之美；古代水利遗址斗门、天门寺遗址、千年园坪、古代染窑、红军洞等具有极高的欣赏价值和考古价值；在天门湖挥杆垂钓，宁神静志，意守丹田；在林涧小溪戏水与水中鱼虾共舞，石道登山，健身壮体，益寿延年；景区开发的漂流、溪降等项目深受游客的青睐，被誉为“海西第一漂”。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, '永泰', '4A', '35元起', '永泰县葛岭镇溪洋村', '0591-87675791', '8:00-16:00', '1天', '春季 夏季 秋季 ', 0),
(5, 40, '九龙山生态旅游景区', '九龙山生态旅游景区，山清水秀，群山环抱，景区宜人，为国家级AAA级旅游景区，并荣获福建省乡村旅游四星经营单位', './Public/Uploads/2017-04-14/58f06d77c3a56.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;九龙山生态旅游景区景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151628180648.jpg&quot; title=&quot;1452226222942034.jpg&quot; alt=&quot;3、九龙山庄.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;九龙山生态旅游景区，山清水秀，群山环抱，景区宜人，为国家级AAA级旅游景区，并荣获福建省乡村旅游四星经营单位、福建省休闲农业示范点、福建省水乡渔村称号。景区位于长乐市江田镇，距离福州市区仅50多分钟车程，交通便捷。景区靠山面海，背倚南阳省委旧址红色旅游景区，面朝下沙海滨度假村，具有得天独厚的地理优势。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151629230625.jpg&quot; title=&quot;1458287010236387.jpg&quot; alt=&quot;4、别墅山庄.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;生态旅游区占地约2000亩，分为山下、山上两个区域。山下九龙山庄，现有现有住宿、农家乐、土特产专卖部，游泳、荡舟、垂钓、烧烤、骑马射箭、漂流、登山及30亩观赏鱼养殖基地等项目，是集旅游、休闲度假的好地方。这里的农家宴和当地的海鲜也堪称一绝。山上九龙溪漂流区离九龙山庄约2公里，九龙溪穿流景区全程终年流水不断。这里有享有“海峡第一漂”美誉的激情漂流，农家乐、射箭区、100余亩天然跑马场、农业生态园等，是一个集度假休闲、观赏、运动、娱乐为一体的现代海滨旅游胜地。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;景区项目众多，周边旅游景点众多，非常适宜两天一夜的长乐深度游。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151629549379.jpg&quot; title=&quot;1458287022497494.jpg&quot; alt=&quot;8、观音坝.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, '长乐', '3A', '90元', '长乐市江田镇友爱村', '0591-83983253', '9:00-15:00', '1天', '四季皆宜 ', 0),
(6, 40, '陈文龙纪念馆', '陈文龙纪念馆位于台江区下杭路，总占地1200平方米，恢复了“海神”陈文龙尚书庙原有的亲水性特点', './Public/Uploads/2017-04-14/58f06dd8e98fe.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;陈文龙纪念馆景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151749524479.jpg&quot; title=&quot;1452152624422918.jpg&quot; alt=&quot;37.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;陈文龙纪念馆位于台江区下杭路，总占地1200平方米，恢复了“海神”陈文龙尚书庙原有的亲水性特点，保持明清建筑形制和风格；有康熙、嘉庆、道光皇帝的三方御碑和9方总督、巡抚等褒扬陈文龙的牌匾；有乾隆到民国时期的14方石碑刻；有41斤重的乾隆年间铸制的镇庙狮钮铁印等，形成了集民俗、宗教、旅游活动为一体的新景观。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151749581045.jpg&quot; title=&quot;1452152632859591.jpg&quot; alt=&quot;38.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;陈文龙（公元1232 - 1277年）原名子龙，字君贲、德刚，福州兴化（今福建莆田）人，宋咸淳五年（1269）戊辰科状元，宋度宗赐名文龙。因丞相贾似道爱其文，对其极为赏识器重，但陈文龙并不以为然。后由于陈文龙刚直不阿、为官清正廉洁、处处以社稷为重、正直敢言。（1260年）忽心烈被拥立为大汗，此后元军开始攻宋，从（1267年）元军主帅阿术开始进攻襄阳直至（1273年）襄阳失守后，陈文龙上书痛责贾似道用人不当，并请罢黄五石、范文虎、赵潜等人。贾似道大怒，将陈文龙贬官抚州，又指使台臣季可上书弹劾陈文龙。但不久，范文虎投降元军，贾似道率兵13万开抵芜湖，结果兵败鲁港，赵潜却最先逃跑，导致其余守将弃城而逃。贾似道后悔未听陈文龙所言，再次起用陈文龙为左司谏，迁侍御史，再迁为参知政事。 由于朝内议和，陈文龙乞请回乡养老，获准。（1276年），益王称帝福州，陈文龙再次出任参知政事，上任后就平定了漳浦、兴化叛乱。（1277年）元军占领广州后，泉州、福州守将纷纷投降。招降使者两次至兴化劝降文龙，均被其严辞拒绝、焚书斩杀，并倾尽家财募兵，打出“生为宋臣，死为宋鬼”旗帜，坚守兴化城。但终因寡不敌众，叛将开城投降，城破自刎不遂和家人被俘。被俘后即押往杭州，在离开莆田时他开始绝食，途中，他赋诗明志：&amp;quot;一门百指沦胥尽，惟有丹衷天地知。&amp;quot; 最后经杭州拜谒岳飞庙时，&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;在庙内吞香灰自尽，葬于杭州西湖智果寺旁，后人将陈文龙与岳飞、于谦并称为&amp;quot;西湖三忠肃&amp;quot;，誉为福建的“岳飞”。其母则被监禁尼寺，病重无药，旁人无不落泪，其母言道“吾与吾儿同死，又何恨哉？”亦病逝。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;陈文龙的英雄事迹可谓惊天地，泣鬼神。历代史传、诗文、戏曲等都有记述，对其备加褒扬，现在福州市地处偏僻的台江区坞尾街的始建于明朝永乐元年（1403年）的万寿尚书庙，仍供奉着这位南宋民族英雄陈文龙，福州市政府则拨款200余万元重修此庙，并将尚书庙面积1100多平方米辟为陈文龙纪念馆。陈文龙又被后人称为镇海王，福建、台湾及东南亚国家。将陈文龙比作&amp;quot;海上保护神&amp;quot;，因为明永乐六年，朝廷封陈文龙为水部尚书；清乾隆四十六年，乾隆皇帝加封陈文龙为镇海王，更由于明清时期，每三年科举后，历朝皇帝都委派新科状元率册封团赴琉球岛（冲绳）、台湾岛册封当地地方官员。册封团在海上行船为祈求平安，将陈文龙塑像立于船中祭拜。由此，就有了海上官船拜陈文龙，民船拜妈祖之说。因此仅在台湾和马祖，保留完好的文龙庙就有16座之多。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, '台江', '2A', '免费', '福州市台江区三通路2号', '0591-83983253', '周一-周五：9:00－11:30，15:00-17:00，周六及周天闭馆。', '1小时', '四季皆宜 ', 0),
(7, 40, '孔元村', '它很浪漫，但并不奢侈。离城不远，离自然很近。这样一个可为普通市民“私人订制”的浪漫“外景地”，闽侯孔元村被称为“福州的普罗旺斯”。', './Public/Uploads/2017-04-14/58f06e330c065.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;孔元村景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151820264219.jpg&quot; title=&quot;1465811242469323.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;它很浪漫，但并不奢侈。离城不远，离自然很近。这样一个可为普通市民“私人订制”的浪漫“外景地”，闽侯孔元村被称为“福州的普罗旺斯”。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;闽侯孔元村坐落于白沙镇区东北处的一个小自然村，全村仅有200余户866人，离福州市区约25公里。这里没有繁华的街道，每一个角落都透露着闲适与宁静。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;寻找到这么一个浪漫的地方，对住在福州的你而言并不奢侈，只要花约1个小时驱车前往就可到此处，肆意享受这绮丽的田园风光。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;“花之谷”：四季都可赏花品果&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;孔元村一年四季都能看到各色花景，月月都能吃上新鲜的农家瓜果。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;今年4月份，这里盛开的“满天星”被网友戏称为“福州的普罗旺斯”，5月份是四季海棠的“天堂”，本月底，20多亩地的香彩雀全面进入盛花期，届时有兴趣的游客可与“粉色花海”来一场浪漫的邂逅。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;来孔元村最重要的是找一种农活进行体验。老少咸宜，可以全家上阵的有果实采摘。村里有相当大面积的草莓园、柑橘园等待游人。当然，能摘到哪种水果，视季节而定。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;不想步行却想览尽乡野风光的人还可以到村部去租借多人骑行车，两人一组或数人一队，村里有数公里长可供自行车骑行的车道。想享受慢生活，可以在河湖边找一处地儿垂钓。想烧烤聊天，附近也有农家乐可提供场所和器具。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;孔元村的特产是橄榄、脐橙和青梅。如果是十二月到了孔元村，村里的苦橘和脐橙不可不尝。从树上新鲜摘下的橙子，汁液鲜甜饱满。苦橘虽然甜度不高，但别有一种清香，据说是败火神器。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;看够吃够，孔元村还有一个地方不可错过。那就是村里人称为“知青楼”的一处旧办公楼，那里曾是福建最大的知青点“白沙五四农场”的场部所在地，最多时曾有数千知青从这里被分配到各个工区，而这一栋极具时代特色的建筑曾凝结了一整代人的青春记忆。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, '闽侯', '未评级', '免费', '福州市闽侯白沙孔元村', '0591-83983253', '全天', '1天', '四季皆宜 ', 0),
(8, 40, '林则徐纪念馆', '林则徐纪念馆，是福州市区里的一座古典园林式建筑，也是是“全国爱国主义教育示范基地”、“国家禁毒教育示范基地”和“国家国防教育示范基地”。', './Public/Uploads/2017-04-14/58f06eb4dc9d7.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;林则徐纪念馆景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151935593497.jpg&quot; title=&quot;1443238067691424.jpg&quot; alt=&quot;12-林则徐纪念馆-1.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;林则徐纪念馆，是福州市区里的一座古典园林式建筑，也是是“全国爱国主义教育示范基地”、“国家禁毒教育示范基地”和“国家国防教育示范基地”。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492151936507692.jpg&quot; title=&quot;1443238076303138.jpg&quot; alt=&quot;12-林则徐纪念馆-2.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;纪念馆筹建于1961年，1982年对外开放，包括国家级文物保护单位&amp;quot;林文忠公祠&amp;quot;和&amp;quot;林则徐史绩展&amp;quot;两大部分，占地面积8500平方米。林文忠公祠内有仪门、御碑亭、树德堂、海纳厅、云左阁等具有清代地方风格的古典建筑，回廊曲径，花木扶疏。“林则徐史绩展”则分为5个部分，通过“矢志报国的闽都赤子”、“清正务实的社稷名臣”、“放眼世界的左海伟人”、“举世共仰的禁毒先驱”、“抗敌御侮的民族英雄”5个专题，突出展示林则徐生平事迹，凸显他高尚的道德情操和惠泽民族的功绩。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 4, '鼓楼', '1A', '免费', '福州市鼓楼区澳门路16号', '0591-87622782', '8:30-18:00，17:30停止入场。', '1小时', '四季皆宜', 1),
(9, 40, '​贵安欢乐世界', '贵安欢乐世界，占地600亩，一个全国性的超级游乐场', './Public/Uploads/2017-04-14/58f06f48e53b0.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;贵安欢乐世界景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492152087501702.jpg&quot; title=&quot;1449718933900921.jpg&quot; alt=&quot;15.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;贵安欢乐世界，占地600亩，一个全国性的超级游乐场。充分利用自然的地形地貌，运用梦幻唯美的装饰手法、现代休闲理念和高新娱乐科技手段，有机的把最先进、最好玩的游戏设备、演艺项目融入主题场景之中，营造出惊奇、热烈、刺激的超级游乐王国，带给游客无尽的惊奇和超凡的享受。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492152087726380.jpg&quot; title=&quot;1456977569256821.jpg&quot; alt=&quot;11.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;恐龙时代的遗迹遍布视野，凝固的历史瞬间把人传送回亿万年前那个消失已久的史前世界：沐浴在夕阳柔和余晖中的侏罗纪公园，伸展着优美长脖子的雷龙，缓步走向湖面；霸王龙巨大的利齿撕裂似鸡龙的血肉，发出振聋发聩的怒吼；铺展翅膀的翼龙，在陡峭的峡谷间飞翔；草原上群龙疾奔，与你擦身而过。。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;贵安欢乐世界设置有各个不同主题游乐项目如：魔幻剧场、电玩竞技场、飓风飞椅、海盗船、森林剧场、自旋滑车、大力水手、旋转木马、魔法城堡、疯狂老鼠、水战船、摩托过山车、60米自由落体（美国）、飞行剧院（德国）、天网碰碰车、跳楼机、疯狂的士高、大摆锤、雪山飞龙过山车、峡谷漂流、4D影院、矿山车、激流勇进、儿童职业体验城、泡泡球馆、转转杯、小飞象、桑巴气球、高空塌车、小小世界、儿童摩天轮、欢乐嘉年华、翻江倒海、儿童火车等。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492152088940275.jpg&quot; title=&quot;1449718962640455.jpg&quot; alt=&quot;12.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;贵安欢乐世界是梦想的天地，推开欢乐之门，走进欢乐村庄，置身其中探寻梦中的童话世界！你可以在如童话般的古堡前驻足仰望，也可以在温馨的小屋之间徜徉，欢乐部落、欢乐坊、童悦玩具店、欢乐补给站、花样年华、手工坊、嘻哈俱乐部、阳光屋……这些带有欢乐字眼的商店，绝对可以满足您的快乐之旅。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;从摩天轮上俯瞰，将会呈现别样风情；动感迪斯科就是考验你胆量的时候了，别忍着了，怕就尖叫吧，迪斯科就是挑战你的极限忍耐力。一次次惊心动魄的置身体验，一个个扑朔迷离的探险传奇，欢乐世界让您一日之内穿越不同世界共享幸福时光！&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;超级大摆锤——海西目前最大的大摆锤。游客们将以70公里的时速冲上云霄，扑向大地，循环感受着强大离心力和太空般的失重感。实属勇敢者的游戏！飓风呼啸在耳边，天空俯冲到地面。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;缤纷的色彩、梦幻剧场、造型各异的魔幻建筑、航行在大海上载满奇珍异宝的海盗船，一条魔法大道把“浪漫满屋”、“阳光屋”、“淘园”、“魔力商城”、“彩虹集市”、“斯卡堡集市”六家综合主题商店和经典游乐项目“海盗传奇”依次串起。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;沙漠飞鹰——悬挂式过山车犹如一条蜿蜒盘旋的巨龙，任由轨道变化创造飞翔、跌落、翻滚、弯转等疯狂节奏体验凌空飞跃的感觉！疯狂大玩家汇聚高品质时尚设备，电玩发烧友不可错过的新潮趣味享受；眩晕中的快乐，感受高空弹跳旋转升降的惊险&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, '连江', '4A', '成人价180元，优惠价120元', '福州市连江县贵安新天地', '0591-38185888', '周一-周五：10:00-18:00；周六、周日及法定节假日：9:00-18:00。', '5小时', '四季皆宜 ', 0),
(10, 40, '福州云顶', '云顶景区位于福建省永泰县青云山顶，海拔在1000米以上的山峰有7座，最高海拔1130米。景区由5个游览区组成：花海梯田、天池草场、翡翠谷、红河谷、七彩瀑谷。', './Public/Uploads/2017-04-14/58f06f929942f.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;福州云顶景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492152179220622.jpg&quot; title=&quot;1449718272918272.jpg&quot; alt=&quot;6.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;云顶景区位于福建省永泰县青云山顶，海拔在1000米以上的山峰有7座，最高海拔1130米。景区由5个游览区组成：花海梯田、天池草场、翡翠谷、红河谷、七彩瀑谷。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;进入景区，首先是花海梯田，在春夏秋三季可以看到不同季节的花海。大约需要1-2小时才能欣赏完梯田，走到景区观光车停靠点（景区内的观光车是每15分钟一班），在天池景区下。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;天池是该景区的最高点，前往天池景区需要爬了365级台阶，天池是一口古火山爆发形成的，呈弯椭圆形的池水。天池周围就是万亩高山草甸，坡度平坦，可以放风筝。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492152179122835.jpg&quot; title=&quot;1449718377425500.jpg&quot; alt=&quot;8.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;天池景区有综合服务站，这里可以提供餐饮与住宿服务。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;乘坐观光车来到的峡谷景区（景区内有翡翠谷、红河谷、七彩瀑谷），全程走完需要4-5小时。跟着脚下的栈道首先进入的是七彩瀑布景区，可以观望彩虹瀑布、五曲瀑布、山神瀑布、人参瀑布、知音瀑布、金钟瀑布、双姝瀑布。当见到河床逐渐变红的时候，说明进入了红河谷景区。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492152179708483.jpg&quot; title=&quot;1449718301909494.jpg&quot; alt=&quot;10.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;进入到翡翠谷景区，感受海滨火山地质运动造就的“冰川遗址——冰臼奇迹”。步行一段时间，眼前出现了野人山寨，进入之后有怪屋、兽头骨架、远古图腾。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;穿过野人山寨后，来到了索道下站（索道单程40元，双程60元），乘全长1100米，高360米的五彩飞索索道穿梭在峡谷之间，大约10分钟。索道上站就在桃源村，这里可以品尝农家美食。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;小贴士：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;云顶景区可以一天内游玩，如果时间充裕的话，还是在景区附近住1晚体验一下QQ山寨里的QQ蛋居。QQ山寨位于天池景区附近，山寨里面有个酒店，主题是蛋，所以叫QQ蛋居，价格为非周末588元，周末价688元。如果想体验野外露宿的话，也提供帐篷，每顶200元上下&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, '永泰', '4A', '成人票（大门票+观光车）150元；成人套票（大门票+观光车+单程索道）190元', '福州市永泰县青云山顶', '0591-24879800', '夏季：8:00-16:30；冬季：8:00-16:00；周六、周日：8:30-17:00。', '5小时', '四季皆宜 ', 0),
(11, 40, '石竹山', '石竹山夏无酷署，冬少严寒，年均气温在19.7 左右，素以石奇竹秀而得名，有“雅胜鼓山”之誉，为福建省道教名山。有诗句曰，“石能留影常来鹤，竹欲摩空尽作龙”。', './Public/Uploads/2017-04-14/58f06fe9f3d52.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;石竹山景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492152256597629.jpg&quot; title=&quot;1457417992647709.jpg&quot; alt=&quot;111_副本.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;石竹山夏无酷署，冬少严寒，年均气温在19.7 左右，素以石奇竹秀而得名，有“雅胜鼓山”之誉，为福建省道教名山。有诗句曰，“石能留影常来鹤，竹欲摩空尽作龙”。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;山不在高，有仙则名。石竹山的道教文化有许多奇、绝之处，主要有“一梦（祈梦）、一签（抽签）、一春（接春）、一愿（许愿）、一生辰（元辰保护神）。每年，石竹山都会举办许多重大的宗教活动和民俗节日，就说接春吧：也就是迎接春神、春王。石竹山自古就被奉为行春之治所。每年的立春这一天，数以万计的信徒来到石竹山，手擎香烛，虔诚地跪拜上山，那时香烟升腾，烛光映天，漫山遍野，人山人海，香客蜿蜒成一条火龙，形成了非常壮观的一道人文景观。还有财神诞、玉皇诞、仙君诞、正月十五元宵节、七巧节、中元节、除夕等，都会把石竹山妆扮得热闹非凡。&lt;img src=&quot;/Public/upload/image/201704/1492152257220922.jpg&quot; title=&quot;1457418025926520.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;“九仙祈梦所”是石竹山宗教文化一大特色。你可以选择一路走上去，也可以坐缆车到半山（往返40元，单程30元），而后步行游览，更加省力。坐在缆车上往下看，石竹湖的碧绿水面和周围的山峦构成优美的山水画卷。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;由索道上站再往前不远便是半山腰的石竹寺，石竹寺是一座佛道共存的古寺院，里面供奉的主神并非玉皇和三清，而是在道教中鼎鼎有名的何氏九仙，据说祈福灵验，香火旺盛。需要注意的是，通常山下的香烛要比山上便宜得多。寺内可能遇到引导你如何拜拜的“热心人”，通常是会需要额外费用的，若无此打算礼貌拒绝即可。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492152257765338.jpg&quot; title=&quot;1457418087405655.jpg&quot; alt=&quot;10_副本.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;如果时间有限，可由石竹寺返程，想欣赏更多的自然景观可继续沿着石阶登顶状元峰。半山两条登山道在福强亭汇合，汇成一条路直通山顶，走完全程还是需要些体力的。此外，山中的狮岩堂也是比较出名的祈福地之一。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, '福清', '4A', '25元', '福州市福清市宏路镇石竹山', '0591-85388522', '5:30-17:30', '1天', '四季皆宜 ', 0),
(12, 40, '国家森林公园', '福州国家森林公园，又名福州植物园，面积859.33公顷，位于福州北郊的新店镇，是中国九大森林公园之一，原名福州树木园，是集科研与游览的综合性公园。', './Public/Uploads/2017-04-14/58f081b30130a.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;国家森林公园景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492156809807596.jpg&quot; title=&quot;1453717118769315.jpg&quot; alt=&quot;19676.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;福州国家森林公园，又名福州植物园，面积859.33公顷，位于福州北郊的新店镇，是中国九大森林公园之一，原名福州树木园，是集科研与游览的综合性公园。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;公园三面环山，一面临水，东以福飞路为界，西至湖顶与叶洋村接壤，南至八一水库北岸堤坝，北至岭头乡与笔架山毗邻。呈长方形，最高处的笔架山海拔643米。以搜集、展览福建树种为主，引进南方的木棉树，北方的钻天杨，热带的南洋杉，亚热带的樟树，温带的油松和被称为活化石的银杏、水杉等2500多种国内外珍贵树种。分森林区、苗圃、温室、专类园、休息区等5个部分。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;休息区内有一颗古榕树，相传是北宋治平年间福州太守张伯玉编户植榕时种下，这株古榕已有千年的树龄，树径约10米，树高20米，树冠如大伞，覆盖面积1300多平米，被人称为树王。榕树有无数的气根扎入地下，给母树供养料和水分，榕树生长茂盛寿命长，所以民间也称为不死树，值得一看。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492156810712929.jpg&quot; title=&quot;1453716879882118.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;img src=&quot;/Public/upload/image/201704/1492156810572805.jpg&quot; title=&quot;1453716880783743.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;每逢周末，多数的福州市民都会去植物园里散步看风景或者去公园的鸟语林，看鸟类表演，但大部分主要是去那里烧烤，打牌，泡茶，看锦鲤。人气最高的时候当属春季，每年的樱花季，公园里面花开遍野，有很多不同品种的樱花、梨花、桃花，很是美丽。踏青赏花、呼吸新鲜空气，再好不过了&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, '晋安', '4A', '鸟语林25元，其余免费', '福州市晋安区新店上赤桥4号', '0591-87916168', '8:00-16:00', '半天', '四季皆宜 ', 0),
(13, 40, '鼓山', '鼓山位于福州市东郊、闽江北岸，距离市中心区约8公里，是福州市最著名的风景区。', './Public/Uploads/2017-04-14/58f0d0130679f.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;鼓山景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492176902876914.jpg&quot; title=&quot;1459911629864846.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;鼓山位于福州市东郊、闽江北岸，距离市中心区约8公里，是福州市最著名的风景区。鼓山最高峰海拔925米，山上胜迹众多，林壑幽美，引人入胜。2002年5月，福建省鼓山风景名胜区经国务院批准列入第四批国家级风景名胜区名单。鼓山以古刹涌泉寺为中心，东有回龙阁、灵源洞等20多景；西有洞壑数十景，其中以十八景尤著；南有罗汉台、香炉峰等50多景；北有大顶峰、白云洞等45景。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492176902633563.jpg&quot; title=&quot;1459911629523675.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;这是古时登鼓山之道。从山麓下院登上2000多级台阶，经过“七亭七里路”就可到达涌泉寺山门。第一亭：“东际亭”，此后途中岩石上有诸多字刻可观。第二亭：“石门亭”，溪水淙淙不绝于耳。第三亭：“乘云亭”，沿道有石刻“乘云”、“仰涛”、“小鼓”等。第四亭：“半山亭”，有石刻“路通仙苑”，转小径可达“十八洞景”。“半山亭”后近300阶有石刻“桃岩洞口”，往东可到桃岩洞，桃花精舍。第五亭：“茶亭”，因亭附近有茶园而得名。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492176903628136.jpg&quot; title=&quot;1459911627216717.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;鼓山历来是福州的佛教胜地，半山腰的涌泉寺是鼓山最重要的景点。从石阶古道步行而上，蜿蜒1800余米，穿过七亭后进山门就直达涌泉寺了。爬到涌泉寺大约也就一小时。鼓山的售票处也位于此，如果想要入涌泉寺，需要买票入内。寺里香火不错，建筑物也规模雄伟，寺中千年铁树、鸡丝木供桌及血经书合称为镇山“三宝”。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492176904502880.jpg&quot; title=&quot;1459911630735346.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 4, '晋安', '4A', '爬山免费，涌泉寺40元，达摩洞十八景10元', '福州市晋安区鼓山路', '0591-83921184', '涌泉寺6:30-17:30，达摩洞十八景8:00-18:00，其他景点全天开放。\r\n', '半天', '夏季 ', 0),
(14, 40, '御温泉', '福州青云山御温泉酒店及温泉休闲中心位于国家重点风景名胜区；国家AAAA级旅游区北入口处一座元宝形山包之中，该酒店项目由总司--美国美中公司投巨资兴建。', './Public/Uploads/2017-04-14/58f0d0c813232.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;御温泉景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; text-align: center;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492177013286742.jpg&quot; title=&quot;1465367566505736.jpg&quot; alt=&quot;U5480P911DT20101217165613.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;福州青云山御温泉酒店及温泉休闲中心位于国家重点风景名胜区；国家AAAA级旅游区北入口处一座元宝形山包之中，该酒店项目由总司--美国美中公司投巨资兴建。园区四周群山环绕，东南面为石笋山，西面为鹅脰山，群峰林立，峭壁峥映；南面峡谷溪流；向北可远瞰永泰山城。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; text-align: center;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492177013356623.jpg&quot; title=&quot;1465367578127315.jpg&quot; alt=&quot;14429173881347urxfv_640X1000.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;1、青云山&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;青云山，国家级4A重点景区,。这里山高林茂，云雾飘渺，瀑布众多。动植物资源丰富，有珍稀动植物——桫椤和猕猴等。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;青山绿水，溪涧瀑布飞溅，云雾环绕、景色优美。整个建筑物依山就势、跌落有序、面面是景，充分利用原有优美自然生态环境，营造返朴归真的氛围。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; text-align: center;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492177014906998.jpg&quot; title=&quot;1465367590813427.jpg&quot; alt=&quot;14429204563453vahys_640X1000.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;2、温泉&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;御温泉酒店里的温泉，水质清澈，富有很多极好的有氧矿物质。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;青云山御温泉酒店的温泉水无臭、无味、无肉眼可见物，色度〈5度，浑浊度〈3度，水质清澈透明。PH值7.72～8.17,水质类型属HCO3––Na型水,低矿化度385.85—419.07mg/L\\氟含量12.5—16mg/L,均达到医疗偏硅酸水,氟水的命名指标。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;3、天然氧吧&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;经检测表明青云山各景点的负氧离子含量平均每立方米可达6万个以上，居全国各著名旅游景区的前列，可谓“天然氧吧”。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492177014848269.jpg&quot; title=&quot;1465367602951432.jpg&quot; alt=&quot;lwsbl6.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;4、青龙峡谷瀑布&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;这里可观赏到美丽的龙潭峡谷、鲤鱼潭、神龟戏水、灵蛇出洞、根包石，以及青龙瀑布、凤尾瀑布。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;5、SPA&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;这里的SPA包含了脸部护理、音乐按摩、水疗、泥疗、海洋疗法等内容，带给人愉悦享受，让人神情舒展。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, '永泰', '4A', '268元', '福州永泰县岭路乡境内青云山景区', '0591-87675791', '周一至周五：14:00-22:00 ；周六至周日：9:00-22:00', '半天', '四季皆宜 ', 0);
INSERT INTO `spot` (`id`, `cate_id`, `name`, `descs`, `pic`, `content`, `type`, `area`, `star`, `ticket`, `address`, `telephone`, `opentime`, `playtime`, `goodtime`, `praise`) VALUES
(15, 40, '福州花海公园', '福州花海公园位于南江滨西大道，东起魁浦大桥会展岛江滨大道沿江地块，横穿鼓山大桥桥底，西至鳌峰洲大桥。', './Public/Uploads/2017-04-14/58f0d12641d22.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;福州花海公园景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492177184181173.jpg&quot; title=&quot;1448347376146700.jpg&quot; alt=&quot;1.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;福州花海公园属于开放式公园，位于南江滨西大道，东起魁浦大桥会展岛江滨大道沿江地块，横穿鼓山大桥桥底，西至鳌峰洲大桥。随着季节变化的花海，很适合周末跟家人朋友一起出来漫步赏花拍照。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492177184774862.jpg&quot; title=&quot;1448347386815260.jpg&quot; alt=&quot;5.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;在花海公园内，春有油菜花、花菱草，夏有向日葵、荷花、百日草，秋有醉蝶花、大花金鸡菊等，在阳光下延展开来，仿佛一幅巨型画卷。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, '仓山', '未评级', '免费', '福州市仓山区南江滨大道福州花海公园\r\n', '0591-38185888', '全天', '1小时', '四季皆宜 ', 0),
(16, 40, '三叠井', '三叠井森林公园系新开发的景区，穿越清溪峡谷，溪水迂回;三叠井:瓮潭、眼镜潭、龙潭呈叠状顺势而下;龙潭', './Public/Uploads/2017-04-14/58f0d18e6635b.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;三叠井景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492177245483890.jpg&quot; title=&quot;1452152140742934.jpg&quot; alt=&quot;7.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;三叠井森林公园系新开发的景区，穿越清溪峡谷，溪水迂回;三叠井:瓮潭、眼镜潭、龙潭呈叠状顺势而下;龙潭，如白龙似的从四五十米高的山涧冲出，直奔下山，落入深潭。站在涧底，抬首仰望，四周均为峭壁陡崖，高耸云天，犹如井底之蛙&amp;quot;坐井观天&amp;quot;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;走进三叠井森林公园，路过一段山路，就会来到了三叠井瀑布的第一井--象鼻瀑布。为什么叫象鼻瀑布呢?据介绍，得名&amp;quot;象鼻瀑布&amp;quot;，是因为潭边的巨岩形似象鼻，岩石侧面还有一个洞就好比是象的眼睛，瀑布从高高的崖顶一直伸到潭底。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492177245754989.jpg&quot; title=&quot;1452152146749911.jpg&quot; alt=&quot;9.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;走过象鼻瀑布，眼前的路变得越来越难走，一条坡度近60度的石阶出现在了跟前。据介绍，这条石阶是人工造的天梯。想爬过它可不容易，这需要持久的耐力，脚步的速率也要恰到好处。三叠井最陡的就是天梯这个地方，它有322个台阶，走到上面到状元帽，那里面就比较平了。天梯两边都有护栏，在入梯处都有提示牌告诉游客，要注意安全，慢点走，手要护好，慢慢往上走，走的时候，不要走的太急，边走边休息。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492177246889898.jpg&quot; title=&quot;1457504560160889.jpg&quot; alt=&quot;40.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;爬过天梯，走过一段弥漫桂花香气的小道，就来到了第二井--仙女瀑布。走到这个地方我们可以看到，仙女瀑布像仙女散花一样，一会儿一会儿散一下，总高100多米，所以叫仙女瀑布，也叫生命之门。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;传说这里是仙女洗澡的地方，幽幽潭水，清可见底。来到这里很不容易，必须要走过一段很陡的山路，但当您看到这样魅力的景色，您就会觉得非常的值得了。走过两井，就到了最后一个目标清音瀑布。第三个井叫清音瀑布，听的到声音，但看不到瀑布，因为这边的路往上比较陡，所以叫清音瀑布。清音瀑布是三井中最后一井，溪水潺潺，瀑声清幽，但任您从哪个角度看，都看不到瀑布。使得此处更为幽静，在这里散步可以呼吸新鲜的空气，也让身心更为惬意。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, '闽侯', '3A', '40元', '闽侯县荆溪镇官源里仁洲村', '0591-22615918', '7:00-17:00', '3小时', '夏季 ', 0),
(17, 40, '畲山水', '罗源霍口乡，是福州仅有的两个畲族乡之一，畲山水景区属罗源“畲风海韵”旅游品牌的重要组成部分', './Public/Uploads/2017-04-14/58f0d2063c0b6.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;畲山水景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/Public/upload/image/201704/1492177408590668.jpg&quot; title=&quot;1492177408590668.jpg&quot; alt=&quot;dda9216c98c2d10f9be92ec6d156bd78.jpg&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;罗源霍口乡，是福州仅有的两个畲族乡之一，畲山水景区属罗源“畲风海韵”旅游品牌的重要组成部分。畲山水有山、有水、有绿树、有红叶，并以潺潺流动的纯净溪水取胜，呈“川”字形的峡谷地貌，组成动静相结合的巨幅山水水墨画。怪石奇树、深潭悬瀑、山泉溪谷、清流畲风，有福建省最大的百年红枫林，是福建省为数不多的水质特别清沏、生态保护良好、人文景观丰富的悠长峡谷景区。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, '罗源', '3A', '40元/人次', '罗源县霍口乡', '0591-22615918', '8:30 - 17:30', '2.5小时', '四季皆宜 ', 0),
(18, 40, '于山', '于山位于福州城区中心鼓楼区东南隅，相传汉代何氏九兄弟炼丹于此，因名九仙山。', './Public/Uploads/2017-04-14/58f0e79947448.jpg', '&lt;h3 style=&quot;margin: 0px; padding: 0px; font-size: 24px; font-weight: 400; height: 43px; border-bottom: 1px solid rgb(235, 235, 235);&quot;&gt;于山景点概况&lt;/h3&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img title=&quot;1443180473716146.jpg&quot; alt=&quot;8-于山风景区.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150925/1443180473716146.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;于山位于福州城区中心鼓楼区东南隅，相传汉代何氏九兄弟炼丹于此，因名九仙山。又以闽越王无诸九日宴饮于山，取名九日山。又因古代有一支“于越氏”居此，俗称于山。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;于山与乌山、屏山三山鼎峙，是福州市区三山之一，值得一去。整座山并不高，以松竹花卉，亭台楼阁和岩石石刻景观等为主，风景比较清幽，适合登山，散步。主要看点：戚公祠，严复读书的法雨堂，六鳌巨石，补山精舍，白塔。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img title=&quot;1443180485663902.jpg&quot; alt=&quot;8-于山风景区-2.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150925/1443180485663902.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;山上怪石嶙峋，林木参天，白塔和白塔寺隐居山中，像个年迈的老人，选择这里安享晚年。山脚下古城墙上被日军轰炸过的遗迹，时刻提醒着路人莫忘国耻，于山，福州历史文化的缩影。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;于山的戚公祠为纪念伟大的抗倭英雄戚继光而建，祠堂建在花岗岩上，岗峦起伏，花木扶疏，曲径通幽，颇具园林雅韵。祠厅系硬山顶土木结构建筑，内祀戚公戎装塑像，方颐隆准，威武庄严。两壁挂有历史画卷，歌颂戚公抗倭功勋。郁达夫游戚公祠，作《满江红》，以抒报国壮志。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;img title=&quot;1443180489256957.jpg&quot; alt=&quot;8-于山风景区-3.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150925/1443180489256957.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 0px auto; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;白塔寺的建筑是清道光年间保存下来的，有天王殿、大雄宝殿和法雨堂三大殿。大雄宝殿的仰板上有精致的彩绘人物图像，堂内悬挂着“宝络庄严”的泥金匾，让你不禁赞叹古人超群的艺术造诣。法雨堂是纪念寺僧舍身求雨而建的，曾经也是严复读书的地方，还是福建船政学堂的校舍，多重“身份”让这里更加令人向往。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;&lt;strong&gt;TIPS：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em;&quot;&gt;白塔寺有斋饭，游览这里可以品尝一下。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, '鼓楼', '4A', '免费', '福州市鼓楼区于山风景区', '0591-83353645', '8:30-17:00', '2小时', '四季皆宜 ', 0);

-- --------------------------------------------------------

--
-- 表的结构 `strategy`
--

CREATE TABLE `strategy` (
  `id` int(11) NOT NULL COMMENT '编号',
  `cate_id` int(11) NOT NULL COMMENT '分类',
  `title` varchar(100) NOT NULL COMMENT '标题',
  `reason` varchar(255) NOT NULL COMMENT '推荐理由',
  `pic` varchar(255) DEFAULT NULL COMMENT '图片',
  `content` text NOT NULL COMMENT '内容',
  `spots` varchar(255) DEFAULT NULL COMMENT '相关景点',
  `areas` varchar(255) DEFAULT NULL COMMENT '相关地区',
  `time` date NOT NULL COMMENT '创建时间',
  `user_id` int(11) NOT NULL COMMENT '创建人id',
  `ishot` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1:热门;0:普通',
  `praise` int(100) NOT NULL DEFAULT '0' COMMENT '赞的数量'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='旅游攻略';

--
-- 转存表中的数据 `strategy`
--

INSERT INTO `strategy` (`id`, `cate_id`, `title`, `reason`, `pic`, `content`, `spots`, `areas`, `time`, `user_id`, `ishot`, `praise`) VALUES
(10, 69, '虎纠经典二日自由行', '只需两天，便可一览榕城之秀美，品出虎纠的人文与深度，玩出新潮范儿。', './Public/Uploads/2017-04-26/5900a895dd43e.jpg', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;虽然不是福州本地人，但是在福州读书了四年的时光，已经留下众多的回忆、生活经历，希望我这近四年在这边的生活经历，能给来福州的亲带来一点点的帮助。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;也许大部分来福州的亲们都会选择三坊七巷，马尾船政局等传统景点，但其实蛮多福州的小众景点都有不一样的味道，假如你时间比较充裕，好希望你可以放慢时间，来一次慢旅行呢。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;以下就是眼中那些美丽的小众景点们~&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;福州国家森林公园&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494244982750.png&quot; alt=&quot;5-福州国家森林公园-3.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494244982750.png&quot; width=&quot;607&quot; style=&quot;border: 0px; margin: 10px auto; width: 607px; display: block; text-align: center !important; height: 403px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;非常纳闷，为啥一些来福州的亲们没提到森林公园？！其实，在我心中，森林公园才是我推荐的外地旅游 Top1...&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;森林公园，满眼绿色，负氧离子很高，最重要的是，里面最大的榕树一定要去看，可以说是福州榕城的标志。（“榕树王”高约20米，树龄930多年，树冠如大伞盖，遮天蔽日，盖地达1330多平方米，树下可容千人乘凉，真可谓“独木成林”）。没见过大古榕，还好意思说来过榕城？&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;当然福州国家森林公园值得一去的原因，门票免费是很重要的。如果是1月至3月，还能看到1500株樱花盛开，站在樱花树下往上看，有一种红花印满天的感觉。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;交通：森林公园门口就有公交车站，54路，102路，72路，84路，87路多可以到达。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;芍圆一号（福州的798）：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494294515277.png&quot; alt=&quot;25-161849-270.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494294515277.png&quot; width=&quot;644&quot; style=&quot;border: 0px; margin: 10px auto; width: 644px; display: block; text-align: center !important; height: 347px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;芍园一号是近几年福州出名的“文艺聚集地”，相当于北京的798，里面是咖啡馆、茶馆、酒吧的聚集处，这边的酒吧都是静吧，所以很适合来这边休憩一下。建议晚上来，比较有感觉。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;仓前山（福州的“万国建筑博览会”）&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494737537718.jpg&quot; alt=&quot;S30713T1304061935034.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494737537718.jpg&quot; width=&quot;642&quot; style=&quot;border: 0px; margin: 10px auto; width: 642px; display: block; text-align: center !important; height: 342px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;鸦片战争后，福州被辟为通商口岸，英美法德俄荷等17个国家的领事馆、各个教士纷纷来到仓山，在这种情况下，一批风格多样、造型美观的近代西式建筑在仓山拔地而起，成为仓山城区建筑的一大特色。如今，仓山里面还保留了很多老建筑，尤其当年的外国领事馆和一些教堂、洋房~&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果从师大仓山校区大门对面的康山里进去，还可以碰到可园。当年民国女神林徽因，也住在这里哦！！&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;金山寺&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494836743457.jpg&quot; alt=&quot;111.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494836743457.jpg&quot; style=&quot;border: 0px; margin: 10px auto; width: 680px; display: block; text-align: center !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在洪塘大桥旁边，在江水中的寺庙，需船夫渡船才可到寺庙，历史可追溯到宋朝。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;西湖公园&amp;amp;左海公园 or 江滨公园&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494905614618.png&quot; alt=&quot;1234.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494905614618.png&quot; width=&quot;646&quot; style=&quot;border: 0px; margin: 10px auto; width: 646px; display: block; text-align: center !important; height: 328px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州也有西湖？是的，虽然不能跟杭州西湖对比，但是福州人眼中还是觉得自己的西湖美的。宋著名词人辛弃疾也曾来过福州西湖，留下了《贺新郎—三山雨中游西湖》夸奖道：“烟雨偏宜晴更好，约略西施未嫁”。或漫步湖边栈道、或踏船悠游湖中、或来赏菊花盛景，西湖都是福州人去不腻的地方。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果西湖秋天赏菊，那么左海公园必玩的是海底世界和摩天轮...现在西湖左海已连通，可以一并游览。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;谁说福州没有海，欢迎你来平潭看海！&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443495001969203.jpg&quot; alt=&quot;1405586485782_0.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443495001969203.jpg&quot; style=&quot;border: 0px; margin: 10px auto; width: 680px; display: block; text-align: center !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;平潭因地理原因，和台湾隔海相望。基本上如果从福州去台湾玩，一定要从平潭走。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;去平潭虽然很麻烦，但是，去平潭看看海什么的，还是很值得的，因为那边的海真的很惊艳，比那些（充满生活垃圾的）海滩纯粹多了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1,5,10,11', '鼓楼,台江,仓山', '2017-04-26', 1, 0, 4),
(11, 69, '平潭过路观光两日游', '本线路集中平潭主要经典去处，带你短时间内饱览当地山海特色，非常适合过路观光以及周末游玩的旅行者。', './Public/Uploads/2017-04-26/5900a94ed93be.jpg', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;虽然不是福州本地人，但是在福州读书了四年的时光，已经留下众多的回忆、生活经历，希望我这近四年在这边的生活经历，能给来福州的亲带来一点点的帮助。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;也许大部分来福州的亲们都会选择三坊七巷，马尾船政局等传统景点，但其实蛮多福州的小众景点都有不一样的味道，假如你时间比较充裕，好希望你可以放慢时间，来一次慢旅行呢。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;以下就是眼中那些美丽的小众景点们~&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;福州国家森林公园&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494244982750.png&quot; alt=&quot;5-福州国家森林公园-3.png&quot; src=&quot;/Public/upload/image/201704/1493215559656487.png&quot; width=&quot;607&quot; style=&quot;border: 0px; margin: 10px auto; width: 607px; display: block; text-align: center !important; height: 403px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;非常纳闷，为啥一些来福州的亲们没提到森林公园？！其实，在我心中，森林公园才是我推荐的外地旅游 Top1...&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;森林公园，满眼绿色，负氧离子很高，最重要的是，里面最大的榕树一定要去看，可以说是福州榕城的标志。（“榕树王”高约20米，树龄930多年，树冠如大伞盖，遮天蔽日，盖地达1330多平方米，树下可容千人乘凉，真可谓“独木成林”）。没见过大古榕，还好意思说来过榕城？&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;当然福州国家森林公园值得一去的原因，门票免费是很重要的。如果是1月至3月，还能看到1500株樱花盛开，站在樱花树下往上看，有一种红花印满天的感觉。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;交通：森林公园门口就有公交车站，54路，102路，72路，84路，87路多可以到达。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;芍圆一号（福州的798）：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494294515277.png&quot; alt=&quot;25-161849-270.png&quot; src=&quot;/Public/upload/image/201704/1493215559135156.png&quot; width=&quot;644&quot; style=&quot;border: 0px; margin: 10px auto; width: 644px; display: block; text-align: center !important; height: 347px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;芍园一号是近几年福州出名的“文艺聚集地”，相当于北京的798，里面是咖啡馆、茶馆、酒吧的聚集处，这边的酒吧都是静吧，所以很适合来这边休憩一下。建议晚上来，比较有感觉。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;仓前山（福州的“万国建筑博览会”）&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494737537718.jpg&quot; alt=&quot;S30713T1304061935034.jpg&quot; src=&quot;/Public/upload/image/201704/1493215559982539.jpg&quot; width=&quot;642&quot; style=&quot;border: 0px; margin: 10px auto; width: 642px; display: block; text-align: center !important; height: 342px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;鸦片战争后，福州被辟为通商口岸，英美法德俄荷等17个国家的领事馆、各个教士纷纷来到仓山，在这种情况下，一批风格多样、造型美观的近代西式建筑在仓山拔地而起，成为仓山城区建筑的一大特色。如今，仓山里面还保留了很多老建筑，尤其当年的外国领事馆和一些教堂、洋房~&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果从师大仓山校区大门对面的康山里进去，还可以碰到可园。当年民国女神林徽因，也住在这里哦！！&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;金山寺&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494836743457.jpg&quot; alt=&quot;111.jpg&quot; src=&quot;/Public/upload/image/201704/1493215560434392.jpg&quot; style=&quot;border: 0px; margin: 10px auto; width: 680px; display: block; text-align: center !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在洪塘大桥旁边，在江水中的寺庙，需船夫渡船才可到寺庙，历史可追溯到宋朝。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;西湖公园&amp;amp;左海公园 or 江滨公园&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494905614618.png&quot; alt=&quot;1234.png&quot; src=&quot;/Public/upload/image/201704/1493215560358668.png&quot; width=&quot;646&quot; style=&quot;border: 0px; margin: 10px auto; width: 646px; display: block; text-align: center !important; height: 328px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州也有西湖？是的，虽然不能跟杭州西湖对比，但是福州人眼中还是觉得自己的西湖美的。宋著名词人辛弃疾也曾来过福州西湖，留下了《贺新郎—三山雨中游西湖》夸奖道：“烟雨偏宜晴更好，约略西施未嫁”。或漫步湖边栈道、或踏船悠游湖中、或来赏菊花盛景，西湖都是福州人去不腻的地方。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果西湖秋天赏菊，那么左海公园必玩的是海底世界和摩天轮...现在西湖左海已连通，可以一并游览。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;谁说福州没有海，欢迎你来平潭看海！&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443495001969203.jpg&quot; alt=&quot;1405586485782_0.jpg&quot; src=&quot;/Public/upload/image/201704/1493215561462354.jpg&quot; style=&quot;border: 0px; margin: 10px auto; width: 680px; display: block; text-align: center !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;平潭因地理原因，和台湾隔海相望。基本上如果从福州去台湾玩，一定要从平潭走。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;去平潭虽然很麻烦，但是，去平潭看看海什么的，还是很值得的，因为那边的海真的很惊艳，比那些（充满生活垃圾的）海滩纯粹多了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '3,12,13,15', '马尾,晋安', '2017-04-26', 1, 1, 2),
(12, 69, '福州暴走精华一日游', '福州市区不大，常常让人有一种一口气走完的冲动，这条路线就是为过路观光和穷游的驴友准备的。路线虽短，但囊括了在福州不得不去的景点，而且花费少，就在近郊，交通方便快捷，美食也一样不落下。', './Public/Uploads/2017-04-26/5900a98e65f1b.png', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;虽然不是福州本地人，但是在福州读书了四年的时光，已经留下众多的回忆、生活经历，希望我这近四年在这边的生活经历，能给来福州的亲带来一点点的帮助。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;也许大部分来福州的亲们都会选择三坊七巷，马尾船政局等传统景点，但其实蛮多福州的小众景点都有不一样的味道，假如你时间比较充裕，好希望你可以放慢时间，来一次慢旅行呢。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;以下就是眼中那些美丽的小众景点们~&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;福州国家森林公园&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494244982750.png&quot; alt=&quot;5-福州国家森林公园-3.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494244982750.png&quot; width=&quot;607&quot; style=&quot;border: 0px; margin: 10px auto; width: 607px; display: block; text-align: center !important; height: 403px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;非常纳闷，为啥一些来福州的亲们没提到森林公园？！其实，在我心中，森林公园才是我推荐的外地旅游 Top1...&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;森林公园，满眼绿色，负氧离子很高，最重要的是，里面最大的榕树一定要去看，可以说是福州榕城的标志。（“榕树王”高约20米，树龄930多年，树冠如大伞盖，遮天蔽日，盖地达1330多平方米，树下可容千人乘凉，真可谓“独木成林”）。没见过大古榕，还好意思说来过榕城？&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;当然福州国家森林公园值得一去的原因，门票免费是很重要的。如果是1月至3月，还能看到1500株樱花盛开，站在樱花树下往上看，有一种红花印满天的感觉。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;交通：森林公园门口就有公交车站，54路，102路，72路，84路，87路多可以到达。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;芍圆一号（福州的798）：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494294515277.png&quot; alt=&quot;25-161849-270.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494294515277.png&quot; width=&quot;644&quot; style=&quot;border: 0px; margin: 10px auto; width: 644px; display: block; text-align: center !important; height: 347px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;芍园一号是近几年福州出名的“文艺聚集地”，相当于北京的798，里面是咖啡馆、茶馆、酒吧的聚集处，这边的酒吧都是静吧，所以很适合来这边休憩一下。建议晚上来，比较有感觉。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;仓前山（福州的“万国建筑博览会”）&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494737537718.jpg&quot; alt=&quot;S30713T1304061935034.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494737537718.jpg&quot; width=&quot;642&quot; style=&quot;border: 0px; margin: 10px auto; width: 642px; display: block; text-align: center !important; height: 342px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;鸦片战争后，福州被辟为通商口岸，英美法德俄荷等17个国家的领事馆、各个教士纷纷来到仓山，在这种情况下，一批风格多样、造型美观的近代西式建筑在仓山拔地而起，成为仓山城区建筑的一大特色。如今，仓山里面还保留了很多老建筑，尤其当年的外国领事馆和一些教堂、洋房~&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果从师大仓山校区大门对面的康山里进去，还可以碰到可园。当年民国女神林徽因，也住在这里哦！！&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;金山寺&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494836743457.jpg&quot; alt=&quot;111.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494836743457.jpg&quot; style=&quot;border: 0px; margin: 10px auto; width: 680px; display: block; text-align: center !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在洪塘大桥旁边，在江水中的寺庙，需船夫渡船才可到寺庙，历史可追溯到宋朝。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;西湖公园&amp;amp;左海公园 or 江滨公园&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494905614618.png&quot; alt=&quot;1234.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494905614618.png&quot; width=&quot;646&quot; style=&quot;border: 0px; margin: 10px auto; width: 646px; display: block; text-align: center !important; height: 328px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州也有西湖？是的，虽然不能跟杭州西湖对比，但是福州人眼中还是觉得自己的西湖美的。宋著名词人辛弃疾也曾来过福州西湖，留下了《贺新郎—三山雨中游西湖》夸奖道：“烟雨偏宜晴更好，约略西施未嫁”。或漫步湖边栈道、或踏船悠游湖中、或来赏菊花盛景，西湖都是福州人去不腻的地方。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果西湖秋天赏菊，那么左海公园必玩的是海底世界和摩天轮...现在西湖左海已连通，可以一并游览。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;谁说福州没有海，欢迎你来平潭看海！&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443495001969203.jpg&quot; alt=&quot;1405586485782_0.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443495001969203.jpg&quot; style=&quot;border: 0px; margin: 10px auto; width: 680px; display: block; text-align: center !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;平潭因地理原因，和台湾隔海相望。基本上如果从福州去台湾玩，一定要从平潭走。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;去平潭虽然很麻烦，但是，去平潭看看海什么的，还是很值得的，因为那边的海真的很惊艳，比那些（充满生活垃圾的）海滩纯粹多了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1,2,5,10', '仓山,马尾,晋安', '2017-04-26', 1, 0, 4),
(13, 69, '闽都文化之旅 （3至4日游）', '“七郡辐辏，闽越一都会也”，福州被称为闽都，不仅是因为它曾是闽越国、五代王闽政权的国都，更是基于其八闽首府的传统地位。由于长期居于八闽的政治、经济、文化中心，福州日渐形成具有鲜明特色的地域文化，被誉称为“闽都文化”。', './Public/Uploads/2017-04-26/5900a9daa517d.jpg', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;虽然不是福州本地人，但是在福州读书了四年的时光，已经留下众多的回忆、生活经历，希望我这近四年在这边的生活经历，能给来福州的亲带来一点点的帮助。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;也许大部分来福州的亲们都会选择三坊七巷，马尾船政局等传统景点，但其实蛮多福州的小众景点都有不一样的味道，假如你时间比较充裕，好希望你可以放慢时间，来一次慢旅行呢。&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;以下就是眼中那些美丽的小众景点们~&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;福州国家森林公园&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494244982750.png&quot; alt=&quot;5-福州国家森林公园-3.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494244982750.png&quot; width=&quot;607&quot; style=&quot;border: 0px; margin: 10px auto; width: 607px; display: block; text-align: center !important; height: 403px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;非常纳闷，为啥一些来福州的亲们没提到森林公园？！其实，在我心中，森林公园才是我推荐的外地旅游 Top1...&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;森林公园，满眼绿色，负氧离子很高，最重要的是，里面最大的榕树一定要去看，可以说是福州榕城的标志。（“榕树王”高约20米，树龄930多年，树冠如大伞盖，遮天蔽日，盖地达1330多平方米，树下可容千人乘凉，真可谓“独木成林”）。没见过大古榕，还好意思说来过榕城？&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;当然福州国家森林公园值得一去的原因，门票免费是很重要的。如果是1月至3月，还能看到1500株樱花盛开，站在樱花树下往上看，有一种红花印满天的感觉。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;交通：森林公园门口就有公交车站，54路，102路，72路，84路，87路多可以到达。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;芍圆一号（福州的798）：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494294515277.png&quot; alt=&quot;25-161849-270.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494294515277.png&quot; width=&quot;644&quot; style=&quot;border: 0px; margin: 10px auto; width: 644px; display: block; text-align: center !important; height: 347px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;芍园一号是近几年福州出名的“文艺聚集地”，相当于北京的798，里面是咖啡馆、茶馆、酒吧的聚集处，这边的酒吧都是静吧，所以很适合来这边休憩一下。建议晚上来，比较有感觉。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;仓前山（福州的“万国建筑博览会”）&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494737537718.jpg&quot; alt=&quot;S30713T1304061935034.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494737537718.jpg&quot; width=&quot;642&quot; style=&quot;border: 0px; margin: 10px auto; width: 642px; display: block; text-align: center !important; height: 342px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;鸦片战争后，福州被辟为通商口岸，英美法德俄荷等17个国家的领事馆、各个教士纷纷来到仓山，在这种情况下，一批风格多样、造型美观的近代西式建筑在仓山拔地而起，成为仓山城区建筑的一大特色。如今，仓山里面还保留了很多老建筑，尤其当年的外国领事馆和一些教堂、洋房~&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果从师大仓山校区大门对面的康山里进去，还可以碰到可园。当年民国女神林徽因，也住在这里哦！！&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;金山寺&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494836743457.jpg&quot; alt=&quot;111.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494836743457.jpg&quot; style=&quot;border: 0px; margin: 10px auto; width: 680px; display: block; text-align: center !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在洪塘大桥旁边，在江水中的寺庙，需船夫渡船才可到寺庙，历史可追溯到宋朝。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;西湖公园&amp;amp;左海公园 or 江滨公园&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443494905614618.png&quot; alt=&quot;1234.png&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443494905614618.png&quot; width=&quot;646&quot; style=&quot;border: 0px; margin: 10px auto; width: 646px; display: block; text-align: center !important; height: 328px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;福州也有西湖？是的，虽然不能跟杭州西湖对比，但是福州人眼中还是觉得自己的西湖美的。宋著名词人辛弃疾也曾来过福州西湖，留下了《贺新郎—三山雨中游西湖》夸奖道：“烟雨偏宜晴更好，约略西施未嫁”。或漫步湖边栈道、或踏船悠游湖中、或来赏菊花盛景，西湖都是福州人去不腻的地方。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果西湖秋天赏菊，那么左海公园必玩的是海底世界和摩天轮...现在西湖左海已连通，可以一并游览。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;谁说福州没有海，欢迎你来平潭看海！&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;1443495001969203.jpg&quot; alt=&quot;1405586485782_0.jpg&quot; src=&quot;http://www.fjta.com/static/uploads/content_img/20150929/1443495001969203.jpg&quot; style=&quot;border: 0px; margin: 10px auto; width: 680px; display: block; text-align: center !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;平潭因地理原因，和台湾隔海相望。基本上如果从福州去台湾玩，一定要从平潭走。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &amp;quot;Microsoft Yahei&amp;quot;, Arial, sans-serif; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;去平潭虽然很麻烦，但是，去平潭看看海什么的，还是很值得的，因为那边的海真的很惊艳，比那些（充满生活垃圾的）海滩纯粹多了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '3,4', '鼓楼,福清', '2017-04-26', 1, 0, 10);
INSERT INTO `strategy` (`id`, `cate_id`, `title`, `reason`, `pic`, `content`, `spots`, `areas`, `time`, `user_id`, `ishot`, `praise`) VALUES
(14, 69, '泰宁大金湖、寨下大峡谷纯玩三日游', '重点推荐三明泰宁大金湖景区的精华景点和玩点，让你在打开惊奇的闽西旅行世界的同时，亲近自然生态，拥抱清新健康。', './Public/Uploads/2017-05-03/5909bbb347efa.jpg', '&lt;p&gt;开放时间：夏季7:00-15:30， 冬季7:30-14:00&lt;span class=&quot;span_right&quot; style=&quot;float: right;&quot;&gt;门票：景区票80元，船票75元，导游费8元&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px;&quot;&gt;大金湖位于福建省西北部，以丹霞地貌景观为主体，同时还有花岗岩地貌景观和人文景观等点缀其中。于2005年2月11日成为联合国教科文组织评定的“泰宁世界地质公园”的核心组成部分，成为继武夷山“双世遗”之后又一个入主世界级别的福建旅游景区。而且，大金湖是同时拥有五块国家级牌子的旅游景区，包括国家重点风景名胜区、国家AAAAA级旅游区、国家地质公园、国家重点文物保护单位、国家森林公园。金湖湖面呈圆弧形，宛......&lt;a class=&quot;more&quot; target=&quot;_bank&quot; href=&quot;http://www.fjta.com/scenic/detail/33&quot; style=&quot;text-decoration-line: none; color: rgb(255, 102, 0);&quot;&gt;景点详情&lt;span style=&quot;font-family: sans-serif, serif; display: inline-block; padding: 0px 4px;&quot;&gt;&amp;gt;&amp;gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/221778d770b479675856eb8b96fcdf48.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/221778d770b479675856eb8b96fcdf48_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/6943c3cb518bbb3ec85a52358fae8d7c.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/6943c3cb518bbb3ec85a52358fae8d7c_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/2345084e0e67c98bc0a34dcdf13d27d4.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/2345084e0e67c98bc0a34dcdf13d27d4_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;float: left; color: rgb(51, 51, 51);&quot;&gt;&lt;span class=&quot;icon-travel-car&quot; style=&quot;display: inline-block; background-position: -412px -113px; width: 34px; height: 28px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;轮船&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;0.5小时&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;甘露岩寺在金湖船舶的一个&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;一般选择驾车，因为那里猫儿山有点远，猫儿山也可以驾车上山，然后再徒步。&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;icon-travel-tip&quot; style=&quot;display: inline-block; background-position: -409px -470px; width: 14px; height: 17px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); position: absolute; top: 0px; left: -36px; z-index: 1;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hour&quot; style=&quot;float: right;&quot;&gt;[建议游玩：半小时]&lt;/span&gt;&lt;span class=&quot;step_num&quot; style=&quot;float: left; width: 55px; height: 40px; background: rgb(255, 102, 0); color: rgb(255, 255, 255); text-align: center;&quot;&gt;2&lt;/span&gt;&lt;span class=&quot;arrow&quot; style=&quot;line-height: 0; font-size: 0px; margin-top: 15px; float: left; width: 0px; height: 0px; border-width: 5px; border-style: dashed dashed dashed solid; border-color: transparent transparent transparent rgb(255, 102, 0);&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;font-size: 20px; padding: 0px 15px 0px 13px;&quot;&gt;&lt;a class=&quot;view_map&quot; data-lng=&quot;117.085106&quot; data-lat=&quot;26.87602&quot; title=&quot;点击查看地图详情&quot; style=&quot;color: rgb(51, 51, 51);&quot;&gt;甘露岩寺&lt;/a&gt;&lt;/strong&gt;4A&lt;/p&gt;&lt;p&gt;开放时间：夏季：7:00-15:30 ，冬季：7:30-14:00&lt;span class=&quot;span_right&quot; style=&quot;float: right;&quot;&gt;门票：包含在金湖（80元）套票里&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px;&quot;&gt;甘露岩寺位于福建省泰宁县境内的泰宁风景名胜区金湖西岸，长滩人形山西侧，始建于宋绍兴十六年（公元1146年），距今已有850多年的历史，采取“一柱插地，不假片瓦”的独特结构建筑，是海内外久负盛名的岩寺之一，上世纪50年代被列为省级文物保护单位。 岩寺隐藏于赤石深壑之中，左边一块红色岩石象一口硕大无比的“钟”，右边那块像一面独步天下的巨“鼓”；甘露寺便在这钟鼓石之间，所以有这么个说法叫“右鼓左钟，庙......&amp;nbsp;&lt;a class=&quot;more&quot; target=&quot;_bank&quot; href=&quot;http://www.fjta.com/scenic/detail/102&quot; style=&quot;text-decoration-line: none; color: rgb(255, 102, 0);&quot;&gt;景点详情&lt;span style=&quot;font-family: sans-serif, serif; display: inline-block; padding: 0px 4px;&quot;&gt;&amp;gt;&amp;gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/20fff08f2be088bbc22e92c92c6ac43e.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/20fff08f2be088bbc22e92c92c6ac43e_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/1a4c8e3f7583b34cf79f797845e5badc.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/1a4c8e3f7583b34cf79f797845e5badc_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/40b0351520166a58380576b09fbe9be3.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/40b0351520166a58380576b09fbe9be3_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;float: left; color: rgb(51, 51, 51);&quot;&gt;&lt;span class=&quot;icon-travel-car&quot; style=&quot;display: inline-block; background-position: -412px -113px; width: 34px; height: 28px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;自驾&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;0.5小时&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;距猫儿山21公里&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;一般选择驾车，因为那里猫儿山有点远，猫儿山也可以驾车上山，然后再徒步。&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;icon-travel-tip&quot; style=&quot;display: inline-block; background-position: -409px -470px; width: 14px; height: 17px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); position: absolute; top: 0px; left: -36px; z-index: 1;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hour&quot; style=&quot;float: right;&quot;&gt;[建议游玩：3小时]&lt;/span&gt;&lt;span class=&quot;step_num&quot; style=&quot;float: left; width: 55px; height: 40px; background: rgb(255, 102, 0); color: rgb(255, 255, 255); text-align: center;&quot;&gt;3&lt;/span&gt;&lt;span class=&quot;arrow&quot; style=&quot;line-height: 0; font-size: 0px; margin-top: 15px; float: left; width: 0px; height: 0px; border-width: 5px; border-style: dashed dashed dashed solid; border-color: transparent transparent transparent rgb(255, 102, 0);&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;font-size: 20px; padding: 0px 15px 0px 13px;&quot;&gt;&lt;a class=&quot;view_map&quot; data-lng=&quot;117.043418&quot; data-lat=&quot;26.796861&quot; title=&quot;点击查看地图详情&quot; style=&quot;color: rgb(51, 51, 51);&quot;&gt;猫儿山国家森林公园&lt;/a&gt;&lt;/strong&gt;未评级&lt;/p&gt;&lt;p&gt;开放时间：夏季：07:00-15:30 ，冬季：7:30-14:00&lt;span class=&quot;span_right&quot; style=&quot;float: right;&quot;&gt;门票：免费&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px;&quot;&gt;猫儿山国家森林公园因山石似猫而得名。猫儿山又名罗汉山、三剑峰，三剑峰和悬索桥是金湖的标志，地处金湖中心。猫儿山之趣，可见三剑峰刺入云霄，金猫山踞天窥世，仙女峰举目望夫，一山三态，不愧为丹霞地貌之杰作。 猫儿山国家森林公园位于福建三明泰宁县境内，濒临金湖十里平湖景区，是大金湖旅游线上一颗璀璨明珠，公园面积2560公顷，主要景区有金猫山景区，鹤鸣山景区，黄家湖景区，平湖景区。 猫儿山国家森林公园......&lt;a class=&quot;more&quot; target=&quot;_bank&quot; href=&quot;http://www.fjta.com/scenic/detail/124&quot; style=&quot;text-decoration-line: none; color: rgb(255, 102, 0);&quot;&gt;景点详情&lt;span style=&quot;font-family: sans-serif, serif; display: inline-block; padding: 0px 4px;&quot;&gt;&amp;gt;&amp;gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/10609f6391adc3937001d2b311faa935.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/10609f6391adc3937001d2b311faa935_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/567f933f9b75340c9f7f165fd4485dcf.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/567f933f9b75340c9f7f165fd4485dcf_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/e60783e0b01e635b9190179191b156bd.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/e60783e0b01e635b9190179191b156bd_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;float: left; color: rgb(51, 51, 51);&quot;&gt;&lt;span class=&quot;icon-travel-car&quot; style=&quot;display: inline-block; background-position: -412px -113px; width: 34px; height: 28px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;自驾&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;0.5小时&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;icon-day-num&quot; style=&quot;display: inline-block; background-position: -131px -100px; width: 48px; height: 40px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); line-height: 40px; color: rgb(255, 255, 255); font-weight: 600; font-size: 14px; text-align: center; float: left; margin: 0px -20px 0px 31px; border-right: 10px solid rgb(255, 255, 255); position: fixed; top: 0px; z-index: 999;&quot;&gt;D2&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hour&quot; style=&quot;float: right;&quot;&gt;[建议游玩：2小时]&lt;/span&gt;&lt;span class=&quot;step_num&quot; style=&quot;float: left; width: 55px; height: 40px; background: rgb(255, 102, 0); color: rgb(255, 255, 255); text-align: center;&quot;&gt;1&lt;/span&gt;&lt;span class=&quot;arrow&quot; style=&quot;line-height: 0; font-size: 0px; margin-top: 15px; float: left; width: 0px; height: 0px; border-width: 5px; border-style: dashed dashed dashed solid; border-color: transparent transparent transparent rgb(255, 102, 0);&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;font-size: 20px; padding: 0px 15px 0px 13px;&quot;&gt;&lt;a class=&quot;view_map&quot; data-lng=&quot;117.195428&quot; data-lat=&quot;27.00922&quot; title=&quot;点击查看地图详情&quot; style=&quot;color: rgb(51, 51, 51);&quot;&gt;上清溪&lt;/a&gt;&lt;/strong&gt;未评级&lt;/p&gt;&lt;p&gt;开放时间：夏季：7:00-15:30， 冬季：7:30-14:00&lt;span class=&quot;span_right&quot; style=&quot;float: right;&quot;&gt;门票：95元&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px;&quot;&gt;上清溪在经6500万年地质运动形成的峡谷中，位于泰宁县境内的金湖上游，全长50多公里，游览“漂流”长度达15公里。其沿岩幽奥的丹霞地貌堪与武夷山风景区的九曲溪，著名的武陵源金鞭溪相媲美。自西汉末年南昌尉梅福弃官来到上清溪畔的栖真岩炼丹隐居以来，入山求道者络绎不绝，上清溪于是成为荟萃灵异事迹和神仙传说的道教胜地。“上清”，道家三境界之一，其义为“仙境”。深山幽谷中，曲流千回百转，两岸奇岩跋扈，好一片......&amp;nbsp;&lt;a class=&quot;more&quot; target=&quot;_bank&quot; href=&quot;http://www.fjta.com/scenic/detail/59&quot; style=&quot;text-decoration-line: none; color: rgb(255, 102, 0);&quot;&gt;景点详情&lt;span style=&quot;font-family: sans-serif, serif; display: inline-block; padding: 0px 4px;&quot;&gt;&amp;gt;&amp;gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/23e6a0554fcffe8891f7ceb6f0c3e42b.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/23e6a0554fcffe8891f7ceb6f0c3e42b_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/c2f7ddd8dd5e9342f6ba82ca6450c923.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/c2f7ddd8dd5e9342f6ba82ca6450c923_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/df5348f3742d0c36414e0ff60e11f665.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/df5348f3742d0c36414e0ff60e11f665_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;float: left; color: rgb(51, 51, 51);&quot;&gt;&lt;span class=&quot;icon-travel-car&quot; style=&quot;display: inline-block; background-position: -412px -113px; width: 34px; height: 28px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;步行&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;40分钟&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;距九龙潭3.2公里&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;在上清溪可以排队买票坐竹筏漂流，用时约2小时。&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;icon-travel-tip&quot; style=&quot;display: inline-block; background-position: -409px -470px; width: 14px; height: 17px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); position: absolute; top: 0px; left: -36px; z-index: 1;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hour&quot; style=&quot;float: right;&quot;&gt;[建议游玩：1.5小时]&lt;/span&gt;&lt;span class=&quot;step_num&quot; style=&quot;float: left; width: 55px; height: 40px; background: rgb(255, 102, 0); color: rgb(255, 255, 255); text-align: center;&quot;&gt;2&lt;/span&gt;&lt;span class=&quot;arrow&quot; style=&quot;line-height: 0; font-size: 0px; margin-top: 15px; float: left; width: 0px; height: 0px; border-width: 5px; border-style: dashed dashed dashed solid; border-color: transparent transparent transparent rgb(255, 102, 0);&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;font-size: 20px; padding: 0px 15px 0px 13px;&quot;&gt;&lt;a class=&quot;view_map&quot; data-lng=&quot;117.192034&quot; data-lat=&quot;26.9904&quot; title=&quot;点击查看地图详情&quot; style=&quot;color: rgb(51, 51, 51);&quot;&gt;九龙潭&lt;/a&gt;&lt;/strong&gt;未评级&lt;/p&gt;&lt;p&gt;开放时间：8:00-16:30&lt;span class=&quot;span_right&quot; style=&quot;float: right;&quot;&gt;门票：陆上游：65元，陆上游+水上竹筏/气垫船：115元&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px;&quot;&gt;九龙潭风景区位于福建省泰宁县世界自然遗产、世界地质公园北部核心区，与上清溪、红石沟、和状元岩毗邻，因四周九条蜿蜒如龙的山涧泉水汇聚潭中而得名。九龙潭是组成密度和复杂度最高的一片中国丹霞峡谷群，是泰宁青年期的丹霞地貌发育的典型地区，融湖、溪、山、谷、岩、峰、沟多样变化，号称“泰宁丹霞山水微缩明珠”。泛舟潭中，望着如画般的山谷、溪涧、草木、碧水，穿过中国最美最长最狭窄、雄奇的水上一线天和幽深的水中龙洞......&amp;nbsp;&lt;a class=&quot;more&quot; target=&quot;_bank&quot; href=&quot;http://www.fjta.com/scenic/detail/171&quot; style=&quot;text-decoration-line: none; color: rgb(255, 102, 0);&quot;&gt;景点详情&lt;span style=&quot;font-family: sans-serif, serif; display: inline-block; padding: 0px 4px;&quot;&gt;&amp;gt;&amp;gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/b187d280b153e3807be2d5eb6c825c9f.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/b187d280b153e3807be2d5eb6c825c9f_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/067a71e5d27b61a13816387d4eb2f795.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/067a71e5d27b61a13816387d4eb2f795_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/6782782229df6c006d403a51c2d78925.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/6782782229df6c006d403a51c2d78925_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;float: left; color: rgb(51, 51, 51);&quot;&gt;&lt;span class=&quot;icon-travel-car&quot; style=&quot;display: inline-block; background-position: -412px -113px; width: 34px; height: 28px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;自驾&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;20分钟&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;距状元岩景区7公里&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;九龙潭可以走栈道和坐竹筏，走栈道也就是爬山，适合户外爱好者；由于景区周边没有发达的公共交通系统，所以有条件的还可以选择自驾。&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;icon-travel-tip&quot; style=&quot;display: inline-block; background-position: -409px -470px; width: 14px; height: 17px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); position: absolute; top: 0px; left: -36px; z-index: 1;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hour&quot; style=&quot;float: right;&quot;&gt;[建议游玩：1小时]&lt;/span&gt;&lt;span class=&quot;step_num&quot; style=&quot;float: left; width: 55px; height: 40px; background: rgb(255, 102, 0); color: rgb(255, 255, 255); text-align: center;&quot;&gt;3&lt;/span&gt;&lt;span class=&quot;arrow&quot; style=&quot;line-height: 0; font-size: 0px; margin-top: 15px; float: left; width: 0px; height: 0px; border-width: 5px; border-style: dashed dashed dashed solid; border-color: transparent transparent transparent rgb(255, 102, 0);&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;font-size: 20px; padding: 0px 15px 0px 13px;&quot;&gt;&lt;a class=&quot;view_map&quot; data-lng=&quot;117.209879&quot; data-lat=&quot;26.970336&quot; title=&quot;点击查看地图详情&quot; style=&quot;color: rgb(51, 51, 51);&quot;&gt;状元岩&lt;/a&gt;&lt;/strong&gt;未评级&lt;/p&gt;&lt;p&gt;开放时间：7:30-15:00&lt;span class=&quot;span_right&quot; style=&quot;float: right;&quot;&gt;门票：40元(不含讲解费)&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px;&quot;&gt;状元岩位于泰宁城北郊，距泰宁县城12公里。景区内林木葱郁，环境幽雅，属原始次森林，森林覆盖率达95%以上，是一座“天然氧吧”。也许是因为这块丹霞圣地充满灵气的缘故，这里还生长了部分热带雨林植物 。状元岩景区分为状元谷、葫芦天、五趾山、香婆岩、鸟鸣涧等片区。主要景点有： 五虎上山， 状元谷，状元谷尽头为三角井，直石铺路，越王台，状元顶，斗米阶。拾阶而上，见丹崖悬岩，茂林奇树，呈现出典型的丹霞地貌景观......&amp;nbsp;&lt;a class=&quot;more&quot; target=&quot;_bank&quot; href=&quot;http://www.fjta.com/scenic/detail/64&quot; style=&quot;text-decoration-line: none; color: rgb(255, 102, 0);&quot;&gt;景点详情&lt;span style=&quot;font-family: sans-serif, serif; display: inline-block; padding: 0px 4px;&quot;&gt;&amp;gt;&amp;gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/e13e4e1c6724d0365d8e8f523446cbee.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/e13e4e1c6724d0365d8e8f523446cbee_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/813564b6965b7c7bd4752a53cf2268ec.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/813564b6965b7c7bd4752a53cf2268ec_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/78fc152b7892545dd8129bc38a293a57.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;http://www.fjta.com/static/uploads/jingdian/78fc152b7892545dd8129bc38a293a57_m.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2,12,13,14', '晋安,连江,福清', '2017-05-03', 1, 0, 0),
(15, 69, '温泉度假之旅（3至7日游）', '福建温泉资源分布广、水温高、水质优，已经超过100处温泉源头，温泉资源全国排名第三，有热泉，也有冷泉，是福建旅游的重要资源。闽菜是中国八大菜系之一，以烹制山珍海味而著称，在色香味形俱佳的基础上，尤以“香”、“味”见长。', './Public/Uploads/2017-05-03/5909bfe322fe8.jpg', '&lt;p&gt;开放时间：7:30-16:30&lt;span class=&quot;span_right&quot; style=&quot;float: right;&quot;&gt;门票：20元&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px;&quot;&gt;旗山国家森林公园，位于福州闽侯南屿境内，地处戴云山脉向东延伸的丘陵地带，面积3587公顷，距福州市中心仅25公里，且紧靠316国道，交通便捷。园内三面环山、群峰如屏，南临八一水库，碧波涟漪，清山绿水相映生辉。旗山国家森林公园阳光明媚，树木常青，一派南国风光，尤其适合北方的游客。旗山国家森林公园包有满神话般色彩的“狮子山”、“大岩顶”、“象鼻峰”，还有奇特的高山平湖“天池湖”、“地池湖”。......&amp;nbsp;&lt;a class=&quot;more&quot; target=&quot;_bank&quot; href=&quot;http://www.fjta.com/scenic/detail/79&quot; style=&quot;text-decoration-line: none; color: rgb(255, 102, 0);&quot;&gt;景点详情&lt;span style=&quot;font-family: sans-serif, serif; display: inline-block; padding: 0px 4px;&quot;&gt;&amp;gt;&amp;gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/a939eeeddd676fff9f57666e20ec7bc4.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;/Public/upload/image/201705/1493811134814938.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/8edd33f0f1a9867439bf0752be7a6836.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;/Public/upload/image/201705/1493811134559456.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/2e81729b826177c0710772d20f8b4706.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;/Public/upload/image/201705/1493811134231717.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;float: left; color: rgb(51, 51, 51);&quot;&gt;&lt;span class=&quot;icon-travel-car&quot; style=&quot;display: inline-block; background-position: -412px -113px; width: 34px; height: 28px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;汽车&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;icon-travel-tip&quot; style=&quot;display: inline-block; background-position: -409px -470px; width: 14px; height: 17px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); position: absolute; top: 0px; left: -36px; z-index: 1;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hour&quot; style=&quot;float: right;&quot;&gt;[建议游玩：半天]&lt;/span&gt;&lt;span class=&quot;step_num&quot; style=&quot;float: left; width: 55px; height: 40px; background: rgb(255, 102, 0); color: rgb(255, 255, 255); text-align: center;&quot;&gt;2&lt;/span&gt;&lt;span class=&quot;arrow&quot; style=&quot;line-height: 0; font-size: 0px; margin-top: 15px; float: left; width: 0px; height: 0px; border-width: 5px; border-style: dashed dashed dashed solid; border-color: transparent transparent transparent rgb(255, 102, 0);&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;font-size: 20px; padding: 0px 15px 0px 13px;&quot;&gt;&lt;a class=&quot;view_map&quot; data-lng=&quot;120.011437&quot; data-lat=&quot;26.891659&quot; title=&quot;点击查看地图详情&quot; style=&quot;color: rgb(51, 51, 51);&quot;&gt;霞浦县&lt;/a&gt;&lt;/strong&gt;未评级&lt;/p&gt;&lt;p&gt;开放时间：全天&lt;span class=&quot;span_right&quot; style=&quot;float: right;&quot;&gt;门票：无需门票&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px;&quot;&gt;霞浦是宁德市下辖的县级行政区，海岸线绵延曲折，大港口水深面阔。海域面积29592.6平方公里，浅海滩涂面积104万亩，大小岛屿194个，海岸线长480公里，其中深水岸线60.6公里，可建造1-50万吨码头泊位183个。霞浦，位居台湾海峡西北岸，地处福建省东北部，陆地面积1489.6平方公里，海域面积29592.6平方公里。现有12个乡镇、2个街道，全县总人口53万。霞浦位于中国南北海岸线的中点，北......&amp;nbsp;&lt;a class=&quot;more&quot; target=&quot;_bank&quot; href=&quot;http://www.fjta.com/scenic/detail/334&quot; style=&quot;text-decoration-line: none; color: rgb(255, 102, 0);&quot;&gt;景点详情&lt;span style=&quot;font-family: sans-serif, serif; display: inline-block; padding: 0px 4px;&quot;&gt;&amp;gt;&amp;gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/e493a3e5ef0a6302056fc2f53b36f6be.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;/Public/upload/image/201705/1493811135900196.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/b63f1c92dd3f2a5da7906c2f3a4e0800.png&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;/Public/upload/image/201705/1493811135688801.png&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/a3e61f59147ef6267ac44bee5d0ec543.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;/Public/upload/image/201705/1493811135303496.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;float: left; color: rgb(51, 51, 51);&quot;&gt;&lt;span class=&quot;icon-travel-car&quot; style=&quot;display: inline-block; background-position: -412px -113px; width: 34px; height: 28px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;汽车&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/strong&gt;&lt;strong style=&quot;float: left; margin: 0px 10px 10px 0px;&quot;&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;icon-day-num&quot; style=&quot;display: inline-block; background-position: -131px -100px; width: 48px; height: 40px; background-image: url(&amp;quot;../img/sprite.png&amp;quot;); line-height: 40px; color: rgb(255, 255, 255); font-weight: 600; font-size: 14px; text-align: center; float: left; margin: 0px -20px 0px 31px; border-right: 10px solid rgb(255, 255, 255);&quot;&gt;D2&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hour&quot; style=&quot;float: right;&quot;&gt;&lt;/span&gt;&lt;span class=&quot;step_num&quot; style=&quot;float: left; width: 55px; height: 40px; background: rgb(255, 102, 0); color: rgb(255, 255, 255); text-align: center;&quot;&gt;1&lt;/span&gt;&lt;span class=&quot;arrow&quot; style=&quot;line-height: 0; font-size: 0px; margin-top: 15px; float: left; width: 0px; height: 0px; border-width: 5px; border-style: dashed dashed dashed solid; border-color: transparent transparent transparent rgb(255, 102, 0);&quot;&gt;&lt;/span&gt;&lt;strong style=&quot;font-size: 20px; padding: 0px 15px 0px 13px;&quot;&gt;&lt;a class=&quot;view_map&quot; data-lng=&quot;117.562304&quot; data-lat=&quot;27.30779&quot; title=&quot;点击查看地图详情&quot; style=&quot;color: rgb(51, 51, 51);&quot;&gt;瀑布林&lt;/a&gt;&lt;/strong&gt;4A&lt;/p&gt;&lt;p&gt;开放时间：景区开放时间：8:00-16:00；温泉开放时间：周一-周四：12:00-22:00，周五-周日、法定节假日：10:00-22:00&lt;span class=&quot;span_right&quot; style=&quot;float: right;&quot;&gt;门票：瀑布林景区成人票：60元；瀑布林温泉：258元&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px;&quot;&gt;瀑布林温泉水采自地下800多米，平均水温52度。温泉井日出水量大、水质优良，PH值7.05，属碳酸氢钠型温泉。水中富含矿物质成分对人的皮肤、神经系统、心血管系统、呼吸道系统等有一定医疗保健作用，属优质的医疗热矿泉。度假村占地面积2000亩，总投资约人民币15亿元，分三期开发建设，第一期打造禅茶文化森林温泉养生区、四星级度假别墅酒店和按4A标准建设的六曲花溪景区、生态农业观光果蔬采摘区、森林瀑布观光....&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '7,8,12,18', '鼓楼,马尾,闽侯', '2017-05-03', 1, 1, 0),
(16, 69, '世遗探秘之旅（三至七日游）', '世界遗产和世界地质公园是全世界公认的首要旅游品牌。我省十三五旅游规划主推“329”，“3”即我省三处世界遗产地（世界文化与自然双遗产—武夷山、世界文化遗产—福建土楼、世界自然遗产—泰宁丹霞），2即两处世界地质公园（泰宁世界地质公园、宁德世界地质公园），是我省最具竞争力的核心旅游产品。', './Public/Uploads/2017-05-03/5909c043200ee.jpg', '&lt;p&gt;&lt;strong style=&quot;font-size: 20px; padding: 0px 15px 0px 13px;&quot;&gt;&lt;a class=&quot;view_map&quot; data-lng=&quot;119.303585&quot; data-lat=&quot;26.08843&quot; title=&quot;点击查看地图详情&quot; style=&quot;color: rgb(51, 51, 51);&quot;&gt;三坊七巷&lt;/a&gt;&lt;/strong&gt;5A&lt;/p&gt;&lt;p&gt;开放时间：全天开放，收费景点8:30-17:00开放&lt;span class=&quot;span_right&quot; style=&quot;float: right;&quot;&gt;门票：免费，部分景点需购买门票&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px;&quot;&gt;对老福州人来说，三坊七巷就是他们的家园，也是福州的代表。三坊七巷起于晋，完善于唐五代，至明清鼎盛，古老的坊巷格局至今基本保留完整，是中国都市仅存的一块“里坊制度活化石”。坊巷内保存有200余座古建筑，其中全国重点文物保护单位有9处，省、市级文保单位和历史保护建筑数量众多，是一座不可多得的“明清建筑博物馆”。三坊七巷向西三片称“坊”，向东七条称“巷”，自北而南依次为：“三坊”衣锦坊、文儒坊、光禄坊，......&amp;nbsp;&lt;a class=&quot;more&quot; target=&quot;_bank&quot; href=&quot;http://www.fjta.com/scenic/detail/27&quot; style=&quot;text-decoration-line: none; color: rgb(255, 102, 0);&quot;&gt;景点详情&lt;span style=&quot;font-family: sans-serif, serif; display: inline-block; padding: 0px 4px;&quot;&gt;&amp;gt;&amp;gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/e465b1ad0e97c82772150f89e8dee991.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;/Public/upload/image/201705/1493811229255122.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/ca8d48095c53041177b4ed9662a66582.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;/Public/upload/image/201705/1493811229464889.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;http://www.fjta.com/static/uploads/jingdian/b4cee5a9b6e3b59ad78367055629d445.jpg&quot; style=&quot;text-decoration-line: none; color: rgb(51, 51, 51); width: 196px; height: 130px; margin-right: 20px; float: left;&quot;&gt;&lt;img width=&quot;196&quot; height=&quot;130&quot; src=&quot;/Public/upload/image/201705/1493811229588057.jpg&quot; alt=&quot;&quot; style=&quot;border: 0px;&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '10,12,14', '闽侯,连江', '2017-05-03', 1, 1, 0);

